[99148] = {name = "Zwielichtschlag", description = "[q]Fügt einem Gegner Schattenschaden zu."},
[99172] = {name = "Einhüllende Flammen", description = "[q]Ragnaros beginnt, die ihm nächste Plattform in Flammen zu hüllen!"},
[99197] = {name = "Präsenz von Tarecgosa", description = "[q]Erhöht die Gesundheits- und Manaregeneration naher Verbündeter. Wenn Ihr Schaden verursacht, besteht eine Chance, den offensiven Zauber zu duplizieren."},
[99199] = {name = "Ritual der Flamme", description = "[q]Das Ritual der Flamme fügt nahen Feinden alle 1 Sek. Feuerschaden zu und macht den Zaubernden gegen nicht-kataklysmischen Schaden immun."},
