[99256] = {name = "Qual", description = "[q]Fügt dem nahestehendsten Ziel ansteigenden regelmäßigen Schattenschaden zu. Während ein Ziel gequält wird, gewinnt der Heiler für jeden eintreffenden Einzelziel-Heilzauber pro 3 Stapeln von 'Qual' je eine Aufladung von 'Lebensfunke'."},
[99259] = {name = "Splitter der Qual", description = "[q]Ruft mitten unter nahen Feinden Splitter der Qual hervor."},
[99268] = {name = "Lebender Meteor", description = "[q]Ragnaros beschwört einen lebenden Meteor am Zielort. Der lebende Meteor rollt auf Spieler zu. Erreicht der lebende Meteor einen Spieler, löst er einen Meteoreinschlag aus, der Feinden im Umkreis von 8 Metern 107250 to 112750 Feuerschaden zufügt."},
[99275] = {name = "Geschmolzener Brocken", description = "[q]Fügt nahen Feinden Schaden zu und stößt sie zurück."},
