[99286] = {name = "Kaltflammenstrahlen", description = "[q]Fügt jedem in Reichweite Frostschaden zu."},
[99287] = {name = "Meteoreinschlag", description = "[q]Der Einschlag des lebenden Meteors fügt Feinden im Umkreis von 8 Metern 107250 to 112750 Feuerschaden zu."},
