[99302] = {name = "Tür zerschmettern", description = "[q]Zerschmettert den Eiswall."},
[99303] = {name = "Verbrennung", description = "[q]Angriffe auf den lebenden Meteor stoßen ihn zurück!"},
[99308] = {name = "Klaffende Wunde", description = "[q]Lässt das Ziel 1 min lang alle 0.2 Sek. für 6000 körperlichen Schaden bluten. Dieser Effekt wird entfernt, wenn die Gesundheit des Ziels unter 50% fällt."},
