[99389] = {name = "Geprägt", description = "[q]Gierige Jungtiere werden auf das erste Ziel geprägt, das sie nach ihrer Geburt sehen, und bedrängen es pausenlos, um gefüttert zu werden."},
[99392] = {name = "Zwielichtwiederhestellung", description = "[q]Regeneriert pro Sekunde 10000 Gesundheit.\n Regeneriert pro Sekunde 1000 Mana.\n Erlittener Schaden um 50% verringert."},
[99399] = {name = "Brennende Wunde", description = "[q]Ragnaros' Angriffe rufen eine brennende Wunde hervor, die 20 sec lang alle 2 Sek. 5386 to 5663 Feuerschaden verursacht. Die Wunde lässt Feuer in den Körper des Ziels sickern, wodurch ein Brennschlag ausgelöst wird."},
[99407] = {name = "Eiseruption", description = "[q]Lässt einen Eisstachel am Zielort entstehen."},
[99409] = {name = "Schwarzes Spalten", description = "[q]Fügt einem Gegner und seinen nächsten Verbündeten Waffenschaden plus zusätzlichem Schaden zu. Trifft bis zu 3 Ziele. Gegner, die von dieser Fähigkeit getroffen wurden, erleiden zusätzlich 12 sec lang alle 3 Sek. Schattenschaden."},
[99411] = {name = "Gift entziehen", description = "[q]Das kleinere Tier entzieht seiner Brutmutter Macht und erhält 20 sec lang alle 100 Sek. 100 'Brennendes Gift'."},
