[99579] = {name = "Geschmolzener Blitz", description = "[q]Der geschmolzene Berster spuckt einen Magmaball auf das Zielgebiet und löst so eine Eruption aus."},
[99587] = {name = "Flammensamen", description = "[q]Pflanzt mehrere Flammensamen auf feindlichen Zielen, die nach Ablauf von 4 Sek. oder bei einem kräftigen Einschlag detonieren und 259000 to 301000 Feuerschaden verursachen."},
