[99844] = {name = "Lodernde Klauen", description = "[q]Beharkt einen Gegner mit lodernden Klauen, verursacht 138750 to 161250 körperlichen Schaden und erhöht seinen erlittenen Feuer- und körperlichen Schaden 15 sec lang um 10%."},
