[52208] = {name = "Zona en llamas", description = "[q]La zona de fuego inflige 200 p. de daño de Fuego cada 1 s durante 8 sec."},
[52219] = {name = "El aliento de Har'koa", description = "[q]Imbuye al objetivo con la facultad de pasar al averno en el Altar de Quetz'lun."},
[52221] = {name = "Golpe heroico", description = "[q]Un ataque contundente que aumenta el daño cuerpo a cuerpo 11 p. y genera gran cantidad de amenaza."},
[52230] = {name = "Infectado por peste", description = "[q]El efecto de la peste inflige 250 p. de daño de Naturaleza cada 3 s durante 15 sec. Además, la peste aumenta la probabilidad de golpe crítico un 30% y el daño crítico un 30% durante 15 sec."},
[52237] = {name = "Pisotón destrozador", description = "[q]Inflige 2313 to 2687 p. de daño a todos los enemigos y destruye a los gólems enfriados que están en un radio de 99 m."},
[52238] = {name = "Templar", description = "[q]Golpea el yunque de Volkhan, lo que crea una serie de gólems fundidos."},
[52254] = {name = "Cohetes aire-aire", description = "[q]Dispara a enemigos aéreos."},
[52257] = {name = "Descarga de las Sombras", description = "[q]Lanza una descarga de magia oscura a un enemigo y le inflige daño de las Sombras."},
[52262] = {name = "¡Acorralado y furioso!", description = "[q]Aumenta un 100% la velocidad de ataque del taumaturgo y el daño físico que inflige 200 p. durante 2 min."},
[52265] = {name = "Repo", description = "[q]¿Adónde vas con ese caballo?"},
[52268] = {name = "Galope", description = "[q]Aumenta la velocidad de carrera un 100% durante 2 s."},
[52270] = {name = "Multidisparo", description = "[q]Dispara una descarga de misiles al enemigo y aliados cercanos que alcanza a un máximo de 3 objetivos y aumenta el daño normal 75 p."},
[52271] = {name = "Colisión violenta", description = "[q]Salta estrellándose contra todos los oponentes cercanos, inflige daño masivo y los aturde durante 4 sec."},
[52272] = {name = "Lanzar roca", description = "[q]Lanza una roca que inflige daño elevado e interrumpe cualquier hechizo que estuviera lanzando el objetivo."},
[52274] = {name = "Consumir górloc", description = "[q]Consume un górloc cercano, otorgando un 5% de salud cada 2 segundos durante 10 segundos."},
[52281] = {name = "Llama del vidente", description = "[q]Invoca el poder del Vidente para aumentar el daño con hechizos infligido por el taumaturgo 400 p. durante 2 min."},
[52282] = {name = "Bola de Fuego", description = "[q]Inflige daño de Fuego a un enemigo."},
[52283] = {name = "Rugido del Señor de la Guerra", description = "[q]El rugido del Señor de la Guerra otorga inmunidad frente a los efectos de frenado, de control de masas y de aturdimiento."},
[52290] = {name = "Elementos dominados", description = "[q]Cuando se activa, otorga a tu próximo hechizo de daño el 100% de bonus de daño y reduce el coste de maná un 100%."},
[52307] = {name = "Esputo de hidra", description = "[q]Inflige 262 to 288 p. de daño de Naturaleza cada 3 s durante 20 sec."},
[52309] = {name = "Voluntad de guerrero", description = "[q]Aumenta la armadura 2000 p. y aumenta el daño infligido un 15% durante 1 min."},
[52311] = {name = "Carga furiosa", description = "[q]Carga hacia delante, lo que inflige daño masivo y aturde a todos los enemigos cercanos."},
[52316] = {name = "Machaque en la cabeza", description = "[q]Ataca con un machaque de cabeza despiadado, que inflige el 145% del daño normal a los enemigos que se encuentren en un cono frente al taumaturgo."},
[52317] = {name = "Defender", description = "[q]Reduce el daño recibido un 50% durante 4 sec."},
[52321] = {name = "Perseverancia de los dioses", description = "[q]El taumaturgo se vuelve inmune a los efectos de aturdimiento y todos los ataques le sanan durante 20 s."},
[52331] = {name = "Gran flagelo", description = "[q]Inflige 1080 to 2520 p. de daño a los enemigos frente al taumaturgo."},
[52334] = {name = "Rezumar petróleo", description = "[q]Inflige 602 to 698 p. de daño de las Sombras."},
[52338] = {name = "Lanzar roca", description = "[q]Lanza una roca enorme lejos. Esta roca inflige daño de Fuego y 1000 p. de daño de asedio a todos los enemigos en un radio de 10 m."},
[52340] = {name = "Espíritu de Har'koa", description = nil},
[52341] = {name = "Sobrecarga eléctrica", description = "[q]Lanza una ola explosiva de magia e inflige daño de Naturaleza a los enemigos cercanos."},
[52342] = {name = "Mal hirviente", description = "[q]Los ataques tienen la probabilidad de infligir 324 to 376 p. de daño de las Sombras."},
[52352] = {name = "Devorar carne", description = "[q]Drena 1980 to 2420 p. de salud y un 5% de aguante de un enemigo y lo transfiere al taumaturgo."},
[52356] = {name = "Lanzar", description = "[q]Lanza tu arma e inflige un 150% de su daño al objetivo."},
[52358] = {name = "Zarpas prestas", description = "[q]Aumenta la velocidad de movimiento un 20% durante 15 sec."},
[52362] = {name = "Llamada de jinete", description = "[q]Devuelve el destrero de la muerte a la Brecha de la Muerte y llama a Salanar el Jinete. Solo se puede usar en la Brecha de la Muerte."},
[52372] = {name = "Toque helado", description = "[q]Inflige de 217 a 235 p. de daño de Escarcha modificado por el poder de ataque y reduce las velocidades de lanzamiento, ataque cuerpo a cuerpo y ataque a distancia un 15% durante 20 sec."},
[52373] = {name = "Golpe de peste", description = "[q]Un golpe feroz que inflige daño con arma más 37 p. modificado por el poder de ataque y apesta al objetivo, lo que inflige 292 p. de daño de las Sombras durante 12 sec."},
[52374] = {name = "Golpe sangriento", description = "[q]Golpea instantáneamente al enemigo, infligiéndole un 60% de daño con arma más 55 p. por cada efecto de enfermedad sobre el objetivo."},
[52375] = {name = "Espiral de la muerte", description = "[q]Dispara una oleada de energía profana, que inflige 184 p. de daño de las Sombras modificado por el poder de ataque a un enemigo o sana 276 p. de daño a un objetivo no-muerto amistoso."},
[52383] = {name = "Cadena de relámpagos", description = "[q]Golpea al enemigo con una Descarga de relámpagos que se extiende a otro enemigo cercano. Afecta a un máximo de 3 objetivos causando daño de Naturaleza a cada uno."},
[52387] = {name = "Calor", description = "[q]Calienta a un gólem fundido, prolongando su vida un 20%."},
[52400] = {name = "Enfurecer feroz", description = "[q]Aumenta un 60% la velocidad de ataque del taumaturgo y el daño físico que inflige 50 p. durante 2 min."},
[52401] = {name = "Sacar tripas", description = "[q]Corta al objetivo y le inflige 800 to 1200 p. de daño y 1500 p. de daño extra durante 9 sec."},
[52402] = {name = "Fuerza aturdidora", description = "[q]Aturde al enemigo durante 6 sec. No se puede esquivar."},
[52425] = {name = "Traumatismo craneal", description = "[q]Propina un golpe doloroso a la cabeza de un enemigo e inflige 324 to 376 p. de daño físico y 139 to 161 p. de daño físico extra cada 1 s durante 5 sec."},
[52429] = {name = "Trizar", description = "[q]Hace añicos el gólem enfriado, inflige 4625 to 5375 p. de daño a los objetivos enemigos cercanos."},
[52430] = {name = "Mente alabeada", description = "[q]Reduce la velocidad de lanzamiento un 75% durante 6 sec."},
[52433] = {name = "Golpe de inmolación", description = "[q]Quema al enemigo, inflige 450 p. de daño de Fuego y 1350 p. de daño de Fuego extra durante 9 sec."},
[52435] = {name = "Cañón Escarlata", description = "[q]Su bajo consumo de combustible convierte a esta arma de medio a largo alcance en una absoluta necesidad en el campo de batalla. ¡Apunta bajo, y dale caña!"},
[52436] = {name = "Asalto con cañón Escarlata", description = "[q]¡BUM! Inflige 13613 to 19387 p. de daño a todos los enemigos que se encuentren a un radio de 12 m del impacto."},
[52442] = {name = "Aura de devoción", description = "[q]Aumenta la armadura de los amigos cercanos 1200 p."},
[52444] = {name = "Luz Sagrada", description = "[q]Invoca la magia Sagrada para sanar a un aliado."},
[52445] = {name = "Exorcismo", description = "[q]Inflige 2375 to 2625 p. de daño Sagrado a un no-muerto o demonio enemigo."},
[52446] = {name = "Salpicadura de ácido", description = "[q]El taumaturgo supura sangre ácida que inflige 200 p. de daño de Naturaleza por segundo a los amigos y enemigos alrededor. Dura 10 sec."},
[52451] = {name = "Invocar necrófagos", description = "[q]Invoca dos esbirros necrófagos cerca del taumaturgo."},
[52457] = {name = "Soldados de Drak'aguul", description = "[q]Esperando las órdenes de Drak'aguul."},
[52460] = {name = "Carga rabiosa", description = "[q]Carga contra el enemigo, lo derriba y le inflige el daño normal más 150 p."},
[52461] = {name = "Rabia imparable", description = "[q]Aumenta un 30% la velocidad de ataque del taumaturgo y el daño físico que inflige un 10% durante 8 sec. El taumaturgo no se puede detener."},
[52466] = {name = "Maldición Drakkari", description = "[q]Inflige daño de las Sombras a un enemigo cada 2 s durante 10 sec."},
[52467] = {name = "Devorar carne", description = "[q]Drena 1980 to 2420 p. de salud y un 5% de las estadísticas de un enemigo y las transfiere al taumaturgo."},
[52468] = {name = "Profecía de sangre", description = "[q]Aumenta el daño recibido por el objetivo 30 p. y el daño infligido 15 p. durante 10 sec."},
[52469] = {name = "Mordedura infectada", description = "[q]Muerde al objetivo, lo que inflige 1885 to 2115 p. de daño de Naturaleza. Además, el objetivo recibe 1000 p. de daño de Naturaleza cada 3 s y el daño físico recibido aumenta un 20%. Dura 9 sec."},
[52470] = {name = "Enfurecer", description = "[q]Aumenta la velocidad de ataque del taumaturgo un 300%, pero reduce el daño físico infligido un 50% durante 8 sec."},
[52480] = {name = "Explotar necrófago", description = "[q]Hace que explote un necrófago cercano, inflige 3800 to 4200 p. de daño de Fuego a los objetivos enemigos en un radio de 10 m."},
[52491] = {name = "Deflagración de telaraña", description = "[q]Inmoviliza a los enemigos cercanos durante 4 sec."},
[52493] = {name = "Pulverizador de veneno", description = "[q]Dispara veneno al enemigo, lo que inflige daño de Naturaleza y luego daño extra cada 3 s durante 10 sec."},
[52495] = {name = "Esclarecimiento elemental", description = "[q]Aumenta el daño infligido por los hechizos de Fuego y Escarcha un 20% durante 2 min."},
[52496] = {name = "Escarabajos de la cripta", description = "[q]Invoca a varios escarabajos de la cripta para que ataquen al objetivo del taumaturgo."},
[52497] = {name = "Flatular", description = "[q]Derriba a los trituracráneos Drakkari cercanos e inflige daño a un enemigo cada 1 s durante 2 sec."},
[52501] = {name = "Cólera", description = "[q]Lanza una descarga de relámpago a un enemigo y le inflige daño de Naturaleza."},
[52502] = {name = "Fuego lunar", description = "[q]Quema al enemigo, inflige daño Arcano y daño extra durante 12 sec."},
[52503] = {name = "Empowered Moonkin Aura", description = "[q]Aumenta la probabilidad de golpe crítico un 10%."},
[52504] = {name = "Lacerar", description = "[q]Lacera al objetivo enemigo, lo que inflige 463 to 537 p. de daño de sangrado, le hace sangrar 750 p. de daño durante 15 sec y genera una gran cantidad de amenaza. El daño aumenta con el poder de ataque. El efecto se acumula hasta 5 veces en un mismo objetivo."},
[52506] = {name = "Magullar", description = "[q]Aumenta 150 p. el daño del siguiente ataque."},
[52507] = {name = "Presencia de Patafuria", description = "[q]Reduce el daño recibido un 10%."},
[52508] = {name = "Lleno a rebosar", description = "[q]Hace que la abominación explote, matando a los trituracráneos Drakkari cercanos."},
[52519] = {name = "Necrofaplosión", description = "[q]Mata instantáneamente a un necrófago desobediente. Eso incluye a los necrófagos que creen que contestar a Gothik es un comportamiento aceptable."},
[52521] = {name = "Zurrar a necrófago", description = "[q]Zurra a un necrófago para que sea sumiso."},
[52522] = {name = "Hemotoxina", description = "[q]Envenena al objetivo, inflige un 8% de la salud del objetivo cada 2 s."},
[52524] = {name = "Telarañas cegadoras", description = "[q]Dispara telarañas a los ojos de los enemigos de alrededor, cegándolos y desorientándolos durante 4 sec."},
[52525] = {name = "Nube enfermiza", description = "[q]Una nube pútrida de enfermedad rodea al taumaturgo, lo que inflige 1000 p. de daño de Naturaleza a los enemigos cercanos cada 2 s."},
[52527] = {name = "Bilis de miseria", description = "[q]Cubre al taumaturgo con una desagradable película de bilis, que reduce el daño y la sanación que hace ese objetivo un 35% e inflige 700 p. de daño de Naturaleza cada 3 durante 15 sec."},
[52531] = {name = "Golpe de vapor", description = "[q]Inflige daño de Fuego a los enemigos que se encuentran en un cono frente al taumaturgo."},
[52532] = {name = "Golpear", description = "[q]Golpea a un enemigo e inflige daño cuerpo a cuerpo aumentado."},
[52534] = {name = "Descarga de las Sombras", description = "[q]Lanza una descarga de magia oscura a un enemigo y le inflige daño de las Sombras."},
[52535] = {name = "Nova de las Sombras", description = "[q]Inflige daño de las Sombras a los enemigos cercanos."},
[52538] = {name = "Cargar", description = nil},
[52539] = {name = "Bola de cañón", description = "[q]Lanza una bola ígnea que inflige 1 p. de daño de Fuego al enemigo."},
[52540] = {name = "Puñalada", description = "[q]Inflige daño normal más 300 p. a un enemigo, pero solo si atacas por la espalda."},
[52551] = {name = "Flor de vida", description = "[q]Sana 232 to 268 p. cada segundo y 1573-1827 p. cuando el efecto se acaba o se disipa."},
[52553] = {name = "Árbol de vida potenciado", description = "[q]Concede un aura beneficiosa a los aliados cercanos, aumentando un 10% la sanación recibida."},
[52554] = {name = "Nutrir", description = "[q]Invoca la magia de Naturaleza para sanar a un aliado."},
[52560] = {name = "Invocar sangre del dios antiguo", description = "[q]Invoca la sangre de los dioses antiguos para ayudar a Ursoc."},
[52564] = {name = "Corrupción del dios antiguo", description = "[q]Inflige 1110 to 1290 p. de daño de las Sombras y 400 p. de daño de las Sombras cada 1 s durante 6 sec."},
[52566] = {name = "Disparar", description = "[q]Dispara a un enemigo y le inflige daño físico."},
[52576] = {name = "Pulso electromagnético", description = "[q]Usando la energía del cañón Escarlata, se emite un poderoso pulso electromagnético a quemarropa. Todos los enemigos que se encuentren cerca serán arrasados."},
[52581] = {name = "Aplastar armadura", description = "[q]Reduce la armadura de un enemigo un 75% durante 6 sec."},
[52583] = {name = "Influencia del dios antiguo", description = "[q]Bajo la influencia del dios antiguo. Aturde periódicamente al objetivo durante 2 sec e inflige 1018 to 1182 p. de daño de las Sombras."},
[52586] = {name = "Tortura mental", description = "[q]Inflige daño de las Sombras a un enemigo y reduce su velocidad de movimiento durante 3 sec."},
[52592] = {name = "Maldición de fatiga", description = "[q]Maldice a todos los enemigos en un radio de 5 m alrededor del objetivo, inflige 2828 to 3172 p. de daño de las Sombras, reduciendo la velocidad de movimiento un 30%, la velocidad de ataque un 30%, y la de lanzamiento un 30%. Dura 10 sec."},
[52601] = {name = "Carga impetuosa", description = "[q]Carga al discípulo de Mam'toth seleccionado a un máximo de 5 to 45 m. No se puede usar en un radio de 5 m del objetivo."},
[52603] = {name = "Atropello", description = "[q]Inflige 4375-5625 p. de daño a un máximo de 4 discípulos de Mam'toth."},
[52604] = {name = "Picadura de escórpido", description = "[q]Pica al objetivo lo que reduce la probabilidad de golpear un 5% durante 15 sec. Un cazador solo puede tener una picadura activa a la vez sobre un mismo objetivo."},
[52606] = {name = "Trampa de inmolación", description = "[q]Coloca una trampa de Fuego que quemará al primer enemigo que se acerque, lo que le infligirá 36 p. de daño de Fuego durante 30 sec. La trampa dura 30 sec. Solo puede haber 1 trampa activa a la vez."},
[52608] = {name = "Lanzar linterna", description = "[q]Lanza una linterna a un enemigo, le inflige 213 to 287 p. de daño de Fuego e invoca una Llamarada ardiente bajo los pies del objetivo."},
[52611] = {name = "Invocar esqueletos", description = "[q]Invoca dos esbirros esqueléticos cerca del taumaturgo."},
[52613] = {name = "Roca de hielo", description = nil},
[52624] = {name = "Postcombustión", description = "[q]Aumenta la probabilidad de asestar un golpe crítico con golpes cuerpo a cuerpo del taumaturgo un 50% durante 10 sec."},
[52626] = {name = "Fundir armadura", description = "[q]Reduce la armadura de los enemigos cercanos 1000 p. durante 30 sec."},
[52628] = {name = "Runa de reprensión", description = "[q]Aumenta el daño infligido un 5% al recibir daño. Este efecto solo se puede activar una vez cada 2 segundos y solo se puede acumular hasta 5 veces."},
[52629] = {name = "Reprensión", description = "[q]Aumenta el daño infligido un 5%. Se acumula hasta 5 veces."},
[52630] = {name = "Runas de batalla", description = "[q]Inflige 150 p. de daño físico a los atacantes."},
[52633] = {name = "Golpe de vacío", description = "[q]Inflige un 125% de daño con arma a un enemigo e ignora toda la armadura del enemigo."},
[52634] = {name = "Escudo de vacío", description = "[q]Una burbuja protectora de vacío emana del escudo del taumaturgo, lo que reduce un 50% todo el daño recibido durante 8 sec."},
[52635] = {name = "Acelerar", description = "[q]Aumenta el ataque cuerpo a cuerpo y la velocidad de lanzamiento del taumaturgo un 10%. Se acumula hasta 20 veces."},
[52636] = {name = "Alterar tiempo", description = "[q]Teletransporta al taumaturgo a un lugar cercano aleatorio, elimina todos los efectos negativos del taumaturgo."},
[52640] = {name = "Forzar forja", description = "[q]Inflige 1203 to 1397 p. de daño físico en un cono frente al taumaturgo, aturde al enemigo durante 3 sec."},
[52643] = {name = "Armas romas", description = "[q]Da la posibilidad de dejar las armas de los atacantes cuerpo a cuerpo romas al ser golpeado, reduciendo el daño infligido."},
[52651] = {name = "Escudo de relámpagos", description = "[q]Rodea al taumaturgo con 20 bolas de relámpagos con un 25% de probabilidad de infligir 250 p. de daño a los atacantes cuerpo a cuerpo o a distancia. Cada vez que el escudo de relámpagos golpea, se disipa una bola de relámpagos. El escudo se disipa tras 30 sec o cuando ha golpeado 20 veces."},
[52657] = {name = "Vórtice temporal", description = "[q]Canaliza un vórtice sobre el enemigo, lo que inflige 600 p. de daño Arcano cada 1 s y ralentiza un 70% la velocidad de lanzamiento de hechizos, de ataque y de movimiento durante 5 sec."},
[52658] = {name = "Sobrecarga estática", description = "[q]Carga a un enemigo con energía, le inflige 1249 to 1451 p. de daño de Naturaleza durante 10 sec e inflige periódicamente 1249 to 1451 p. de daño de Naturaleza a sus aliados cercanos. Tras 10 sec, tiene lugar una explosión eléctrica que inflige 2313 to 2687 p. de daño de Naturaleza extra a todos los enemigos en un radio de 8 m y los derriba."},
[52660] = {name = "Ola Arcana", description = "[q]Lanza una ola de energía mágica al objetivo enemigo, lo que inflige 2590 to 3010 p. de daño Arcano."},
[52666] = {name = "Expulsar enfermedad", description = "[q]Gancho eructa una nube enfermante que inflige 713 to 787 p. de daño de Naturaleza e interrumpe el lanzamiento de hechizos de los enemigos cercanos durante 4 sec."},
[52671] = {name = "Quemar arqueado", description = "[q]Inflige 602 to 698 p. de daño de Naturaleza al objetivo y aumenta el daño de Naturaleza recibido un 2%."},
[52672] = {name = "Necrofaplosión", description = "[q]Mata instantáneamente a una criatura inferior de la Plaga elegida por el taumaturgo, ¡a menudo de forma espectacular!"},
[52694] = {name = "Devolver el Ojo de Acherus", description = "[q]Devuelve el Ojo de Acherus y regresa a casa."},
[52695] = {name = "Muñeco de vudú", description = "[q]Inflige 833 to 967 p. de daño físico."},
[52696] = {name = "Cadenas constrictoras", description = "[q]Reviste a los objetivos de cadenas, lo que inflige 1000 p. de daño físico cada 1 s y aturde al objetivo durante 5 sec."},
[52699] = {name = "Fundir runa", description = "[q]Imbuye al objetivo con la runa de Tordon."},
[52701] = {name = "Relámpago cargado", description = "[q]Los ataques tienen la posibilidad de infligir daño de Naturaleza."},
[52702] = {name = "Puñetazo rúnico", description = "[q]Golpe masivo que inflige 300% de daño con arma y graba una runa en la piel del objetivo."},
[52703] = {name = "Tótem centinela cargado", description = "[q]Invoca un tótem de relámpagos que daña periódicamente a los enemigos cercanos. Dura 14 sec."},
[52705] = {name = "Choque avizor", description = "[q]Inflige daño de Naturaleza a los enemigos que se encuentran en un cono frente al taumaturgo."},
[52708] = {name = "Robar carne", description = "[q]Roba la carne de un objetivo enemigo, reduce el daño que inflige el enemigo un 75% y aumenta la del taumaturgo un 75%."},
[52710] = {name = "Relámpago cargado", description = "[q]Los ataques tienen la posibilidad de infligir 24 to 29 p. de daño de Naturaleza."},
[52713] = {name = "Tejido de runas", description = "[q]Inflige 195 to 225 p. de daño de las Sombras cada 1 s e inmoviliza al objetivo durante 4 sec."},
[52714] = {name = "Runa revitalizante", description = "[q]Sana al taumaturgo 2500 p. de daño tras 5 sec."},
[52715] = {name = "Runa de destrucción", description = "[q]Usa una runa ígnea sobre un enemigo durante 5 sec que inflige daño tras 5 s."},
[52716] = {name = "Aterrorizado", description = "[q]¡Aterrorizado!"},
[52717] = {name = "Tormenta de truenos", description = "[q]Invoca una descarga de relámpagos que inflige daño a los enemigos cercanos en un radio de 10 m. Inflige 747 to 853 p. de daño de Naturaleza a todos los enemigos cercanos y los lanza a 85.3 m."},
[52719] = {name = "Arremetida de conmoción", description = "[q]Un golpe brutal que inflige daño con arma y aturde al oponente durante 3 sec."},
[52720] = {name = "Enjambre de carroña", description = "[q]Una ola creciente de magia caótica choca contra los enemigos frente al taumaturgo e inflige 3230 to 3570 p. de daño de las Sombras y 380 to 420 p. de daño de las Sombras cada 3 s durante 15 sec."},
[52721] = {name = "Dormir", description = "[q]Duerme a un enemigo durante un máximo de 10 sec. Cualquier daño despertará al objetivo."},
[52722] = {name = "Explosión mental", description = "[q]Inflige 4163 to 4837 p. de daño de las Sombras a un enemigo."},
[52723] = {name = "Toque vampírico", description = "[q]Sana al taumaturgo la mitad del daño infligido por un ataque cuerpo a cuerpo."},
[52740] = {name = "Lanzar arma", description = "[q]Lanza tu arma e inflige 2040 to 2760 p. de daño físico sobre el objetivo y 5425 p. de daño extra durante 21 sec."},
[52743] = {name = "Guantazo en la cabeza", description = "[q]Inflige 13 to 16 p. de daño a un enemigo y lo aturde durante un máximo de 8 sec. Dejarás de atacar automáticamente. El objetivo debe estar de cara a ti. Cualquier daño que reciba el objetivo aturdido lo revivirá."},
[52744] = {name = "Aullido perforador", description = "[q]Provoca que todos los enemigos situados cerca del guerrero queden atontados durante 6 sec."},
[52754] = {name = "Bala impactante", description = "[q]Dispara a un enemigo y le inflige daño físico.\n\n Repele a la víctima."},
[52755] = {name = "Multidisparo de impacto", description = "[q]Dispara una salva de misiles a un enemigo y a sus aliados cercanos que golpea a un máximo de 3 objetivos, inflige 2040 to 2760 p. de daño físico y los derriba."},
[52758] = {name = "Flecha penetrante", description = "[q]Dispara a un enemigo y le inflige daño físico."},
[52761] = {name = "Red con púas", description = "[q]Inmoviliza un enemigo durante 6 sec y le inflige daño físico."},
[52766] = {name = "Distorsión temporal", description = "[q]El tiempo se ralentiza, reduce la velocidad de movimiento, de lanzamiento y de ataque un 70% durante 6 sec."},
[52770] = {name = "Dispersar", description = "[q]Se dispersa en chispas de Ionar."},
[52771] = {name = "Golpe hiriente", description = "[q]Hiere salvajemente al objetivo, le inflige un 100% de daño con arma, reduce los efectos de sanación un 25% e inflige 750 p. de daño físico cada 1 s durante 4 sec."},
[52772] = {name = "Maldición de esfuerzo", description = "[q]Aumenta el coste de los hechizos y facultades un 100% durante 10 sec."},
[52773] = {name = "Arremetida con martillo", description = "[q]Inflige un 125% de daño normal de arma a un enemigo y reduce su velocidad de movimiento un 65% durante 5 sec."},
[52774] = {name = "Renovar acero", description = "[q]Repara un 10% del daño cada 2 s durante 6 sec. Solo se puede usar sobre los vrykuls férreos."},
[52778] = {name = "Haz soldador", description = "[q]Inflige daño de Naturaleza a un enemigo durante 5 sec."},
[52780] = {name = "Bola de relámpagos", description = "[q]Lanza una bola de relámpago al objetivo e inflige 2013 to 2587 p. de daño de Naturaleza al objetivo enemigo."},
[52791] = {name = "Luz naciente", description = "[q]El intenso calor de las llamas está incubando el huevo de draco primordial."},
[52792] = {name = "Luz naciente", description = "[q]Preparándose para incubar."},
[52812] = {name = "Furia de arrabio", description = "[q]Inflige 1143 to 1397 p. de daño de Fuego y 833 to 967 p. de daño de Fuego extra cada 1 s durante 5 sec. Doble de daño si el objetivo está aturdido."},
[52813] = {name = "Multidisparo", description = "[q]Dispara una descarga de misiles al enemigo y aliados cercanos que alcanza a un máximo de 3 objetivos y aumenta el daño normal 278 to 322 p."},
[52814] = {name = "Vientos árticos", description = "[q]Una racha de viento frío inflige 2035 to 2365 p. de daño de Naturaleza."},
[52835] = {name = "Rajar", description = "[q]Inflige daño con arma más 438 to 562 p. a un enemigo y a sus aliados cercanos. Afecta hasta a 4 objetivos."},
[52836] = {name = "Cólera Sagrada", description = "[q]Lanza descargas de Poder Sagrado en todas direcciones, inflige 818 to 1597 p. de daño Sagrado a todos los objetivos no-muertos y demoníacos en un radio de 20 m."},
[52856] = {name = "Cargar", description = "[q]Carga contra un enemigo, inflige el daño normal y aturde al enemigo durante 2 sec, además derriba a todos los enemigos al alcance."},
[52859] = {name = "Sumisión", description = "[q]Obliga a Akali a someterse y calmarse, regresando a su lugar sobre rejilla de Gundrak."},
[52862] = {name = "Devorar viento", description = "[q]Devora a un aparecido tormentoso herido y adquiere sus poderes."},
[52864] = {name = "Devorar agua", description = "[q]Devora a un espíritu acuoso herido y adquiere sus poderes."},
[52869] = {name = "Evocación", description = "[q]Regenera un 10% de maná cada segundo durante 10 sec. El daño que se recibe mientras está activo aumenta un 10%."},
[52870] = {name = "Viento esquilador", description = "[q]Inflige 1125 to 1875 p. de daño a los enemigos que se encuentran en un cono frente al taumaturgo y los derriba."},
[52873] = {name = "Herida abierta", description = "[q]Inflige daño físico a un enemigo cada 3 s y aumenta el daño recibido un 15% durante 15 sec."},
[52878] = {name = "Mareo", description = "[q]Te sientes mareado."},
[52879] = {name = "Desvío", description = "[q]Aumenta tu probabilidad de parar ataques un 25%."},
[52883] = {name = "Contraataque", description = "[q]Un ataque que traspasa las defensas del objetivo, inmovilizándolo durante 8 sec. Solo se puede usar cuando pares un ataque."},
[52884] = {name = "Drenar poder", description = "[q]Drena el resto del poder de Akali. La mayoría ira a su maestro en Guldrak bajo la rejilla, pero el profeta se quedará un poco también. Akali morirá al final del lanzamiento."},
[52885] = {name = "Lanzamiento mortal", description = "[q]Lanza una daga al enemigo y le inflige un 2400% de daño con arma. También interrumpe el lanzamiento de hechizos y evita que se pueda lanzar cualquier hechizo de esa misma escuela durante 3 sec."},
[52886] = {name = "Trampa de púas", description = "[q]Coloca una trampa de púas que explota al acercarse, inflige daño significativo a todos los enemigos cercanos."},
[52889] = {name = "Disparo envenenado", description = "[q]Deja atontado a un enemigo, lo que reduce su velocidad de movimiento un 50% durante 5 sec."},
[52890] = {name = "Golpe penetrante", description = "[q]Un golpe que ignora la armadura e inflige un 100% de daño con arma."},
[52891] = {name = "Giro de hojas", description = "[q]Reduce el daño cuerpo a cuerpo y a distancia recibido 1000 p. Dura 4 sec."},
[52893] = {name = "Zona antimagia", description = "[q]Coloca una gran zona antimagia estática que reduce un 75% el daño con hechizos infligido a cualquier miembro de tu grupo que se encuentre dentro. La zona antimagia dura 10 sec o hasta que absorbe 10000 p. de daño con hechizos."},
[52895] = {name = "Venda", description = "[q]Sana 3000 p. de daño durante 6 sec."},
[52896] = {name = "Mitigar hechizos", description = "[q]Reduce el daño con hechizos recibido un 10% tras haber recibido un golpe crítico con u hechizo. Dura 4 sec."},
[52904] = {name = "Lanzar", description = "[q]Lanza tu arma e inflige 3500 p. de daño físico."},
[52905] = {name = "Descarga de truenos", description = "[q]Golpea al enemigo con una Descarga de relámpagos que se extiende a otro enemigo cercano. Afecta al objetivo y a los aliados cercanos, les inflige 2775 to 3225 p. de daño de Naturaleza a cada uno."},
[52909] = {name = "Respiración acuática", description = "[q]Otorga a todos los miembros del grupo cercanos la facultad de Respiración acuática durante 30 min."},
[52919] = {name = "Nube venenosa", description = "[q]Daño de Naturaleza infligido cada 2 s."},
[52921] = {name = "Arco eléctrico", description = "[q]Electrocuta al objetivo, inflige instantáneamente 3238 to 3762 p. de daño de Naturaleza y el objetivo tiene la posibilidad de electrocutar a los amigos cercanos."},
[52922] = {name = "La penitencia del inquisidor", description = "[q]Lanza una descarga sagrada que derriba a todos lo que se encuentren en su radio de impacto."},
[52926] = {name = "Punición de Valroth", description = "[q]Pune al enemigo causándole daño Sagrado."},
[52931] = {name = "Flema tóxica", description = "[q]Dispara veneno al enemigo, lo que inflige daño de Naturaleza y luego daño extra cada 5 s durante 10 sec."},
[52940] = {name = "¡A dormir!", description = "[q]Sana 600 p. de daño durante 6 sec."},
[52943] = {name = "Remolino de relámpagos", description = "[q]Gira alrededor del enemigo, desatando una dolorosa descarga de rayos."},
[52944] = {name = "Golpe de relámpagos", description = "[q]Invoca una descarga de relámpagos que inflige daño de Naturaleza y derriba a todos los enemigos cercanos."},
[52960] = {name = "Nova de relámpagos", description = "[q]Emite un pulso de energía que inflige 6013 to 6987 p. de daño de Naturaleza a los enemigos en un radio de 20 m."},
[52964] = {name = "Frenesí chisposo", description = "[q]El taumaturgo lanza chispas en todas las direcciones, inflige 18 to 21 p. de daño de Naturaleza con cada chispa."},
[52968] = {name = "Ira corazón de sangre", description = "[q]Aumenta un 60% la velocidad de ataque del taumaturgo y el daño físico que inflige durante 25 sec."},
[52969] = {name = "Succión de energía", description = "[q]Succiona energía de los cuerpos de los oponentes cercanos y restaura salud del taumaturgo."},
[52972] = {name = "Diseminación", description = "[q]Protege contra todos los ataques físicos y devuelve todos los hechizos durante 4 s."},
[52973] = {name = "Aliento de Escarcha", description = "[q]Inflige daño de Escarcha a los enemigos frente al taumaturgo y los inmoviliza hasta 6 sec."},
[52974] = {name = "Rugido feroz", description = "[q]Reduce el poder de ataque cuerpo a cuerpo de los enemigos cercanos durante 15 sec."},
[52977] = {name = "Torbellino", description = "[q]Ataca a los enemigos cercanos con un torbellino que inflige daño normal y genera mucha amenaza."},
[52979] = {name = "Absorción frígida", description = "[q]Vuelve a ganar un 5% de salud cada segundo durante 8 segundos y un 5% extra de salud por cualquier daño que reciba mientras dura."},
[52982] = {name = "Inmunidad de Akali", description = "[q]Consume una pequeña parte de la energía de Akali para hacerte inmune a cualquier daño y efecto."},
[52989] = {name = "Aturdimiento de Akali", description = "[q]Utiliza la energía de Akali para dejar indefensos a todos tus oponentes."},
[52993] = {name = "Ritual de aniquilación de Akali", description = "[q]Un ritual que aniquilará por completo los cuerpos y almas de aquellos a quienes selecciones como objetivo. No hay retorno posible."},
[53004] = {name = "Compulsión de Har'koa", description = "[q]Fuerza al objetivo para que obedezca los deseos de Har'koa."},
[53029] = {name = "Desafío de Har'koa", description = "[q]Har'koa reta al profeta de Akali."},
[53030] = {name = "Veneno parasitario", description = "[q]Drena 500 p. de salud de los enemigos de alrededor por segundo y la transfiere al taumaturgo. Si un objetivo muere mientras está afectado, el taumaturgo se sanará un 10%. Dura 10 sec."},
[53032] = {name = "Aluvión de garras", description = "[q]Libera la ira de Soo-brada sobre los enemigos cercanos y los ataca con furia durante 10 sec."},
[53039] = {name = "Desplegar paracaídas", description = "[q]Despliega el paracaídas para no morir en la caída."},
[53043] = {name = "Choque eléctrico", description = "[q]Electrocuta a un enemigo y después inflige daño de Naturaleza extra cada 3 s durante 12 sec."},
[53044] = {name = "Descarga de relámpagos", description = "[q]Golpea a un objetivo con relámpagos, lo que le inflige daño de Naturaleza."},
[53045] = {name = "Dormir", description = "[q]Duerme al objetivo enemigo durante un máximo de 4 sec. Cualquier daño infligido despertará al objetivo. Solo se puede dormir a un objetivo cada vez."},
[53047] = {name = "Bramido tempestuoso", description = "[q]Ruge furioso, lo que aumenta un 50% el daño infligido."},
[53048] = {name = "Rugido alarmante", description = "[q]Libera a los amigos cercanos de efectos de frenado, control de masas y de aturdimiento."},
[53049] = {name = "Aluvión cargado", description = "[q]Ataca a todos los enemigos cercanos con un aluvión de hachas que dura 6 sec."},
[53052] = {name = "Salir de fase", description = "[q]Vuelve al objetivo invulnerable contra los ataques y hechizos hostiles, pero no puede moverse, ni atacar. Dura hasta 20 sec."},
[53059] = {name = "Lanza con punta envenenada", description = "[q]Inflige daño físico cada 3 s durante 12 sec."},
[53068] = {name = "Puñetazo rúnico", description = "[q]Golpe masivo que inflige 3150 to 3850 p. de daño y graba una runa en la piel del objetivo."},
[53070] = {name = "Orden de huargen", description = "[q]Controla la mente del objetivo. Dura 20 sec o hasta que la salud del objetivo caiga por debajo del 35%."},
[53071] = {name = "Tormenta de truenos", description = "[q]Invoca una descarga de relámpagos que inflige daño a los enemigos cercanos en un radio de 15 m. Inflige 3700 to 4300 p. de daño de Naturaleza a todos los enemigos cercanos y los derriba."},
[53072] = {name = "Descarga tormentosa", description = "[q]Inflige 3238 to 3762 p. de daño a un enemigo."},
[53086] = {name = "Descarga de las Sombras", description = "[q]Lanza una descarga de magia oscura a un enemigo y le inflige daño de las Sombras."},
[53093] = {name = "Disparo de veraplata", description = "[q]Dispara a un enemigo con una bala de veraplata."},
[53094] = {name = "Mordedura de huargen infectada", description = "[q]Infecta a un objetivo enemigo, inflige 150 p. de daño de Naturaleza a un enemigo cada 5 s y acumulándose hasta 3 veces. En el nivel de infección 3, el objetivo se convierte en un huargen."},
[53096] = {name = "Sentencia de Quetz'lun", description = "[q]Juzga a un Drakkari indigno de seguir viviendo. Al menos hasta que su espíritu se reincorpore."},
[53110] = {name = "Devorar humanoide", description = "[q]Devora a un cruzado Escarlata para rellenar el maná del vencedor Razaescarcha."},
[53114] = {name = "Descarga de muerte congelada", description = "[q]Dispara una Descarga de muerte congelada a los objetivos enemigos, inflige 13125 to 16875 p. de daño a todos los humanos y 35000 to 45000 p. de daño a las balistas."},
[53117] = {name = "Asalto con balista", description = "[q]Dispara una jabalina que explota en su destino, inflige 9901 to 14099 p. de daño a todos los enemigos en el área de impacto."},
[53167] = {name = "Relámpago bifurcado", description = "[q]Inflige daño de Naturaleza a los enemigos que se encuentran en un cono frente al taumaturgo."},
[53177] = {name = "Puertas principales de telaraña", description = nil},
[53185] = {name = "Puerta lateral de telaraña", description = nil},
[53210] = {name = "Apocalipsis", description = "[q]El cielo descargará su ira."},
[53218] = {name = "Atracción congelada", description = "[q]Reduce significativamente la velocidad de movimiento."},
[53229] = {name = "Tormenta violenta", description = "[q]Crea una violenta tormenta en el área objetivo que inflige 3700 to 4300 p. de daño de Naturaleza a los enemigos cada 2 s y aumenta el tiempo entre los ataques de los enemigos un 67%. Dura 15 sec."},
[53239] = {name = "Salva de hachas", description = "[q]Lanza una salva de hachas."},
[53273] = {name = "Reclamo explosivo", description = "[q]Tritura a los enemigos cercanos con metralla e inflige 1440 to 2160 p. de daño físico."},
[53278] = {name = "Necrofaplosión", description = "[q]¡BUM!"},
[53314] = {name = "Descarga de relámpagos", description = "[q]Golpea a un objetivo con relámpagos, lo que le inflige daño de Naturaleza."},
[53317] = {name = "Desgarrar", description = "[q]Inflige daño físico a un enemigo cada 3 s durante 15 sec."},
[53318] = {name = "Machaque", description = "[q]Aplasta al objetivo, inflige 5550 to 6450 p. de daño y repeliéndolo."},
[53322] = {name = "Machacar telarañas", description = "[q]Lanza una telaraña sobre un enemigo, inmovilizándolo e infligiendo 200 p. de daño por segundo durante 6 sec."},
[53330] = {name = "Herida infectada", description = "[q]Aumenta el daño físico infligido a un enemigo durante 20 sec."},
[53333] = {name = "Descarga de las Sombras", description = "[q]Lanza una descarga de magia oscura a un enemigo y le inflige daño de las Sombras."},
[53334] = {name = "Animar huesos", description = "[q]Lanza una pila de huesos y les da vida. Crea una serie de huesos animados cada 3 s durante 9 sec."},
[53345] = {name = "Asalto con flechas", description = "[q]Dispara una flecha explosiva que inflige 438 to 562 p. de daño a todos los enemigos en un radio de 4 m."},
[53350] = {name = "Niebla extintiva", description = "[q]Una niebla fría invade el ambiente, extinguiendo los efectos de las llamas y sanando a los amigos cercanos."},
[53361] = {name = "Frenesí", description = "[q]Velocidad de ataque aumentada un 50% durante 12 sec."},
[53363] = {name = "Sacudida de alas", description = "[q]Inflige 9000 to 15000 p. de daño a los enemigos en un cono frente al taumaturgo, derribándolos y ralentizando su velocidad de movimiento."},
[53394] = {name = "Zurrar", description = "[q]Zurra a un enemigo infligiendo 943 to 1057 p. de daño e interrumpiendo el hechizo que esté lanzando durante 5 sec."},
[53395] = {name = "Golpe heroico", description = "[q]Un ataque contundente que inflige 2625 to 3375 p. de daño físico."},
[53399] = {name = "Embate de barrido", description = "[q]Inflige daño normal más 50 p. a los enemigos que se encuentran en un cono frente al taumaturgo y los derriba."},
[53400] = {name = "Nube ácida", description = "[q]Pulveriza ácido en la ubicación del objetivo, lo que crea una nube que inflige 707 to 793 p. de daño de Naturaleza cada segundo a los enemigos que se encuentran dentro de ella. Dura 1.50 min."},
[53401] = {name = "Rabioso", description = "[q]Tu mascota entra en un frenesí de muerte. Los ataques que impacten en el objetivo tienen una probabilidad de aumentar el poder de ataque un 5%. Este efecto se acumula hasta 5 veces. Dura 20 sec."},
[53406] = {name = "Agarrón de telaraña", description = "[q]Dispara telarañas pegajosas, inflige 2357 to 2643 p. de daño y atrapando a los enemigos, arrastrándolos hacia el taumaturgo."},
[53418] = {name = "Perforar armadura", description = "[q]Reduce la armadura de un enemigo un 50% durante 8 sec."},
[53425] = {name = "Lanzar pelambre de oso", description = "[q]Inflige 693 to 806 p. de daño físico."},
[53426] = {name = "Lame tus heridas", description = "[q]Tu mascota se sana un 100% de su salud total durante 5 sec mientras esté canalizando."},
[53432] = {name = "Trampa para osos", description = "[q]Coloca una trampa para osos que atrapa al primer enemigo que se aproxime, reteniéndolo e infligiendo 370 to 430 p. de daño por herida sangrante por 2 s durante 5 sec. Si se inflige algún daño, el efecto se interrumpirá. La trampa existirá durante 1 min. Solo puede haber activa una trampa a la vez."},
[53434] = {name = "Llamada de lo Salvaje", description = "[q]Tu mascota ruge, aumentando un 10% el poder de ataque cuerpo a cuerpo y a distancia de tu mascota. Dura 20 sec."},
[53437] = {name = "Partedorsales", description = "[q]Inflige daño con arma y aturde al objetivo durante 4 sec."},
[53438] = {name = "Incitar horror", description = "[q]Provoca que el objetivo enemigo huya de horror durante 2 sec e inflige 450 p. de daño de las Sombras cada 1 s."},
[53454] = {name = "Empalar", description = "[q]Dispara una púa a través del suelo que empala a todos los objetivos en un radio de 4 m, inflige 2828 to 3172 p. de daño y los lanza por los aires. Este ataque ignora la armadura."},
[53463] = {name = "Devolver carne", description = "[q]Devuelve la carne al objetivo."},
[53467] = {name = "Enjambre parasitante", description = "[q]Crea un enjambre de langostas que protege al taumaturgo. Las langostas infligen 330 to 370 p. de daño de Naturaleza por segundo a todos los enemigos en un radio de 60 m. Dura 15 sec."},
[53472] = {name = "Aporrear", description = "[q]Entras en cólera y aporreas a todos los enemigos frente al taumaturgo en un radio de 15 m, inflige 9425 to 10575 p. de daño y golpeándolos contra el suelo durante 6 sec."},
[53476] = {name = "Intervenir", description = "[q]Tu mascota corre a gran velocidad hacia un miembro del grupo, lo que absorbe 50% de la salud total de la mascota del daño recibido del próximo ataque."},
[53477] = {name = "Provocar", description = "[q]Tu mascota provoca que el objetivo la ataque durante 3 sec."},
[53478] = {name = "Última carga", description = "[q]Tu mascota gana temporalmente un 30% de su salud máxima durante 20 sec. Cuando el efecto se disipa, la salud se pierde."},
[53480] = {name = "Rugido de sacrificio", description = "[q]Protege a un objetivo amistoso de los golpes críticos, haciendo que los ataques contra ese objetivo no puedan ser golpes críticos, pero un 20% de todo el daño recibido por ese objetivo también lo recibe la mascota. Dura 12 sec."},
[53490] = {name = "Cabezota", description = "[q]Elimina todos los efectos que reducen el movimiento y todos los efectos que provocan pérdida de control sobre tu mascota y reduce el daño infligido a tu mascota un 20% durante 12 sec."},
[53493] = {name = "Incinerar", description = "[q]Inflige 1480 to 1720 p. de daño de Fuego a tu objetivo."},
[53496] = {name = "Llamas disparadas", description = "[q]De las manos del taumaturgo salen llamas disparadas que infligen daño de Fuego a los enemigos cercanos."},
[53498] = {name = "Flagelo", description = "[q]Un flagelo feroz que inflige daño al objetivo y a un máximo de 3 enemigos cercanos."},
[53499] = {name = "Arañazo", description = "[q]Araña al objetivo, lo que inflige 880 to 1320 p. de daño y 1005 p. de daño extra durante 9 sec."},
[53504] = {name = "Llamas chisporroteantes", description = "[q]200 p. de daño de Fuego infligido cada 3 s durante 24 sec.\n Niebla extintiva reduce este efecto."},
[53508] = {name = "Mordedura de lobezno", description = "[q]Un ataque feroz que inflige [1 + ((Ranged attack power * 0.40) * 0.10)] p. de daño, modificados por el nivel de mascota, que tu mascota puede usar cuando realice un ataque crítico. No se puede esquivar, bloquear o parar."},
[53517] = {name = "Rugido de recuperación", description = "[q]El rugido inspirador de tu mascota restaura 30 p. de enfoque durante 9 sec."},
[53520] = {name = "Alfazaques carroñeros", description = "[q]Invoca 2 alfazaques carroñeros cada segundo durante 4 sec."},
[53536] = {name = "Explosión aullante", description = "[q]Golpea al objetivo con un viento helado que inflige 278 to 302 p. de daño de Escarcha a todos los enemigos en un radio de 10 m. Inflige el triple de daño a los enemigos congelados."},
[53549] = {name = "Toque helado", description = "[q]Inflige de 86 a 92 p. de daño de Escarcha modificado por el poder de ataque y reduce las velocidades de lanzamiento, ataque cuerpo a cuerpo y ataque a distancia un 86 to 92% durante until cancelled."},
[53550] = {name = "Helada mental", description = "[q]Golpea la mente del objetivo con frío, inflige 100 p. de daño de Escarcha modificados por el poder de ataque e interrumpe el lanzamiento de hechizos mientras que también evita que se lance ningún hechizo de esa escuela durante 4 sec."},
[53570] = {name = "Frío hambriento", description = "[q]Desata todo el poder rúnico disponible para eliminar de repente todo el calor de la tierra que rodea al caballero de la Muerte. Los enemigos que se encuentren en un radio de 10 m quedan atrapados en hielo, impidiendo que puedan realizar ninguna acción durante 1 s por cada 5 p. de poder rúnico. Se considera que los enemigos están congelados, pero cualquier daño romperá el hielo."},
[53602] = {name = "Dardo", description = "[q]Lanza un rápido dardo al enemigo, inflige 750 p. de daño cada 3 s. Dura 12 sec."},
[53605] = {name = "Degeneración", description = "[q]Ataca instantáneamente al objetivo, le inflige un 60% de daño con arma y le implanta una enfermedad que inflige 1400 p. de daño durante 21 sec. Cualquier hechizo de sanación en el tiempo que haya sobre el objetivo se corrompe y aumenta drásticamente el daño que inflige la enfermedad a la vez que elimina el efecto sanador."},
[53607] = {name = "Rejuvenecimiento", description = "[q]Sana a un aliado cada 3 s durante 12 sec."},
[53608] = {name = "Flor de vida", description = nil},
[53616] = {name = "Salva de descarga de veneno", description = "[q]Dispara veneno al enemigo, lo que inflige daño de Naturaleza y luego daño extra cada 3 s durante 9 sec."},
[53617] = {name = "Descarga de veneno", description = "[q]Dispara veneno al enemigo, lo que inflige daño de Naturaleza y luego daño extra cada 3 s durante 9 sec."},
[53618] = {name = "Hender armadura", description = "[q]Hiende la armadura de un enemigo y la reduce más en cada uso de Hender armadura. Se puede aplicar hasta 20 veces. Dura 20 sec."},
[53625] = {name = "Salto heroico", description = "[q]Carga contra un enemigo, lo que le inflige el daño normal más 0 p. y aturde al oponente durante 2 sec."},
[53629] = {name = "Gancho", description = "[q]Inflige el daño normal más 150 p. a un enemigo y lo derriba."},
[53630] = {name = "Trueno", description = "[q]Invoca una descarga de relámpagos que inflige daño a los enemigos cercanos en un radio de 10 m. Inflige 14003 to 15997 p. de daño de Naturaleza a todos los enemigos cercanos y los lanza a 1599.7 m."},
[53631] = {name = "Rajar", description = "[q]Inflige daño con arma más 30 p. a un enemigo y a sus aliados cercanos. Afecta hasta a 5 objetivos y los derriba."},
[53632] = {name = "Necrofaplosión", description = "[q]¡BUM!"},
[53633] = {name = "Rajar", description = "[q]Inflige daño con arma más 30 p. a un enemigo y a sus aliados cercanos. Afecta hasta a 5 objetivos y los derriba."},
[53634] = {name = "Pisotón", description = "[q]Pisotea el suelo, inflige daño físico a los enemigos en una gran área y los lanza por los aires."},
[53635] = {name = "Abrazo de la Muerte", description = "[q]Golpea a todos los enemigos cercanos, inflige 13125 to 16875 p. de daño y sana 13125 to 16875 p. a todos los aliados cercanos."},
[53638] = {name = "Cólera Sagrada", description = "[q]Envía descargas de Poder Sagrado en todas direcciones, e inflige 765 to 935 p. de daño Sagrado a todos los enemigos en un radio de 0 m, doblándose en cada salto."},
[53639] = {name = "Golpe letal", description = "[q]Ataca instantáneamente al objetivo, le inflige un 400% de daño con arma y le implanta una enfermedad que inflige 8400 p. de daño durante 21 sec. Cualquier hechizo de sanación en el tiempo que haya sobre el objetivo se corrompe y aumenta drásticamente el daño que inflige la enfermedad a la vez que elimina el efecto sanador."},
[53640] = {name = "Añublo profano", description = "[q]Un enjambre vil de insectos profanos rodea al taumaturgo en un radio de 10 m. Todos los enemigos en el enjambre reciben 0 p. de daño por segundo y se apestan con una enfermedad que se acumula hasta 3 veces. Persiste 1 s por 10 p. de poder rúnico. Generar poder rúnico durante el efecto puede prolongarlo."},
[53642] = {name = "El poder de Mograine", description = "[q]Potenciado por el poder del alto señor Darion Mograine. Tu daño, salud y regeneración de salud aumentan de forma significativa. ¡Eres una fuerza de la oscuridad imparable!"},
[53643] = {name = "Golpe Sagrado", description = "[q]Inflige 7438 to 9562 p. de daño Sagrado a toda la Plaga frente al taumaturgo."},
[53646] = {name = "Pacto demoníaco", description = "[q]Aumenta tu daño con hechizos un 2% y tu demonio invocado otorga el efecto de Pacto demoníaco a todos los miembros del grupo o banda cercanos.\n\n El efecto Pacto demoníaco aumenta el poder con hechizos un 10%."},
[53658] = {name = "La luz del alba", description = "[q]Y habrá alba antes de la hora más oscura..."},
[53694] = {name = "Golpe de peste", description = "[q]Un golpe feroz que inflige daño con arma y apesta al objetivo, lo que inflige 2666.7 p. de daño de las Sombras durante 20 sec."},
[53717] = {name = "Deflagración de cadáver", description = "[q]Hace que un cadáver explote e inflija 60 p. de daño de Naturaleza modificado por el poder de ataque a todos los enemigos en un radio de 20 m. Usará un cadáver cercano si no hay ninguno seleccionado. No afecta a los cadáveres mecánicos o elementales."},
[53721] = {name = "Muerte y descomposición", description = "[q]10 p. de daño de las Sombras modificado por el poder de ataque infligido cada 1 s a todos los objetivos en el área afectada durante 10 sec. Tiene la posibilidad de provocar que los objetivos afectados se encojan de miedo."},
[53765] = {name = "Abrir portal de las Sombras", description = "[q]Abre un portal hacia el reino de las sombras para invocar a un esbirro que se encuentre dentro."},
[53766] = {name = "Caparazón antimagia", description = "[q]Rodea al caballero de la Muerte con un caparazón antimagia, absorbiendo un 75% del daño infligido por los hechizos dañinos siguientes. El daño absorbido por el escudo antimagia carga al caballero de la Muerte con poder rúnico extra. Dura 5 sec."},
[53769] = {name = "Espiral de la muerte", description = "[q]Dispara una oleada de energía profana, que inflige daño de las Sombras modificado por el poder de ataque a un objetivo enemigo o sana daño a un objetivo no-muerto amistoso."},
[53778] = {name = "Imposición de manos", description = "[q]Una poderosa sanación."},
[53788] = {name = "Ráfaga de lava", description = "[q]Lanzas al objetivo lava fundida que inflige 1776 to 2264 p. de daño de Fuego. Si el objetivo tiene Choque de llamas, Ráfaga de lava consumirá Choque de llamas, haciendo que Ráfaga de lava aseste un golpe crítico."},
[53790] = {name = "Actitud defensiva", description = "[q]Actitud de combate defensiva."},
[53791] = {name = "Actitud rabiosa", description = "[q]Una actitud agresiva."},
[53792] = {name = "Actitud de batalla", description = "[q]Actitud de combate equilibrada."},
[53801] = {name = "Frenesí", description = "[q]Aumenta un 25% la velocidad de ataque del taumaturgo y un 50% el daño físico que inflige durante 10 min."},
[53813] = {name = "Escudo Arcano", description = "[q]Protege al taumaturgo del daño."},
[53824] = {name = "Lanzar", description = "[q]Lanza tu arma al objetivo."},
[53850] = {name = "Ráfaga de las Sombras", description = "[q]Lanza una ráfaga de energía que derriba a todos los enemigos cercanos, inflige 960 to 1440 p. de daño de las Sombras."},
[54028] = {name = "¡Intruso!", description = "[q]Un mago guardián te ha teletransportado fuera de El Enclave de Plata."},
[54029] = {name = "¡Intruso!", description = "[q]Un mago guardián te ha teletransportado fuera del Santuario Atracasol."},
[54049] = {name = "Mordedura de las Sombras", description = "[q]Muerde al enemigo, inflige [ 166 + (1.228 * (Spell power * 0.5))] p. de daño de las Sombras más un 30 % de daño extra por cada uno de tus efectos de daño en el tiempo sobre el objetivo."},
[54074] = {name = "Veneno hiriente", description = "[q]Envenena al enemigo, reduciendo todos los efectos de sanación utilizados sobre él un 10% durante 15 sec."},
[54087] = {name = "Lanzar cristal de añublo", description = "[q]Lanza un cristal de añublo a un objetivo enemigo. El cristal se hace añicos con el impacto, inflige 2043 to 2257 p. de daño de Naturaleza y deja atrás un residuo tóxico que inflige daño extra durante 45 sec."},
[54093] = {name = "Silencio", description = "[q]Silencia a los enemigos cercanos impidiendo que lancen hechizos durante 6 sec."},
[54095] = {name = "Bola de Fuego", description = "[q]Inflige 2025 to 2475 p. de daño de Fuego a un enemigo."},
[54102] = {name = "Invocar esfera etérea", description = "[q]Invoca una esfera etérea para ayudar al taumaturgo a luchar."},
[54105] = {name = "Niebla de añublo", description = "[q]Inflige daño de Naturaleza a los enemigos cercanos."},
[54113] = {name = "Descarga de las Sombras", description = "[q]Lanza un misil de magia oscura que inflige daño de las Sombras."},
[54115] = {name = "Explosión de cristal de añublo", description = "[q]Lanza un cristal de añublo a un objetivo enemigo. El cristal se hace añicos con el impacto, inflige 2043 to 2257 p. de daño de Naturaleza y deja atrás un residuo tóxico que inflige daño extra durante 45 sec."},
[54123] = {name = "Frenesí", description = "[q]Aumenta un 50% la velocidad de ataque del taumaturgo y un 50% el daño físico que inflige durante 10 min."},
[54132] = {name = "Arremetida de conmoción", description = "[q]Un golpe brutal que inflige 5000 p. de daño y aturde al oponente durante 5 sec."},
[54135] = {name = "Golpe poderoso", description = "[q]Un potente ataque que inflige 15300 to 20700 p. de daño físico."},
[54136] = {name = "Infusión de Sangreañublo", description = "[q]Sana al controlador del trol sangreañublo 2000 p. cada 1 s durante 5 sec."},
[54160] = {name = "Poder Arcano", description = "[q]Aumenta la velocidad de lanzamiento y de ataque un 75% y el daño físico que inflige un 75%."},
[54164] = {name = "Invocar jugadores", description = nil},
[54166] = {name = "Santuario del hacedor", description = "[q]Elimina a El Etimidiano del combate y lo protege de todos los ataques y hechizos durante 12 sec. en ese tiempo, El Etimidiano tampoco puede atacar, ni usar hechizos."},
[54170] = {name = "Planear", description = "[q]Velocidad de vuelo aumentada un 350%."},
[54177] = {name = "Fervor de Zepik", description = "[q]Probabilidad de infligir daño para aumentar el daño que asesta Zepik durante un breve periodo. Se acumula hasta 5 veces."},
[54185] = {name = "Tajo con garra", description = "[q]Inflige 120 p. de daño físico."},
[54188] = {name = "Hender armadura", description = "[q]Corta la armadura de un enemigo y la reduce más en cada uso de Hender armadura. Se puede acumular hasta 5 veces. Dura 30 sec."},
[54193] = {name = "Furia de la tierra", description = "[q]Lanza a todos los enemigos cercanos por los aires, les inflige daño y reduce su velocidad de movimiento durante 6 sec."},
[54194] = {name = "Aliviar", description = "[q]Restaura salud a un aliado."},
[54195] = {name = "Cólera de la tierra", description = "[q]Lanza una bola de tierra encantada al enemigo, inflige daño considerable."},
[54202] = {name = "Salva de tromba Arcana", description = "[q]Lanza contra los enemigos misiles que infligen 1758 to 2042 p. de daño Arcano."},
[54210] = {name = "Lluvia de Fuego", description = "[q]Invoca una lluvia de arrabio que quema a todos los enemigos del área seleccionada infligiendo 6 p. de daño de Fuego cada 3 s durante 5 sec."},
[54226] = {name = "Sacudida Arcana", description = "[q]Inflige 879 to 1021 p. de daño Arcano a un enemigo y aumenta el daño Arcano que recibe 150 p. durante 20 sec."},
[54237] = {name = "Explosión de agua", description = "[q]Dispara un chorro de agua contra un enemigo, inflige 2775 to 3225 p. de daño de Escarcha y repeliéndolo."},
[54241] = {name = "Salva de descarga de agua", description = "[q]Lanza una descarga de agua a todos los enemigos cercanos en un radio de 45 m que les inflige 1850 to 2150 p. de daño de Escarcha."},
[54261] = {name = "Lanza de hielo", description = "[q]Inflige 46 to 53 p. de daño de Escarcha a un objetivo enemigo. Inflige el triple de daño contra objetivos congelados."},
[54287] = {name = "Enfurecer", description = "[q]Aumenta un 60% la velocidad de ataque del taumaturgo y el daño físico que inflige durante 2 min."},
[54290] = {name = "Disparo de telaraña", description = "[q]Inflige 1500 p. de daño de Naturaleza y reduce la velocidad de movimiento un 10%. Dura 5 s. Se acumula hasta 7 veces."},
[54306] = {name = "Burbuja protectora", description = "[q]Protege al taumaturgo dentro de una burbuja de agua, reduciendo el daño recibido un 99%. Cada ataque o golpe lanzado contra el taumaturgo eliminará una carga. Dura until cancelled o hasta que se hayan usado 35 cargas."},
[54309] = {name = "Marca de oscuridad", description = "[q]Aflige a un objetivo con oscuridad pura e inflige 500 p. de daño de las Sombras por segundo. Además, cuando se sana al objetivo, inflige un 1885 to 2115 p. de daño de las Sombras a los amigos cercanos al objetivo. Dura 10 sec."},
[54311] = {name = "Escarabajos de la cripta", description = "[q]Invoca a varios escarabajos de la cripta para que ataquen al objetivo del taumaturgo."},
[54312] = {name = "Frenesí", description = "[q]Aumenta la velocidad de lanzamiento un 100% y el daño físico infligido un 25% durante 10 min."},
[54313] = {name = "Enjambre de escarabajos de la cripta", description = "[q]Invoca un enjambre de escarabajos de la cripta para que ataquen a todos los objetivos enemigos cercanos."},
[54314] = {name = "Drenar poder", description = "[q]Drena la energía de todos los enemigos cercanos, reduciendo el daño que infligen un 1% y aumentando el que infliges tú un 2%."},
[54318] = {name = "Piedra de hogar", description = "[q]Te devuelve a <Hearthstone Location>. Habla con un tabernero en otro sitio para convertirlo en tu nuevo hogar."},
[54322] = {name = "Escudo divino", description = "[q]Protege al taumaturgo de todos los ataques y hechizos durante 12 sec, pero también aumenta el tiempo entre los ataques un 100% durante ese tiempo."},
[54323] = {name = "Bendición de la Luz", description = "[q]Imbuye al objetivo con la bendición y la aprobación de la Luz Sagrada."},
[54340] = {name = "Vómito vil", description = "[q]Inflige 602 to 698 p. de daño de Naturaleza y reduce la velocidad de movimiento del enemigo un 50% durante 10 sec."},
[54345] = {name = "Golpe marchitador", description = "[q]Un golpe que inflige un 120% de daño con arma normal y marchita el cuerpo de un enemigo, aumenta el tiempo entre los ataques un 30% durante 4 sec."},
[54356] = {name = "Rabia imparable", description = "[q]Aumenta un 50% la velocidad de ataque del taumaturgo y el daño físico que inflige un 25% durante until cancelled. El taumaturgo no se puede detener."},
[54361] = {name = "Al vacío", description = "[q]Desplaza violentamente al objetivo al vacío, con lo que inflige 750 p. de daño de las Sombras por segundo. Al final de este efecto, el objetivo se introduce completamente en el vacío y puede ver las criaturas del vacío escondidas."},
[54369] = {name = "Invocar a centinela del vacío", description = "[q]Invoca a un centinela del vacío."},
[54378] = {name = "Herida mortal", description = "[q]Un golpe lacerante alcanza al objetivo, reduciendo la eficacia de los hechizos de sanación un 10%."},
[54386] = {name = "Vigilancia de Darmuk", description = "[q]Aumenta la probabilidad de esquivar del taumaturgo un 75% durante 6 sec."},
[54387] = {name = "Garra famélica", description = "[q]Drena 278 to 322 p. de salud de un enemigo y se la transfiere al taumaturgo."},
[54396] = {name = "Vínculo óptico", description = "[q]Canaliza un haz de energía oscura sobre el objetivo y le inflige daño de las Sombras durante 12 sec. Este daño solo se inflige al principio y se construye a medida que el canal alcanza su duración completa."},
[54399] = {name = "Burbuja de agua", description = "[q]Quedas envuelto en una burbuja de agua que inflige 204 to 236 p. de daño de Escarcha cada 3 s y reduce la velocidad de movimiento un 50% durante 12 sec."},
[54400] = {name = "Muro de agua", description = "[q]Invocas un violento muro de agua que inflige 602 to 698 p. de daño de Naturaleza cuando entablas combate con objetivos hostiles."},
[54417] = {name = "Rayo de sufrimiento", description = "[q]Inflige daño de las Sombras a un solo objetivo en el tiempo."},
[54420] = {name = "Entregar grifo", description = "[q]Entrega el grifo del Embate a Uzo Clamamuerte en el Ascenso de la Muerte."},
[54424] = {name = "Inteligencia vil", description = "[q]Aumenta 2126 p. el maná máximo de los miembros del grupo o banda y provoca que regeneren 0 p. de maná cada 5 s."},
[54429] = {name = "Remolino de relámpagos", description = "[q]Gira alrededor del enemigo, desatando una dolorosa descarga de rayos."},
[54431] = {name = "Lanzar lanza", description = "[q]Lanza un arma al enemigo, lo que inflige daño físico."},
[54451] = {name = "Toque marchitado", description = "[q]Pone enfermo a un enemigo, inflige daño de las Sombras cada 3 s y reduciendo la velocidad de movimiento un 50% durante 12 sec."},
[54453] = {name = "Trampa arácnida", description = "[q]Canaliza un rayo de telaraña, que impide que el objetivo se mueva e inflige 907 to 1053 p. de daño de las Sombras después de 3 s."},
[54458] = {name = "Machaque", description = "[q]Repele a tu enemigo, inflige 500 p. de daño físico."},
[54459] = {name = "Magullar", description = "[q]Ataca a un enemigo con un ataque frontal potente e inflige 1360 to 1840 p. de daño."},
[54460] = {name = "Cargar", description = "[q]Embiste a tu objetivo y lo aturde durante 3 segundos."},
[54462] = {name = "Chirrido aullante", description = "[q]La velocidad de movimiento de enemigos cercanos se reduce un 50% durante 6 sec.\n Reduce el poder de ataque de enemigos cercanos 25 p."},
[54467] = {name = "Armadura ósea", description = "[q]El grifo osario se rodea de 5 huesos giratorios. Mientras quede al menos 1 hueso, recibe un 40% menos de daño de todos los orígenes e inflige un 20% más de daño con todos los ataques, hechizos y facultades. Cada hechizo o ataque que impacte consume un hueso. Dura 5 min."},
[54469] = {name = "Golpe de peste", description = "[q]Un golpe feroz que inflige daño de garra más 2000 p. modificados por el poder de ataque y apesta al objetivo infligiendo 2200 p. de daño de las Sombras durante 12 sec. Solo sirve contra los jinetes de grifo del Embate."},
[54470] = {name = "Mordedura venenosa", description = "[q]Escupe veneno al enemigo e inflige daño de Naturaleza y daño extra cada 3 s durante 9 sec."},
[54475] = {name = "Desenfreno", description = "[q]Aumenta un 30% la velocidad de ataque del taumaturgo y el daño físico que inflige un 20% durante 6 sec. Tras 6 sec provoca agotamiento."},
[54476] = {name = "Presencia de sangre", description = "[q]Fortalece al grifo osario con Presencia de sangre, lo que aumenta el daño un 5% y sana al grifo osario un 1% del daño infligido."},
[54477] = {name = "Agotado", description = "[q]El Desenfreno ha Agotado al objetivo, lo aturde durante 4 sec."},
[54479] = {name = "Escudo de tierra", description = "[q]Protege al objetivo con un escudo de tierra que hace que los ataques cuerpo a cuerpo sanen al objetivo protegido. Este efecto solo ocurre una vez cada pocos segundos. 20 cargas. Dura 2 min."},
[54481] = {name = "Sanación en cadena", description = "[q]Imbuye a un aliado herido con energía de sanación que se extiende a otro aliado cercano. El hechizo afecta hasta a 3 objetivos."},
[54482] = {name = "Sangre de Sseratus", description = "[q]Invoca el poder de Sseratus para aumentar un 10% la velocidad de ataque y de lanzamiento durante 2 min."},
[54483] = {name = "Vigor de Sseratus", description = "[q]Llama el poder de Sseratus para aumentar un 60% la velocidad de ataque y de lanzamiento y reducir el daño recibido un 30% durante 2 min."},
[54484] = {name = "Invocar serpiente Drakkari", description = "[q]Invoca una serpiente Drakkari para que ayude al taumaturgo."},
[54487] = {name = "Ataque con salto", description = "[q]Salta al objetivo y le inflige un 120% de daño con arma."},
[54511] = {name = "Choque de tierra", description = "[q]Golpea a un enemigo con fuerza de conmoción, lo que inflige daño de Naturaleza e interrumpe el hechizo que se esté lanzando durante 2 sec."},
[54512] = {name = "Escudo de peste", description = "[q]Absorbe 1500 p. de daño."},
[54516] = {name = "Ansia de sangre", description = "[q]Aumenta la velocidad de ataque cuerpo a cuerpo, a distancia y de lanzamiento de hechizos de todos los miembros del grupo un 35%. Dura 20 sec."},
[54517] = {name = "Atracción magnética", description = "[q]Atrae a un objetivo enemigo de la plataforma contraria al taumaturgo."},
[54520] = {name = "Penitencia", description = "[q]Lanza una salva de luz Sagrada al objetivo, y le inflige 296 to 344 p. de daño Sagrado a un enemigo cada 1 s durante 3 sec."},
[54524] = {name = "Sudario de oscuridad", description = "[q]Al recibir daño, reduce la sanación recibida por el atacante un 20%."},
[54526] = {name = "Tormento", description = "[q]Atormenta la mente de un enemigo e inflige 105 to 115 p. de daño de las Sombras cada 1 s y aturde al objetivo durante 5 sec."},
[54527] = {name = "Saliva corrosiva", description = "[q]Chupa la enemigo, cubriéndolo de saliva que se come el 5% de su armadura en cada aplicación. Se puede acumular hasta 20 veces."},
[54532] = {name = "Púa de hielo", description = "[q]Una púa de hielo inflige 629 to 731 p. de daño de Escarcha."},
[54537] = {name = "Detonación", description = "[q]Inflige 8313 to 10687 p. de daño de Fuego a todos los enemigos cercanos."},
[54559] = {name = "Oleada de energía", description = nil},
[54565] = {name = "Hachazo", description = "[q]Da un Hachazo al objetivo, inflige daño de Naturaleza y embruja al objetivo durante 3 sec."},
[54580] = {name = "Coagular", description = "[q]La coagulación aumenta el daño físico infligido 150 p."},
[54588] = {name = "Racha de viento", description = "[q]Una racha de viento se lleva al objetivo por los aires y le inflige 1249 to 1451 p. de daño de Naturaleza."},
[54589] = {name = "Vientos arremolinados", description = "[q]Los vientos arremolinados rodean al taumaturgo, aumentan la velocidad de movimiento un 25% y aumentan la velocidad de lanzamiento y ataque un 10% durante 1.50 min."},
[54593] = {name = "Golpe de serpiente", description = "[q]El veneno de serpiente inflige 213 to 247 p. de daño de Naturaleza cada 2 s durante 8 sec."},
[54601] = {name = "Forma de serpiente", description = "[q]El taumaturgo toma la forma de una serpiente, aumentando el daño infligido 200 p. y aumenta la velocidad de lanzamiento y ataque un 25% durante 8 sec."},
