[55814] = {name = "Flema de Eck", description = "[q]Eck escupe bilis tóxica en un cono delante suyo, inflige 2970 p. de daño de Naturaleza y drenando 220 p. de maná cada 1 s durante 3 sec."},
[55815] = {name = "Brinco de Eck", description = "[q]Eck salta sobre un objetivo distante."},
[55816] = {name = "Eck rabioso", description = "[q]Eck se vuelve rabioso, aumentando su velocidad de ataque un 150% y todo el daño que inflige un 500%."},
[55821] = {name = "Pisotón masivo", description = "[q]Pisotea el suelo, inflige 6300 to 7700 p. de daño físico a todos los enemigos cercanos y los derriba."},
[55825] = {name = "Tormenta de truenos", description = "[q]Invoca una descarga de relámpagos que inflige daño 1027 to 1173 p. de daño de Naturaleza a los enemigos cercanos que se encuentren en un radio de 10 m."},
[55847] = {name = "Vacío de sombras", description = "[q]Crea una tormenta de sombras que dura 10 sec e inflige 1388 to 1612 p. de daño de las Sombras cada 1 s."},
[55848] = {name = "Invisibilidad", description = "[q]Vuelve invisible al taumaturgo."},
[55850] = {name = "Salva de descarga de las Sombras", description = "[q]Lanza misiles de magia oscura a los enemigos cercanos y les inflige daño de las Sombras."},
[55860] = {name = "Carga colmipala", description = "[q]Aumenta la velocidad de movimiento un 60% durante 5 sec."},
[55867] = {name = "Barba apestosa", description = "[q]Reduce el aguante de un enemigo un 1% durante 30 sec.\n\n\"Por dentro olía aún peor...\""},
[55872] = {name = "Orbe de llamas", description = "[q]Lanza una bola de Fuego enorme."},
[55876] = {name = "Contaminación por peste", description = nil},
[55881] = {name = "Huracán", description = "[q]Crea una violenta tormenta en el área objetivo que inflige daño de Naturaleza a los enemigos cada 5 s y aumenta el tiempo entre los ataques de los enemigos un 67%. Dura 10 sec."},
[55882] = {name = "Orbe de tormentas", description = "[q]Lanza una bola de relámpagos enorme."},
[55886] = {name = "Roca", description = "[q]Lanza una roca enorme a un enemigo."},
[55888] = {name = "Orbe de agua", description = "[q]Lanza una bola de agua enorme."},
[55889] = {name = "Orbe de Escarcha", description = "[q]Inflige 800 p. de daño de Escarcha al objetivo, reduciendo su velocidad de movimiento un 20%."},
[55909] = {name = "Ola aplastante", description = "[q]Una cascada de agua cae sobre los enemigos que se encuentran delante del taumaturgo en un cono de un 65 m, inflige 2313 to 2687 p. de daño de Escarcha."},
[55912] = {name = "Explosión de aire", description = "[q]Una fuerte ráfaga de viento derriba a todos los enemigos."},
[55916] = {name = "Ola de magma", description = "[q]Una cascada de magma cae sobre los enemigos que se encuentran delante del taumaturgo en un cono de 65 m, inflige 2313 to 2687 p. de daño de Fuego."},
[55918] = {name = "Ola de choque", description = "[q]Lanza una ola de fuerza delante del guerrero, inflige (25 / 100 * Attack power) p. de daño y aturde a todos los objetivos cercanos que se encuentren en un radio de 10 m en un cono frontal durante 4 sec."},
[55929] = {name = "Empalar", description = "[q]Empala al objetivo."},
[55931] = {name = "Crear Esfera de llamas", description = "[q]Crea una esfera de fuego puro que flota por el aire hacia el objetivo mientras lanza rayos abrasadores a los enemigos que se encuentren en un radio de 18 m."},
[55936] = {name = "Descenso en picado", description = "[q]Cae sobre una vermis de Las Cumbres Tormentosas y le causa 5850 to 7150 p. de daño."},
[55937] = {name = "Aroma a pescado", description = "[q]Estás cubierto de una salsa espesa hecha de mero."},
[55946] = {name = "Invocar a Bigotes", description = "[q]Llama a su fiel mascota, Mostacho."},
[55948] = {name = "Crecer", description = "[q]Crece, aumentado el daño infligido un 10%."},
[55958] = {name = "Descarga tormentosa", description = "[q]Lanza un martillo de tormenta contra una vermis Pico Tormenta, inflige 8750 to 11250 p. de daño y aturde al objetivo durante 4 sec antes de volver a la mano del que lo lanza."},
[55959] = {name = "Abrazo de los vampiros", description = "[q]Consume la fuerza vital del objetivo, le aturde e inflige 1500 p. de daño de las Sombras cada 2 s durante 20 sec o hasta que el príncipe Taldaram reciba 20000 p. de daño."},
[55960] = {name = "Kill Credit", description = nil},
[55964] = {name = "Esfumarse", description = "[q]Pone al taumaturgo en modo sigilo durante 2.50 sec."},
[55968] = {name = "Sed de sangre", description = "[q]Ataca instantáneamente al objetivo, inflige un 120% de daño normal con armas. Además, los siguientes 3 ataques cuerpo a cuerpo que acierten restaurarán un 1% de salud. Este efecto dura 10 sec."},
[55973] = {name = "Peste de sangre", description = "[q]Un golpe feroz que inflige daño con arma y apesta al objetivo, lo que inflige 2000 p. de daño de las Sombras durante 12 sec."},
[55980] = {name = "Pisotón de mamut", description = "[q]Inflige 5266 to 7734 p. de daño a los enemigos en un radio de 10 m."},
[55982] = {name = "Carga de mamut", description = "[q]Carga contra un enemigo, lo que le inflige el daño normal más 2647 to 2953 p. y aturde al oponente durante 3 sec."},
[55984] = {name = "Descarga de las Sombras", description = "[q]Lanza una descarga de magia oscura a un enemigo, le inflige daño de las Sombras y aumenta el daño de las Sombras recibido un 10% durante 10 sec."},
[55987] = {name = "Bola de Fuego engullidora", description = "[q]Dispara una bola de Fuego engullidora a los objetivos enemigos, inflige 3063 to 3937 p. de daño a todos los necrófagos y 10500 to 13500 p. de daño a los gigantes."},
[56036] = {name = "Runa de destrucción", description = "[q]Usa una runa ígnea sobre un enemigo durante 5 sec que inflige daño tras 5 s."},
[56038] = {name = "Descarga de peste", description = "[q]Una descarga de peste sacada directamente del corazón marchito del vargul. Sirve para torturar a los enemigos. Inflige 833 to 967 p. de daño de las Sombras."},
[56061] = {name = "Asolar", description = nil},
[56062] = {name = "Pisotón atronador", description = "[q]Envía una ola de fuerza hacia delante que inflige 3150 to 3850 p. de daño y derriba a todos los objetivos enemigos en un radio de 10 m."},
[56063] = {name = "Deflagración Arcana", description = "[q]Lanza una ola explosiva de magia e inflige 1350 to 1650 p. de daño Arcano a los enemigos cercanos."},
[56064] = {name = "Salva de descarga de las Sombras", description = "[q]Lanza misiles de magia oscura a los enemigos próximos y les inflige 1575 to 1925 p. de daño de las Sombras."},
[56091] = {name = "Pica llameante", description = "[q]Dispara un chorro rápido de Fuego a tu objetivo, e inflige 943 to 1057 p. de daño de Fuego. Otorga 1 p. de combo."},
[56092] = {name = "Engullir en llamas", description = "[q]Lanza una explosión de llamas, que incendia a tu objetivo infligiendo 1500 p. de daño de Fuego cada 3 s. Este efecto se acumula. Se prolonga por cada punto de combo:\n 1 p.: 6 segundos\n 2 p.: 10 segundos\n 3 p.: 14 segundos\n 4 p.: 18 segundos\n 5 p.: 22 segundos"},
[56095] = {name = "Flecha de Escarcha", description = "[q]Dispara una flecha de escarcha que inflige daño masivo y reduce la velocidad de movimiento durante 6 sec."},
[56105] = {name = "Vórtice", description = "[q]Estira un vórtice giratorio de energía Arcana, inflige daño Arcano a todos los enemigos que atrape."},
[56107] = {name = "Carga rabiosa", description = "[q]Carga contra el enemigo, lo derriba y le inflige el daño normal más 5100 to 6900 p."},
[56130] = {name = "Camada de peste", description = "[q]Hace que el enemigo enferme, inflige 12750 p. de daño de Naturaleza durante 30 sec."},
[56149] = {name = "Veneno mortal", description = "[q]Inflige daño de Naturaleza cada 3 s durante 12 sec."},
[56152] = {name = "Chispazo", description = "[q]Aumenta el daño infligido por el objetivo un 50% y sacrifica al taumaturgo durante el proceso."},
[56153] = {name = "Aura de guardián", description = "[q]Protege a los enjambristas en un gran radio alrededor del taumaturgo, volviéndolos inmunes al daño."},
[56219] = {name = "Ofrenda del heraldo", description = "[q]Aumenta el daño que inflige el taumaturgo un 200% durante 20 sec."},
[56223] = {name = "Cuerda de telaraña", description = nil},
