[56310] = {name = "Juramento roto", description = "[q]Aumenta un 30% la velocidad de ataque del taumaturgo y un 10% el daño físico que inflige durante 10 sec."},
[56319] = {name = "Bola de relámpagos", description = "[q]Inflige 850 p. de daño de Naturaleza cada 1 s durante 8 sec a los enemigos que se adentren en el área."},
