[56426] = {name = "Ejecutar", description = "[q]Intenta rematar a un enemigo herido, lo que inflige un 120% de daño normal. Ejecutar solo se puede usar con enemigos que tengan un 20% de salud o menos."},
[56451] = {name = "Escudo de tierra", description = "[q]Protege al objetivo con un escudo de tierra que hace que los ataques cuerpo a cuerpo sanen al objetivo protegido. Este efecto solo ocurre una vez cada pocos segundos. 10 cargas. Dura 2 min."},
