[56491] = {name = "Ráfaga de lava", description = "[q]Lanzas lava fundida al objetivo y le infliges daño de Fuego."},
[56504] = {name = "Sumersión", description = "[q]Desaparece bajo la superficie de la nieve y se vuelve móvil."},
[56505] = {name = "Oleada de poder", description = "[q]Dispara un rayo de magia pura a un enemigo e inflige 5000 p. de daño Arcano por segundo a todos los enemigos que lo rodeen durante 5 sec."},
