[56580] = {name = "Veneno pegajoso", description = "[q]Envenena a un enemigo, inflige 1000 p. de daño de Naturaleza y aumentado el coste de sus hechizos y facultades un 10% durante 45 sec."},
[56581] = {name = "Picadura fatal", description = "[q]Pica al enemigo e inflige daño de Naturaleza extra cada 2 s durante 12 sec. Para lanzar este hechizo el taumaturgo debe estar gravemente herido."},
[56584] = {name = "Toxinas combinadas", description = "[q]Las toxinas de Reptador Profundo cubren tus armas y se mezclan con tus venenos, otorgándote la posibilidad de infligir daño de Naturaleza extra cuando tus venenos infligen daño a un objetivo."},
[56605] = {name = "Veneno virulento", description = "[q]Inflige 2500 p. de daño de Naturaleza cada 3 s durante 24 sec. Se puede acumular hasta 3 veces."},
[56620] = {name = "Llamas iracundas", description = "[q]Inflige 197 to 253 p. de daño de Fuego cada 2.79900002 s durante 3 sec."},
[56621] = {name = "Orbe de truenos", description = "[q]Lanza una bola de relámpago al objetivo, inflige 1651 to 2349 p. de daño."},
