[56715] = {name = "Saquear salud", description = "[q]Roba instantáneamente 500 p. de salud a un enemigo, transfiriéndosela al taumaturgo."},
[56716] = {name = "Vientos helados", description = "[q]Vientos helados soplan alrededor del taumaturgo durante 2 min. El frío ralentiza la velocidad de lanzamiento de hechizos un 50% pero aumenta el daño infligido por los hechizos de Escarcha un 75%."},
