[56753] = {name = "Lanzar bola de nieve", description = "[q]Ordena a Snorri lanzar una bola de nieve a un aparecido hirviente."},
[56778] = {name = "Escudo de maná", description = "[q]Envuelve al taumaturgo con un escudo protector que dura 1 min y amortigua hasta 30000 p. de daño físico, drenando maná en lugar de salud. Drena 1 p. de maná por cada punto de daño amortiguado."},
