[56891] = {name = "Descarga de relámpagos", description = "[q]Golpea a un objetivo con relámpagos, lo que le inflige daño de Naturaleza."},
[56896] = {name = "Golpe de rinoceronte", description = "[q]Un ataque potente."},
[56897] = {name = "Llamada de tormenta", description = "[q]Inflige 2200 p. de daño de Naturaleza cada 1 s a una zona seleccionada durante 5 sec. (Tiempo de reutilización de 15 s.)"},
[56898] = {name = "Corrupción", description = "[q]Corrompe a los enemigos cercanos, lo que inflige daño de las Sombras cada 3 s durante 15 sec."},
[56908] = {name = "Aliento de llamas", description = "[q]Inflige 8750 to 11250 p. de daño de Fuego a los enemigos en un cono frente al taumaturgo."},
[56909] = {name = "Rajar", description = "[q]Inflige un 35% de daño con arma a un enemigo y a sus aliados más cercanos. Afecta hasta a 10 objetivos."},
[56910] = {name = "Latigazo de cola", description = "[q]Un barrido de cola golpea a todos los enemigos que se encuentren detrás del taumaturgo, lo que les inflige 3063 to 3937 p. de daño y los aturde durante 2 sec."},
