[57063] = {name = "Atracción Arcana", description = "[q]Aumenta el daño Arcano recibido 2000 p. durante 10 sec."},
[57066] = {name = "Atropello", description = "[q]Inflige 3299 to 3701 p. de daño físico a los enemigos cercanos."},
[57076] = {name = "Corroer carne", description = "[q]Corroe la carne del enemigo, luego inflige 825 p. de daño de Naturaleza extra cada 3 s durante 15 sec."},
[57088] = {name = "Salva de Descarga de tósigo", description = "[q]Amanitar lanza veneno a los jugadores, lo que les inflige daño de Naturaleza."},
[57090] = {name = "Devolver a la vida", description = "[q]Sana al objetivo 500 p. cada segundo. Dura 10 sec. Otorga un punto de combo. Se acumula hasta 5 veces."},
[57092] = {name = "Velocidad llameante", description = "[q]Aumenta la velocidad de vuelo un 500% durante 8 sec."},
[57094] = {name = "Azote", description = "[q]Inflige 4163 to 4837 p. de daño físico a un enemigo."},
[57095] = {name = "Raíces enredadoras", description = "[q]Enreda a un enemigo en raíces, lo que inflige daño de Naturaleza cada 2 s y lo inmoviliza durante un máximo de 10 sec."},
[57108] = {name = "Escudo de Llamas", description = "[q]Rodea al taumaturgo con un escudo de llamas que reduce todo el daño recibido un 80%. Se prolonga por cada punto de combo:\n 1 p.: 2 s\n 2 p.: 3 s\n 3 p.: 4 s\n 4 p.: 5 s\n 5 p.: 6 s"},
[57143] = {name = "Ráfaga de vida", description = "[q]Libera una ráfaga de energía rejuvenecedora, lo que sana a los objetivos amistosos que se encuentren en un radio de 60 m y aumenta un 50% la sanación realizada por el taumaturgo. La sanación y la duración aumentan por cada punto de combo:\n 1 p.: 5000 p. de sanación / 5 s\n 2 p.: 7500 p. de sanación / 10 s\n 3 p.: 10000 p. de sanación / 15 s\n 4 p.: 12500 p. de sanación / 20 s\n 5 p.: 15000 p. de sanación / 25 s"},
[57322] = {name = "Nova de relámpagos", description = nil},
[57374] = {name = "Descarga de las Sombras", description = "[q]Lanza una descarga de magia oscura a un enemigo y le inflige daño de las Sombras."},
[57376] = {name = "Descarga Sagrada", description = "[q]Lanza una descarga de magia de Luz a un enemigo y le inflige daño Sagrado."},
[57377] = {name = "Condenación", description = "[q]La magia de la luz golpea al enemigo, inflige 5655 to 6345 p. de daño Sagrado."},
[57381] = {name = "Dolor implacable", description = "[q]Magia oscura golpea a cada enemigo, inflige 5655 to 6345 p. de daño de las Sombras."},
[57385] = {name = "Cañón Argenta", description = "[q]Dispara a un enemigo de la Plaga y mira cómo arde."},
[57386] = {name = "Estampida", description = "[q]Tu rinoceronte vapulea a un enemigo cercano, lo que hace que reciba un 30 % de daño extra de los efectos de sangrado durante 30 sec."},
[57395] = {name = "Arremetida desesperada", description = "[q]Usa el último reducto de energía del taumaturgo e inflige un 200% de daño con arma y aturde al taumaturgo durante 3 sec."},
[57408] = {name = "Nube tormentosa", description = "[q]Invoca una Nube tormentosa alrededor del taumaturgo que inflige 300 p. de daño de Naturaleza cuando le atacan."},
[57412] = {name = "Bomba de expiación", description = "[q]Desata el día del Juicio Final sobre la Plaga, inflige ingentes cantidades de daño. Algunos dirían que el daño es absurdo."},
[57413] = {name = "Sueño intermitente", description = "[q]Inmerso en la Pesadilla Esmeralda."},
[57428] = {name = "Campo estático", description = "[q]Invoca un campo de energía chisporroteante donde se encuentre un objetivo aleatorio. Este campo inflige 9425 to 10575 p. de daño Arcano por segundo a todos los enemigos en un radio de 30 m de ese lugar."},
[57430] = {name = "Campo estático", description = "[q]Invoca un campo de energía chisporroteante donde se encuentre un objetivo aleatorio. Este campo inflige 9425 to 10575 p. de daño Arcano por segundo a todos los enemigos en un radio de 30 m de ese lugar."},
[57432] = {name = "Pulso Arcano", description = "[q]Ataca con un pulso de energía Arcana instantáneo que inflige 28275 to 31725 p. de daño Arcano a los enemigos en un radio de 30 m."},
[57454] = {name = "Púa de hielo", description = "[q]Una púa de hielo inflige 1295 to 1505 p. de daño de Escarcha y repele al enemigo."},
[57456] = {name = "Mordisquielo", description = "[q]Inflige 48 to 52 p. de daño de Escarcha cada 3 s durante 6 sec.\n Velocidad de movimiento reducida un 10% durante 6 sec."},
[57459] = {name = "Tormenta Arcana", description = "[q]Dispara una salva de misiles mágicos al aire. Estos misiles seleccionan un objetivo enemigo de forma aleatoria y se lanzan contra él causándole 9425 to 10575 p. de daño Arcano."},
[57468] = {name = "Retozar", description = "[q]Retoza sobre un enemigo y le inflige un 200% de daño normal."},
[57477] = {name = "Aliento helado", description = "[q]Inflige daño de Escarcha a los enemigos que están en un cono frente al taumaturgo, lo que les roba su maná y reduce su velocidad de movimiento durante 10 sec."},
[57480] = {name = "Haz cinético", description = "[q]Inflige 720 to 880 p. de daño de Naturaleza y arrastra al objetivo hacia el taumaturgo."},
[57488] = {name = "Borrasca", description = "[q]Carga contra un enemigo, lo que le inflige el daño normal más 428 to 472 p."},
[57491] = {name = "Tsunami de llamas", description = "[q]Inflige daño de Fuego a los enemigos cercanos y los empapa con lava, lo que los ralentiza e inflige daño de Fuego extra durante 10 sec."},
[57493] = {name = "Ráfaga de velocidad", description = "[q]Aumenta la velocidad de vuelo un 100% durante 8 sec."},
[57524] = {name = "Metanoia", description = "[q]Aumenta el daño crítico recibido e infligido un 50% y aumenta la probabilidad de golpe crítico de taumaturgo un 25% durante 2 min."},
[57546] = {name = "Ahuyentar el mal superior", description = "[q]El objetivo no-muerto o demonio se sentirá impulsado a huir durante un máximo de 20 sec. El daño infligido puede interrumpir el efecto. Solo huirá 1 objetivo cada vez."},
[57547] = {name = "Toque Val'kyr", description = "[q]Inflige 324 to 376 p. de daño de las Sombras cada 1 s durante 5 sec."},
[57557] = {name = "Pirosacudida", description = "[q]Inflige daño de Fuego a un enemigo y aumenta el daño de Fuego recibido durante 1 min."},
[57570] = {name = "Aliento sombrío", description = "[q]Inflige 6938 to 8062 p. de daño de Fuego a los enemigos en un cono frente al taumaturgo."},
[57579] = {name = "Fisura de las Sombras", description = "[q]Inflige 6188 to 8812 p. de daño de las Sombras a un enemigo que se encuentre en la fisura de las Sombras pasados 5 s."},
[57580] = {name = "Infusión de relámpagos", description = "[q]Imbuye al objetivo de relámpagos, lo que provoca una Sobrecarga que inflige 1411 to 1589 p. de daño de Naturaleza en un área alrededor del objetivo tras 5 sec."},
[57581] = {name = "Explosión del Vacío", description = "[q]Inflige 6188 to 8812 p. de daño de las Sombras a cualquier enemigo que se encuentre en la fisura de las Sombras después de 5 s."},
[57589] = {name = "Disparar", description = "[q]Dispara a un enemigo y le inflige daño físico."},
[57592] = {name = "Reparar edificio", description = "[q]Repara los edificios dañados."},
[57599] = {name = "Golpe de peste", description = "[q]Un golpe feroz que inflige un 60% de daño con arma más 27 p. de daño y contagia al objetivo con Peste de sangre, una enfermedad que inflige daño de las Sombras en el tiempo. El golpe elimina un efecto de sanación en el tiempo del objetivo."},
[57601] = {name = "Peste de sangre", description = "[q]Una enfermedad que inflige daño de las Sombras durante 12 sec. Tiene un 50% de probabilidad de eliminar un efecto de sanación en el tiempo cada vez que inflija daño."},
[57609] = {name = "Disparar cañón", description = "[q]Dispara un cañón, inflige hasta 5997 to 6003 p. de daño y 1300 p. de daño de asedio a las estructuras cercanas."},
[57616] = {name = "Púa de cristal", description = "[q]Escupe una púa de cristal al objetivo e inflige 666 to 774 p. de daño de Escarcha."},
[57617] = {name = "Veneno Hielopunta", description = "[q]Dispara veneno al enemigo, lo que inflige daño de Naturaleza."},
[57628] = {name = "Bola de Fuego", description = "[q]Inflige daño de Fuego a un enemigo."},
[57629] = {name = "Nova de Escarcha", description = "[q]Inflige daño de Escarcha a los enemigos cercanos y los inmoviliza durante un máximo de 5 sec."},
[57635] = {name = "Separación", description = "[q]Intentas separarte del objetivo, saltando hacia atrás. Debes estar de frente al objetivo."},
[57640] = {name = "Golpe siniestro", description = "[q]Inflige un 125% de daño con arma a un enemigo."},
[57641] = {name = "Eviscerar", description = "[q]Remate que inflige daño."},
[57643] = {name = "Reflejo de hechizos", description = "[q]Levanta tu escudo y refleja el siguiente hechizo lanzado sobre ti. Dura 5 sec."},
[57644] = {name = "Descarga de las Sombras", description = "[q]Lanza una descarga de magia oscura a un enemigo y le inflige daño de las Sombras."},
[57645] = {name = "Corrupción", description = "[q]Corrompe a los enemigos cercanos, lo que inflige daño de las Sombras cada 3 s durante 24 sec."},
[57647] = {name = "Fuego lunar", description = "[q]Quema al enemigo, inflige daño Arcano y daño extra durante 12 sec."},
[57648] = {name = "Cólera", description = "[q]Lanza una descarga de relámpago a un enemigo y le inflige daño de Naturaleza."},
[57652] = {name = "Ola aplastante", description = "[q]Provocas una Ola aplastante que inflige 4163 to 4837 p. de daño de Naturaleza al entrar en contacto con objetivos hostiles frente a ti en un radio de 20 m y los derriba. Doble de daño a los objetivos congelados."},
[57655] = {name = "Forma felina", description = "[q]Cambio a forma felina aumentando el poder de ataque cuerpo a cuerpo 0 p. más agilidad. Protege al taumaturgo de los efectos de polimorfia y permite el uso de varias facultades felinas.\n\n El cambio de forma libera al taumaturgo de los efectos de polimorfia y de reducción del movimiento."},
[57657] = {name = "Destrozar", description = "[q]Destroza al enemigo y le inflige un 100% de daño normal más 25 p. y provoca que el objetivo reciba un 30% de daño extra de Magullar, Triturar y los efectos de sangrado durante 12 sec. Otorga 1 p. de combo."},
[57661] = {name = "Destripar", description = "[q]Remate que inflige daño en el tiempo."},
[57665] = {name = "Descarga de Escarcha", description = "[q]Inflige daño de Escarcha a un enemigo y reduce su velocidad de movimiento durante 4 sec."},
[57668] = {name = "Nova de Escarcha", description = "[q]Inflige daño de Escarcha a los enemigos cercanos y los inmoviliza durante un máximo de 8 sec."},
[57676] = {name = "Portal: Shattrath", description = "[q]Crea un portal que teletransporta a los miembros del grupo que lo utilicen hasta Shattrath."},
[57688] = {name = "Inquina enfocada", description = "[q]Enfurece al taumaturgo cuando le enfocan los hechizos y ataques, aumenta el daño y la velocidad de ataque."},
[57723] = {name = "Agotamiento", description = "[q]No puede beneficiarse de Heroísmo u otro efecto similar."},
[57724] = {name = "Saciado", description = "[q]No puede beneficiarse de Ansia de sangre u otro efecto similar."},
[57725] = {name = "Descarga de las Sombras", description = "[q]Lanza una descarga de magia oscura a un enemigo y le inflige daño de las Sombras."},
[57728] = {name = "Ola de choque", description = "[q]Envía una ola de fuerza frente al taumaturgo que inflige 4050 to 4950 p. de daño y aturde a todos los objetivos enemigos en un radio de 15 m en un cono frontal durante 2 sec."},
[57733] = {name = "Ira dracónica", description = "[q]Aumenta la velocidad de ataque cuerpo a cuerpo, a distancia y de lanzamiento de hechizos de todos los miembros del grupo un 35%. Dura 30 sec."},
[57740] = {name = "Aura de devoción", description = "[q]Da armadura extra a los miembros del grupo cercanos durante 10 min."},
[57741] = {name = "Ola de choque", description = "[q]Envía una ola de fuerza frente al gólem que inflige 3150 to 3850 p. de daño y aturde a todos los objetivos enemigos en un radio de 10 m en un cono frente al taumaturgo durante 2 sec."},
[57742] = {name = "Furia vengadora", description = "[q]Golpea a las unidades amistosas cercanas con furia y les otorga inmunidad a los efectos de control de masas."},
[57753] = {name = "Crear Orbe de llamas", description = "[q]Crea un orbe de fuego puro que flota por el aire hacia el objetivo mientras lanza rayos abrasadores a los enemigos que se encuentren en un radio de 18 m."},
[57757] = {name = "Lluvia de Fuego", description = "[q]Invoca una lluvia de arrabio que quema a todos los enemigos del área seleccionada infligiendo 2775 to 3225 p. de daño de Fuego cada 2 s durante 6 sec."},
[57759] = {name = "Soltar martillo", description = "[q]Golpea a un enemigo en la cabeza con un martillo, reduce su tamaño, sus estadísticas y el daño físico que inflige un 5%. Se acumula."},
[57762] = {name = "Flor de vida", description = "[q]Sana 142 to 158 p. cada segundo y 463 to 537 p. cuando el efecto se termina o se disipa."},
[57765] = {name = "Nutrir", description = "[q]Sana a un objetivo amistoso 694 to 806 p. Sana un 20% extra si tienes los efectos Rejuvenecimiento, Recrecimiento o Flor de vida activos sobre el objetivo."},
[57767] = {name = "Limpiar", description = "[q]Purga a un objetivo amistoso, lo que elimina 1 efecto de envenenamiento, 1 efecto de enfermedad y 1 efecto de magia."},
[57769] = {name = "Sello de orden", description = "[q]Le da al paladín la oportunidad de infligir 70 p. de daño Sagrado extra. El paladín solo puede tener un sello activo a la vez. Dura 2 min."},
[57774] = {name = "Sentencia de Luz", description = "[q]Desata la energía de un hechizo de sello para sentenciar a un enemigo durante until cancelled, otorgando a los ataques cuerpo a cuerpo realizados contra el enemigo sentenciado una probabilidad de sanar al atacante."},
[57775] = {name = "Sanación superior", description = "[q]Invoca la magia Sagrada para sanar a un aliado."},
[57777] = {name = "Renovar", description = "[q]Sana al objetivo 2360 to 2640 p. de daño durante 15 sec."},
[57778] = {name = "Palabra de las Sombras: dolor", description = "[q]Pronuncia una palabra de oscuridad que inflige daño de las Sombras a un enemigo cada 3 s durante 18 sec."},
[57779] = {name = "Tortura mental", description = "[q]Inflige daño de las Sombras a un enemigo y reduce su velocidad de movimiento durante 3 sec."},
[57780] = {name = "Descarga de relámpagos", description = "[q]Golpea a un objetivo con relámpagos, lo que le inflige daño de Naturaleza."},
[57781] = {name = "Descarga de relámpagos", description = "[q]Lanza una descarga de relámpagos sobre el objetivo que le inflige 234 to 266 p. de daño de Naturaleza."},
[57783] = {name = "Choque de tierra", description = "[q]Golpea a un enemigo con fuerza de conmoción, lo que inflige daño de Naturaleza e interrumpe el hechizo que se esté lanzando durante 2 sec."},
[57784] = {name = "Tormenta de truenos", description = "[q]Invoca una descarga de relámpagos que inflige daño a los enemigos cercanos en un radio de 15 m. Inflige 185 to 215 p. de daño de Naturaleza a todos los enemigos cercanos y los derriba."},
[57785] = {name = "Ola de sanación", description = "[q]Invoca la magia de Naturaleza para sanar a un aliado."},
[57789] = {name = "Golpe mortal", description = "[q]Inflige un 100% de daño con arma y deja al objetivo herido, lo que reduce un 50% la eficacia de cualquier sanación durante 5 sec."},
[57790] = {name = "Sed de sangre", description = "[q]Ataca instantáneamente al objetivo, lo que le inflige (Attack power * 142 / 100) p. de daño. Además, los siguientes 3 ataques cuerpo a cuerpo que alcancen al objetivo restaurarán el 1,5% de salud máxima. Este efecto dura 8 sec. El daño se basa en tu poder de ataque."},
[57791] = {name = "Sed de sangre", description = "[q]Ataca instantáneamente al objetivo, lo que le inflige 30 p. de daño. Además, los siguientes 3 ataques cuerpo a cuerpo que tengan éxito restaurarán un 1,5% de salud máxima. Este efecto dura 8 sec."},
[57794] = {name = "Salto heroico", description = "[q]Salta en el aire y golpea a todos los enemigos en un radio de 8 m del área objetivo, lo que inflige un 50% de daño con arma y los aturde durante 2 sec."},
[57795] = {name = "Devastar", description = "[q]Hiende la armadura del objetivo, lo que provoca el efecto de Hender armadura. Además inflige un 20% de daño con arma más 50 p. por cada aplicación de Hender armadura sobre el objetivo. El efecto de Hender armadura se puede acumular hasta 5 veces."},
[57798] = {name = "Consagración", description = "[q]Consagra el suelo que el paladín tiene bajo sus pies, lo que inflige 240 p. de daño Sagrado durante 8 sec a los enemigos que lo pisen."},
[57799] = {name = "Escudo de vengador", description = "[q]Lanza un escudo Sagrado al enemigo, lo que inflige 180 to 220 p. de daño Sagrado y lo deja atontado. A continuación salta a los enemigos cercanos. Afecta a 2 objetivos en total."},
[57801] = {name = "Aliento de llamas", description = "[q]Inflige 3063 to 3937 p. de daño de Fuego a los enemigos en un cono frente al taumaturgo."},
[57802] = {name = "Escudo de tierra", description = "[q]Protege al objetivo con un escudo de tierra que hace que los ataques cuerpo a cuerpo sanen al objetivo protegido. Este efecto solo ocurre una vez cada pocos segundos. 6 cargas. Dura 2 min."},
[57807] = {name = "Hender armadura", description = "[q]Hiende la armadura de un enemigo y la reduce cada vez más por cada Hender armadura. Se puede aplicar hasta 5 veces. Dura 20 sec."},
[57815] = {name = "Chirrido penetrante", description = "[q]Inflige 24 to 29 p. de daño de las Sombras a los enemigos cercanos."},
[57825] = {name = "Descarga de Escarcha", description = "[q]Inflige daño de Escarcha a un enemigo y reduce su velocidad de movimiento durante 4 sec."},
[57832] = {name = "Atronar", description = "[q]Golpea a los enemigos cercanos, lo que aumenta el tiempo entre sus ataques un 20% durante 30 sec y les inflige 200 p. de daño. Daño aumentado por poder de ataque. Genera amenaza adicional y puede afectar a un máximo de 4 objetivos."},
[57833] = {name = "Flema de Escarcha", description = "[q]Inflige 264 to 306 p. de daño de Escarcha cada 1 s."},
[57835] = {name = "Ofrenda del crepúsculo", description = "[q]Aumenta el daño de las Sombras de los objetivos un 50% y reduce el daño recibido un 100%."},
[57843] = {name = "Resguardo de Fuego potenciado con mojo", description = "[q]Rodea al taumaturgo con un escudo que dura 1.50 min. Aumenta el daño infligido un 30% y puede absorber una cantidad fija de daño de Fuego."},
[57846] = {name = "Golpe heroico", description = "[q]Un ataque contundente que aumenta el daño cuerpo a cuerpo un 10%."},
[57861] = {name = "Rugido de Rhunok", description = "[q]Ruge y aumenta el daño recibido un 20% durante 30 sec."},
[57882] = {name = "Saltar", description = "[q]Salta al lugar indicado que se encuentre a una distancia de entre 5 y 45 metros. Esta acción hará que el acechador ágil abandone el combate."},
[57912] = {name = "Relámpagos Arcanos", description = "[q]Los sistemas de defensa de El Bastión Violeta infligen 13000 p. de daño Arcano a las formas de vida no autorizadas."},
[57941] = {name = "Tortura mental", description = "[q]Inflige daño de las Sombras a un enemigo y reduce su velocidad de movimiento durante 3 sec."},
[57942] = {name = "Salva de descarga de las Sombras", description = "[q]Lanza misiles de magia oscura a los enemigos cercanos y les inflige daño de las Sombras."},
[57949] = {name = "Temblar", description = "[q]Inflige 1414 to 1586 p. de daño de las Sombras al recibir daño. Salta a un miembro de la banda cercano."},
[57959] = {name = "Anomalías del plano", description = nil},
[57976] = {name = "Explosión de plano", description = "[q]Lanza una ola explosiva de magia e inflige daño Arcano a los enemigos cercanos."},
[57984] = {name = "Explosión de Fuego", description = "[q]Inflige daño de Fuego a un enemigo."},
[58025] = {name = "Sacudida de Escarcha", description = "[q]Aumenta el daño de Escarcha que recibe el taumaturgo durante 5 sec."},
[58026] = {name = "Bendición de la Cruzada", description = nil},
[58040] = {name = "Destruir sello de la puerta", description = "[q]Debilita el sello de la puerta de la prisión."},
