[58127] = {name = "Tormenta divina", description = "[q]Un ataque con arma instantáneo que inflige daño Sagrado a un máximo de 4 enemigos en un radio de 8 m."},
[58130] = {name = "Entereza ligada al hielo", description = "[q]El caballero de la Muerte congela su sangre para hacerse inmune a los efectos de aturdir y reduce todo el daño recibido un 50% durante 12 sec. No elimina los efectos ya existentes de aturdir."},
