[58153] = {name = "Luz profana", description = "[q]Sana a un objetivo amistoso 4936 to 5493 p."},
[58154] = {name = "Martillo de injusticia", description = "[q]Aturde al objetivo durante 6 sec."},
[58187] = {name = "Poder transferido", description = "[q]Aumenta la velocidad de ataque de un aliado un 1% durante 1 sec."},
[58203] = {name = "Cadena de hierro", description = "[q]Ata una cadena a una bomba de la Plaga para poder arrastrarla cerca de una atrocidad torpe. No será posible realizar otras acciones mientras la cadena esté atada."},
