[58283] = {name = "Lanzar roca", description = "[q]Lanza un roca a un enemigo, infligiendo 405 to 495 p. de daño y desorientando al objetivo durante 2 sec. Es poco probable que los enemigos se den cuenta de quién ha lanzado la roca."},
[58291] = {name = "Disrupción con escudo", description = "[q]Debilita la magia que mantiene al prisionero a raya."},
[58438] = {name = "Ignición", description = "[q]Prende fuego con malicia a un ent pacífico."},
[58455] = {name = "Deflagración Arcana", description = "[q]Lanza una ola explosiva de magia e inflige 648 to 752 p. de daño Arcano a los enemigos cercanos."},
[58456] = {name = "Tromba Arcana", description = "[q]Lanza contra el objetivo enemigo varios misiles que le infligen 720 to 880 p. de daño Arcano."},
[58457] = {name = "Descarga de Escarcha", description = "[q]Inflige 723 to 977 p. de daño de Escarcha a un enemigo y reduce su velocidad de movimiento durante 4 sec."},
[58459] = {name = "Empalar", description = "[q]Inflige 250 p. de daño físico a un enemigo cada 3 s durante 12 sec."},
[58460] = {name = "Golpe brutal", description = "[q]Inflige un 150% de daño con arma."},
[58461] = {name = "Hender armadura", description = "[q]Corta la armadura de un enemigo y la reduce un 10% por cada uso de Hender armadura. Puede aplicarse hasta 5 veces. Dura 20 sec."},
[58462] = {name = "Explosión Arcana", description = "[q]Golpea al objetivo con energía, inflige 833 to 967 p. de daño Arcano. Cada vez que lances el hechizo Explosión Arcana, se reducirá el tiempo de lanzamiento. El efecto se acumula hasta 5 veces y dura 8 sec."},
[58463] = {name = "Cono de frío", description = "[q]Inflige 50 p. de daño de Escarcha a los enemigos en un cono frente al taumaturgo, reduciendo su velocidad de movimiento durante 6 sec."},
[58464] = {name = "Cadenas de hielo", description = "[q]Forma cadenas glaciales alrededor del enemigo que bloquean el objetivo durante 4 sec."},
[58469] = {name = "Potenciación Arcana", description = "[q]Aumenta un 50% las velocidades de ataque cuerpo a cuerpo, ataque a distancia, movimiento y lanzamiento durante 10 sec."},
[58470] = {name = "Traslación táctica", description = "[q]Teletransporta al taumaturgo detrás del objetivo y lo libera de sus ataduras."},
[58471] = {name = "Puñalada", description = "[q]Inflige un 175% de daño con arma a un enemigo, pero solo si atacas desde detrás."},
[58504] = {name = "Golpe de agonía", description = "[q]Inflige un 100% de daño con arma y deja dolorido al objetivo, aumentando un 20% el daño infligido al objetivo durante 5 sec."},
[58506] = {name = "Sigilo", description = "[q]Pone al taumaturgo en modo sigilo. Dura hasta que se cancela."},
[58508] = {name = "Flagelo lateral", description = "[q]Inflige un 60% de daño con arma a un objetivo cercano."},
[58510] = {name = "Obstáculo mágico", description = "[q]Reduce un 50% la velocidad de lanzamiento del enemigo durante 6 sec."},
[58511] = {name = "Aroma a manzana podrida", description = "[q]Hueles bastante mal."},
[58514] = {name = "Aroma a plátano podrido", description = "[q]El olor es una mezcla entre pie de necrófago y trasero de cerdo."},
[58516] = {name = "Abrumar", description = "[q]Abruma instantáneamente a un enemigo y le inflige un 75% de daño con arma. Solo puede utilizarse después de que el objetivo esquive. Abrumar no se puede bloquear, ni esquivar ni parar."},
[58517] = {name = "Lesión grave", description = "[q]Un despiadado ataque que inflige un 100% de daño con arma y hace que el objetivo sangre abundantemente hasta que se cure del todo. Inflige 694 to 806 p. de daño cada 3 s hasta que se cura."},
[58519] = {name = "Flema", description = "[q]Te han escupido. Nadie te quiere."},
[58526] = {name = "Ataduras azures", description = "[q]Ata a un enemigo con zarcillos de magia, evitando que pueda atacar o lanzar hechizos durante 4 sec."},
[58531] = {name = "Misiles Arcanos", description = "[q]Lanza misiles mágicos contra un enemigo y le inflige daño Arcano."},
[58532] = {name = "Salva de descarga de Escarcha", description = "[q]Inflige 1800 to 2200 p. de daño de Escarcha a los enemigos cercanos, lo que reduce su velocidad de movimiento durante 4 sec."},
[58534] = {name = "Congelación profunda", description = "[q]Inflige 1018 to 1182 p. de daño de Escarcha y aturde al objetivo durante 4 sec."},
[58535] = {name = "Descarga de Escarcha", description = "[q]Inflige daño de Escarcha a un enemigo y reduce su velocidad de movimiento durante 4 sec."},
[58536] = {name = "Salva Arcana", description = "[q]Lanza una descarga mágica contra los enemigos cercanos, infligiendo 2035 to 2365 p. de daño Arcano."},
[58537] = {name = "Polimorfia", description = "[q]Transforma al enemigo en oveja obligándole a deambular durante 5 sec. Mientras deambula, la oveja no puede atacar ni lanzar hechizos. Si recibe daño volverá a su forma normal. Solo se puede aplicar Polimorfia a un objetivo al mismo tiempo. Solo funciona con bestias, dragones, gigantes, humanoides y alimañas."},
[58540] = {name = "Prisión de Eidolon", description = nil},
[58541] = {name = "Salto de fase", description = "[q]Cambia de fase 30 m a la sombra a menos que haya algo en medio. También libera a la sombra de cualquier atadura."},
[58542] = {name = "Empollar huevos", description = "[q]Empollar huevos crepusculares..."},
[58543] = {name = "Desfase", description = "[q]Dejas temporalmente el mundo normal, saliendo del combate y aumentando la velocidad de movimiento durante 10 sec."},
[58544] = {name = "Succión de sangre", description = "[q]Drena salud a un enemigo durante 6 sec y se la transfiere a la sombra. Este efecto es extremadamente débil, ya que la sombra no está acostumbrada al combate directo."},
[58548] = {name = "Etéreo", description = "[q]Resulta extremadamente difícil que los enemigos lo detecten."},
[58567] = {name = "Hender armadura", description = "[q]Hiende la armadura del objetivo, lo que la reduce un 4% por cada uso de Hender armadura y genera una gran cantidad de amenaza. Amenaza aumentada con el poder de ataque. Se puede usar un máximo de 3 veces. Dura 30 sec."},
[58579] = {name = "Llamarada ardiente", description = "[q]La zona de fuego inflige 150 p. de daño de Fuego cada 1 s durante 30 sec."},
