[58619] = {name = "Cargar", description = "[q]Carga contra un enemigo, inflige el daño normal más 120 p. y derriba al enemigo."},
[58624] = {name = "Rejuvenecimiento corrupto", description = "[q]Coloca energía Ley corrupta sobre un enemigo, infligiendo 360 to 440 p. de daño Arcano cada 3 segundos durante 12 sec."},
