[58678] = {name = "Fragmentos de roca", description = "[q]Desata una lluvia de rocas dentadas que golpean al objetivo enemigo cada 0.1 s durante 3 sec e infligen daño a cualquier enemigo extra que esté en un radio de 5 del objetivo."},
[58683] = {name = "Combate salvaje", description = nil},
[58688] = {name = "Energía incontrolable", description = "[q]Libera un torrente de energía Arcana que inflige 3800 to 4200 p. de daño Arcano a los enemigos que se encuentren delante del taumaturgo. Los objetivos enemigos también reciben 1000 p. de daño Arcano cada 3 s durante 15 sec."},
[58690] = {name = "Barrido de cola", description = "[q]Inflige 1575 to 1925 p. de daño a los enemigos en un cono detrás del taumaturgo, interrumpiendo el lanzamiento de hechizos durante 2 sec."},
[58693] = {name = "Ventisca", description = "[q]Invoca una ventisca que dura 8 sec, lo que inflige 1500 p. de daño de Escarcha cada 2 s y reduce la velocidad de movimiento de todos los enemigos de un área seleccionada un 40%."},
