[58743] = {name = "Interceptar", description = "[q]Carga contra un enemigo, le inflige 65 p. de daño y le aturde durante 3 sec."},
[58747] = {name = "Interceptar", description = "[q]Carga contra un enemigo, infligiendo 65 p. de daño y lo aturde durante 3 sec."},
[58769] = {name = "Interceptar", description = "[q]Carga contra un enemigo, le inflige 65 p. de daño y le aturde durante 3 sec."},
[58770] = {name = "Drenar maná", description = "[q]Drena hasta 5000 p. de maná de un enemigo durante 5 sec, y se lo transfiere al taumaturgo."},
[58839] = {name = "Golpe de peste", description = "[q]Un golpe feroz que inflige un 60% de daño con arma más 9 p. de daño y contagia al objetivo con Peste de sangre, una enfermedad que inflige daño de las Sombras en el tiempo. El golpe elimina un efecto de sanación en el tiempo del objetivo."},
[58840] = {name = "Peste de sangre", description = "[q]Una enfermedad que inflige daño de las Sombras durante 12 sec. Tiene un 50% de probabilidad de eliminar un efecto de sanación en el tiempo cada vez que inflija daño."},
