[58843] = {name = "Golpe de peste", description = "[q]Un golpe feroz que inflige un 60% de daño con arma más 9 p. de daño y contagia al objetivo con Peste de sangre, una enfermedad que inflige daño de las Sombras en el tiempo. El golpe elimina un efecto de sanación en el tiempo del objetivo."},
[58844] = {name = "Peste de sangre", description = "[q]Una enfermedad que inflige daño de las Sombras durante 12 sec. Tiene un 50% de probabilidad de eliminar un efecto de sanación en el tiempo cada vez que inflija daño."},
