[58905] = {name = "Embate agresivo", description = nil},
[58912] = {name = "Tormenta de muerte", description = "[q]Golpea a un enemigo con una descarga de muerte que pasa a otro enemigo cercano. Este hechizo puede afectar hasta a 12 objetivos, causándoles daño de las Sombras a todos."},
