[59133] = {name = "Golpe de peste", description = "[q]Un golpe feroz que inflige daño con arma más 37 p. modificado por el poder de ataque y apesta al objetivo, lo que inflige 292 p. de daño de las Sombras durante 12 sec."},
[59134] = {name = "Espiral de la muerte", description = "[q]Dispara una oleada de energía profana, que inflige 184 p. de daño de las Sombras modificado por el poder de ataque a un enemigo o sana 276 p. de daño a un objetivo no-muerto amistoso."},
