[62437] = {name = "Tremor terrenal", description = "[q]Inflige 5250 to 6750 p. de daño físico a los enemigos que se encuentren en un radio de 50000 m e interrumpe todos lanzamientos de hechizos durante 8 sec."},
[62441] = {name = "Renovar", description = "[q]Sana a un objetivo amistoso 13650 to 14350 p. cada 3 s durante 15 sec."},
[62442] = {name = "Sanación superior", description = "[q]Invoca la magia Sagrada para sanar a un aliado."},
[62443] = {name = "Punición Sagrada", description = "[q]Pune al enemigo y le inflige 7650 to 9350 p. de daño Sagrado."},
[62444] = {name = "Golpe heroico", description = "[q]Un ataque contundente que inflige 3938 to 5062 p. de daño físico extra."},
[62451] = {name = "Energía inestable", description = "[q]Inflige 4875 to 5125 p. de daño de Naturaleza cada 1 s a los enemigos en un radio de 4 m durante 10 sec."},
[62467] = {name = "Vacío de poder", description = "[q]El Ancestro está exhausto después de prestar sus poderes a Freya."},
[62469] = {name = "Congelar", description = "[q]Inflige daño de Escarcha a los enemigos cercanos y los inmoviliza durante un máximo de 10 sec."},
[62471] = {name = "Velocidad aumentada", description = "[q]Inyecta pirita líquida en el enemigo y aumenta la velocidad de movimiento un 100%. Dura 20 sec."},
[62475] = {name = "Desconexión de sistemas", description = "[q]Sobrecarga del sistema, daño recibido aumentado un 50%."},
[62478] = {name = "Golpes helados", description = "[q]Reduce el daño físico un 70% pero los ataques infligen 31062 p. de daño de Escarcha. Además, todos los enemigos reciben 4000 p. de daño de Escarcha cada 2. Dura 20 sec."},
[62488] = {name = "Activar ensamblaje", description = "[q]Lanza una descarga de magma a un ensamblaje férreo desactivado aleatorio, lo activa y hace que defienda al taumaturgo."},
[62490] = {name = "Lanzar barrica de pirita", description = "[q]Lanza una orbe de pirita azul lejos e inflige daño pero consume munición."},
[62494] = {name = "Pirita líquida", description = "[q]Contiene pirita líquida.\n Puede obtenerse de mecánicos del demoledor."},
[62507] = {name = "Toque de dominio", description = "[q]Subyuga al objetivo a la voluntad de Yogg-Saron, lo debilita pero le confiere un control sutil."},
[62519] = {name = "Acorde con la Naturaleza", description = "[q]Aumenta la sanación recibida un 8%."},
[62522] = {name = "Electrochoque", description = "[q]Electrocuta a todos los objetivos que se encuentren en un radio de 25 m en un cono frente al taumaturgo. También interrumpe el lanzamiento de hechizos e impide que se lance cualquier hechizo de esa misma escuela durante 4 sec."},
[62526] = {name = "Detonación de runa", description = "[q]Inmoviliza a un objetivo con un resguardo rúnico que detona tras 4 sec para infligir 11250 to 13750 p. de daño de Fuego alrededor del objetivo."},
[62528] = {name = "Toque de Eonar", description = "[q]La presencia de la Protectora sana a Freya 6000 p. de salud cada 1 s."},
[62532] = {name = "Apretón de conservador", description = "[q]El apretón del Protector evita los ataques y el lanzamiento de hechizos."},
[62541] = {name = "Feromonas pujante", description = "[q]Las feromonas pujantes protegen contra la Empuñadura de conservador.\n Aumenta el daño infligido un 0%."},
[62546] = {name = "Agostar", description = "[q]Dispara un caño de llamas y quema a todos los enemigos frente al taumaturgo que se encuentren a 30 m, inflige 2357 to 2643 p. de daño de Fuego cada medio segundo durante 3 sec. Además, este efecto agosta el terreno y lo vuelve fuego, también inflige 1885 to 2115 p. de daño de Fuego cada segundo a todos los enemigos que se encuentren a 13 m. Los ensamblajes férreos que estén en este radio empezarán a calentarse y finalmente se volverán magma."},
[62548] = {name = "Agostar", description = "[q]Dispara un caño de llamas y quema a todos los enemigos frente al taumaturgo que se encuentren a 30 m, inflige 2357 to 2643 p. de daño de Fuego cada medio segundo durante 3 sec. Además, este efecto agosta el terreno y lo vuelve fuego, también inflige 1885 to 2115 p. de daño de Fuego cada segundo a todos los enemigos que se encuentren a 13 m. Los ensamblajes férreos que estén en este radio empezarán a calentarse y finalmente se volverán magma."},
[62563] = {name = "Cargar", description = "[q]Un ataque de carga que inflige 45000 p. de daño y elimina 1 capa de Defender del oponente."},
[62575] = {name = "Romper escudo", description = "[q]Un ataque a distancia que inflige 2000 p. de daño y elimina una capa de Defender del oponente."},
[62580] = {name = "Salva de descarga de Escarcha", description = "[q]Inflige daño de Escarcha a los enemigos cercanos, lo que reduce su velocidad de movimiento durante 4 sec."},
[62581] = {name = "¡Pupas!", description = "[q]Tus aventuras besarranas han hecho que te salgan pupas."},
[62583] = {name = "Descarga de Escarcha", description = "[q]Inflige daño de Escarcha a un enemigo y reduce su velocidad de movimiento durante 4 sec."},
[62584] = {name = "Don de la Protectora", description = "[q]El Don de Eonar sana a Freya y a todos sus esbirros un 30% de su salud."},
[62589] = {name = "Furia de la naturaleza", description = "[q]Infecta a un enemigo durante 10 sec, lo que hace que inflija daño de Naturaleza a sus aliados cercanos cada 2 s."},
[62597] = {name = "Nova de Escarcha", description = "[q]Inflige 7069 to 7931 p. de daño de Escarcha a los enemigos cercanos y los inmoviliza durante un máximo de 6 sec."},
[62598] = {name = "Detonar", description = "[q]Detonar inflige 4163 to 4837 p. de daño de Fuego a los enemigos en un radio de 15 m."},
[62608] = {name = "Latigazo de llamas", description = "[q]El azotador inflige daño de Fuego igual al 150% de su daño cuerpo a cuerpo."},
[62613] = {name = "Cargar", description = "[q]Carga contra un enemigo, inflige 4625 to 5375 p. de daño físico y derriba al enemigo."},
[62619] = {name = "Feromonas", description = "[q]Las feromonas pujantes protegen contra la Empuñadura de protector."},
[62623] = {name = "Rayo de sol", description = "[q]Inflige 5088 to 5912 p. de daño de Naturaleza a los enemigos en un radio de 8 m del objetivo."},
[62625] = {name = "Embate en el suelo", description = "[q]Inflige 65801 to 74199 p. de daño de Naturaleza a los enemigos en un cono frente al taumaturgo."},
[62632] = {name = "Ciclón", description = "[q]Ataca a los enemigos cercanos con un torbellino de acero que dura 15 sec e inflige el daño normal más 110 p."},
[62634] = {name = "Mortero", description = "[q]Dispara un explosivo en miniatura, que inflige daño de Fuego a todos los enemigos en el área objetivo."},
[62649] = {name = "Descarga tormentosa", description = "[q]Inflige 6013 to 6987 p. de daño a un enemigo."},
[62650] = {name = "Entereza de Escarcha", description = "[q]Aumenta el daño infligido un 10% y reduce el daño recibido de cualquier fuente un 20%."},
[62653] = {name = "Maremoto", description = "[q]Carga hacia delante 40 m a los enemigos en una fila."},
[62660] = {name = "Fragor de sombra", description = "[q]Lanza un misil hacia un objetivo aleatorio. Cuando el misil toca tierra, inflige 11310 to 12690 p. de daño de las Sombras a todos los enemigos en un radio de 10 m de ese emplazamiento."},
[62661] = {name = "Llamas abrasadoras", description = "[q]Inflige 13875 to 16125 p. de daño de Fuego a todos los enemigos que se encuentren en un radio de 100 m, sobrecalentando sus armaduras y reduciendo sus facultades para protegerlos un 75% durante 10 sec."},
[62662] = {name = "Oleada de oscuridad", description = "[q]Potencia al taumaturgo con poder oscuro y aumenta el daño físico un 100% pero reduce la velocidad de movimiento un 55% durante 10 sec."},
[62663] = {name = "Corteza endurecida", description = "[q]Los ataques infligidos al azotador activan el crecimiento de la corteza protectora, aumentan el daño y reducen la velocidad de movimiento. Se acumula."},
[62666] = {name = "Sacudida de alas", description = "[q]Derriba a los enemigos en un radio alrededor del taumaturgo."},
[62670] = {name = "Temple de naturaleza", description = "[q]Aumenta todo el daño infligido un 10% y la sanación recibida de todas las fuentes un 20%."},
[62671] = {name = "Velocidad de invención", description = "[q]Aumenta todo el daño infligido un 10% y la velocidad de movimiento un 20%."},
[62678] = {name = "Invocar aliados de la naturaleza", description = "[q]Invoca aliados de Freya para ayudar en la batalla a proteger Ulduar."},
[62680] = {name = "Caños de llamas", description = "[q]Pisotea el suelo, provoca que géiseres de llamas entren en erupción bajo todos los enemigos. Estos géiseres infligen 5655 to 6345 p. de daño de Fuego, lanzan a los objetivos por los aires e interrumpen cualquier hechizo que se esté lanzando durante 8 sec. Además, los objetivos reciben 1000 p. de daño de Fuego por segundo durante 8 sec."},
[62692] = {name = "Aura de desesperación", description = "[q]Evita la regeneración de maná de todas las fuentes naturales cercanas. Aspecto de víbora, Sentencias del sabio, Ira del chamán y Armonización espiritual se mantienen parcialmente efectivos. También redice la velocidad de los ataques cuerpo a cuerpo un 20%."},
[62702] = {name = "Furia de la tormenta", description = "[q]Aumenta todo el daño infligido un 10% y la salud total un 20%."},
[62709] = {name = "¡Contraataque!", description = "[q]Un contraataque que inflige 35000 p. de daño y elimina una capa de Defender del objetivo."},
[62710] = {name = "Strong Guy Sundering Thrust Aura", description = nil},
[62756] = {name = "Stag Fire Wall", description = nil},
[62775] = {name = "Rabia timpánica", description = "[q]Inflige daño a todos los enemigos que se encuentren en un radio de 500 m y reduce la velocidad de movimiento un 10% durante 4 sec."},
[62793] = {name = "Cólera", description = "[q]Golpea a un objetivo con relámpagos, lo que le inflige daño de Naturaleza."},
[62796] = {name = "Bola de Fuego", description = "[q]Inflige daño de Fuego a un enemigo."},
[62804] = {name = "Rejuvenecimiento", description = "[q]Sana a un aliado cada 3 s durante 12 sec."},
[62809] = {name = "Gran sanación", description = "[q]Invoca la magia Sagrada para sanar a todos los aliados cercanos."},
[62818] = {name = "Disparar", description = "[q]Dispara a un asolador o a un invasor y les inflige daño físico."},
[62821] = {name = "Fuego calentito", description = "[q]Calienta a las criaturas amistosas cercanas, lo que evita los efectos de Congelar y Frío cortante y aumenta el espíritu 4 p. Además, tus hechizos y ataques a distancia tienen una probabilidad de chamuscar a los enemigos. Fuego calentito se puede apagar con hielo que caiga o Congelación apresurada."},
[62822] = {name = "Beber poción de sanación", description = "[q]Sana al centinela con un elixir Sagrado."},
[62823] = {name = "Crear Fuego calentito", description = "[q]Crea un fuego calentito que calienta a las unidades amistosas cercanas. Fuego calentito se puede apagar con hielo que caiga o Congelación apresurada."},
[62831] = {name = "Recargar robot", description = "[q]Recarga un robot de la pila de chatarra y lo devuelve a la vida."},
[62834] = {name = "Bum", description = "[q]Explota al morir e inflige daño de Fuego a todas las unidades cercanas."},
[62845] = {name = "Seccionar", description = "[q]Inflige un 20% de daño con arma a un enemigo, reduciendo su velocidad de movimiento un 60% durante 10 sec."},
[62856] = {name = "Detectar invisibilidad", description = "[q]Permite a Orux Tres Veces Maldito detectar la invisibilidad inferior."},
[62863] = {name = "Duelo", description = "[q]¡Desafía a otro combatiente a un duelo montado!"},
[62906] = {name = "Resguardo de Freya", description = "[q]Inflige 13500 to 16500 p. de daño de Naturaleza a todos los enemigos e invoca a los guardianes de la Naturaleza alrededor del enemigo."},
[62909] = {name = "Infierno de Mimiron", description = "[q]Inflige 13500 to 16500 p. de daño de Fuego a todos los enemigos cercanos y deja una llamarada en el terreno."},
[62911] = {name = "Martillo de Thorim", description = "[q]Inflige 13500 to 16500 p. de daño con Golpe de tormenta a todos los enemigos cercanos."},
[62939] = {name = "Latigazo de relámpagos", description = "[q]Golpea al enemigo con una Descarga de relámpagos que se extiende a otro enemigo cercano. Afecta a un máximo de 5 objetivos e inflige mayor daño de Naturaleza a cada objetivo sucesivo."},
[62942] = {name = "Fortificación rúnica", description = "[q]Fortifica a los aliados cercanos."},
[62944] = {name = "Pisotón de kodo", description = nil},
[62960] = {name = "Cargar", description = "[q]Un ataque de carga que inflige 8500 p. de daño y elimina 1 capa de Defender del oponente."},
[62974] = {name = "Bocina sónica", description = "[q]Envía una ola de fuerza frente a la motocicleta, inflige 6300 to 7700 p. de daño a todos los objetivos enemigos en un radio de 35 m en un cono frontal."},
[62975] = {name = "Descarga de las Sombras", description = "[q]Lanza una descarga de magia oscura a un enemigo y le inflige daño de las Sombras."},
[62997] = {name = "Explosión de plasma", description = "[q]El cañón supura una corriente de plasma e inflige 17000 p. de daño Piroarcano por segundo durante 6 sec. Este efecto se puede resistir."},
[63003] = {name = "Cargar", description = "[q]Un ataque de carga que inflige 17000 p. de daño y elimina 1 capa de Defender del oponente."},
[63005] = {name = "Contendiente", description = nil},
[63007] = {name = "Feromonas de guardián", description = "[q]Emitiendo Feromonas de agregación en un radio alrededor del taumaturgo."},
[63009] = {name = "Deflagración", description = "[q]Inflige 12000 p. de daño de Fuego a todos los enemigos que se encuentren en un radio de 3 m."},
[63010] = {name = "Cargar", description = "[q]Un ataque de carga que inflige 8500 p. de daño y elimina 1 capa de Defender del oponente."},
[63018] = {name = "Luz abrasadora", description = "[q]Carga al objetivo con energía divina, chamuscando a los aliados cercanos durante 9 sec."},
[63024] = {name = "Bomba de gravedad", description = "[q]Carga al objetivo con energía oscura, haciendo que exploten y alcancen a los aliados tras 10 sec."},
[63027] = {name = "Minas de proximidad", description = "[q]Lanza Minas de proximidad en direcciones aleatorias a 15 m. Estas minas se activan si un enemigo se encuentra en un radio de 3 m de ellas e infligen 12000 p. de daño a todos los enemigos que se encuentren a 3 m."},
[63028] = {name = "Forzar lanzamiento", description = nil},
[63032] = {name = "Fuego estelar", description = "[q]Inflige 694 to 806 p. de daño Arcano al objetivo."},
[63038] = {name = "Salva oscura", description = "[q]Lanza misiles de magia oscura, inflige daño de las Sombras y causa una herida a los enemigos cercanos, reduce la efectividad de cualquier sanación un 25% durante 10 sec."},
[63042] = {name = "Dominar mente", description = "[q]Domina la mente del objetivo, colocándolo temporalmente bajo el control de Yogg-Saron. También reduce la Cordura un 4%."},
[63044] = {name = "Desplazar raíces", description = nil},
[63047] = {name = "Latigazo del Guardián", description = "[q]Inflige 11100 to 12900 p. de daño de Naturaleza y 2313 to 2687 p. de daño de Naturaleza extra cada 1 s durante 6 sec."},
[63059] = {name = "Polinizar", description = "[q]Poliniza un azotador guardián, lo sana un 15% y aumenta el daño que inflige un 25% durante 30 sec."},
[63082] = {name = "Ligar vida", description = "[q]Sana 9250 to 10750 p. de salud cada 1 s durante 6 sec."},
[63111] = {name = "Lanza de Escarcha", description = "[q]Lanza de Escarcha inflige 7863 to 9137 p. de daño de Escarcha, ralentiza la velocidad de ataque y de lanzamiento un 100% e inflige 100 p. de daño de Escarcha cada 5 durante until cancelled."},
[63134] = {name = "Bendición de Sara", description = "[q]Sana a un objetivo amistoso 27000 to 33000 p., pero inflige 60000 p. de daño de las Sombras durante 20 sec."},
[63136] = {name = "Abrazo del invierno", description = "[q]Un Escudo congelado que absorbe daño y aumenta el daño físico infligido un 100% durante 1 min."},
[63138] = {name = "Fervor de Sara", description = "[q]Aumenta el daño de un objetivo amistoso un 20% pero también aumenta el daño recibido por el objetivo un 100%. Dura 15 sec."},
[63147] = {name = "Ira de Sara", description = "[q]Inflige 12500 p. de daño de las Sombras cada 3 s pero aumenta el daño físico infligido 12000 p. Dura 12 sec."},
[63148] = {name = "Escudo divino", description = "[q]Protege al paladín de todo el daño y hechizos durante 12 sec pero aumenta el tiempo entre tus ataques un 100%. Una vez protegido, el objetivo no puede volverse invulnerable gracias a Escudo divino, Protección divina ni Mano de protección durante 1 min."},
[63149] = {name = "Tierra violenta", description = "[q]Tierra violenta surge de los pies de los objetivos, los derriba e inflige 9713 to 11287 p. de daño de Naturaleza a cualquiera que esté en un radio de 4 m del objetivo."},
[63169] = {name = "Petrificar articulaciones", description = "[q]Inflige daño de Naturaleza y reduce la velocidad de movimiento un 25% durante 20 sec."},
[63208] = {name = "Gorka Ride", description = nil},
[63226] = {name = "Respiración venenosa", description = "[q]Inflige 7863 to 9137 p. de daño de Naturaleza a enemigos que se encuentran frente al taumaturgo y 3700 to 4300 p. de daño de Naturaleza extra cada 3 s durante 12 sec."},
[63227] = {name = "Enfurecer", description = "[q]Reduce la velocidad de ataque un 100% y aumenta un 50% el daño durante 8 sec."},
[63233] = {name = "Necrocución", description = "[q]Un ataque a distancia que inflige 814 p. de daño y elimina una capa de Defender del oponente."},
[63236] = {name = "Llama devoradora", description = "[q]Lanza una Bomba de lava a un enemigo y le inflige daño de Fuego."},
[63240] = {name = "Espinas de raíz férrea", description = "[q]Hace que salgan espinas del objetivo amistoso que infligen 650 p. de daño de Naturaleza cuando los atacantes golpean. Dura 10 sec."},
[63241] = {name = "Tranquilidad", description = "[q]Regenera a todos los miembros del grupo cercanos 13875 to 16125 p. cada 2 s durante 10 sec. Canalizado."},
[63242] = {name = "Nutrir", description = "[q]Sana a un objetivo amistoso 23125 to 26875 p. Sana un 20% extra si tienes los efectos Rejuvenecimiento, Recrecimiento o Flor de vida activos sobre el objetivo."},
[63247] = {name = "Tsunami vivo", description = "[q]Invocas un tsunami vivo que inflige 6013 to 6987 p. de daño de Naturaleza cuando entra en contacto con objetivos hostiles, inflige 1850 to 2150 p. de daño de Naturaleza extra cada 2 s durante 10 sec."},
[63272] = {name = "Huracán", description = "[q]Crea una violenta tormenta en el área objetivo que inflige 7863 to 9137 p. de daño de Naturaleza a los enemigos cada 2 s y aumentando el tiempo entre los ataques de los enemigos un 67%. Dura 10 sec. Debe canalizar su magia para mantener el hechizo."},
[63275] = {name = "Pesca", description = "[q]Coge una caña de pescar y encuentra una extensión de agua donde pescar. Haz clic con el botón derecho del ratón sobre el cebo cuando veas que se mueve para coger el pez. Una mayor habilidad aumenta tu probabilidad de pescar con éxito en zonas de niveles más altos."},
[63276] = {name = "Marca de los Ignotos", description = "[q]Inflige una antigua imprecación al objetivo, succionando 5000 p. de salud cada 1 s de los enemigos cercanos al objetivo durante 10 sec."},
[63317] = {name = "Aliento de llamas", description = "[q]Inflige 13125 to 16875 p. de daño de Fuego a los enemigos que se encuentran en un cono frente al taumaturgo."},
[63341] = {name = "Cinturón cohete", description = "[q]Es un pájaro, es un avión… ¡Es Mimiron!"},
[63356] = {name = "Machaque aéreo", description = "[q]El ataque masivo que inflige daño físico y reduce la armadura un 20% durante 20 sec."},
[63364] = {name = "Barrera de saronita", description = "[q]Crea una Barrera de saronita que reduce todo el daño recibido un 99%."},
[63414] = {name = "Puesta en rotación", description = "[q]Pone en rotación los cañones dobles durante 4 sec antes de desencadenar una tromba de láseres en una dirección aleatoria. Inflige 20000 p. de daño Piroarcano cada 0.3 s a todos los enemigos a los que alcance. Dura 10 sec."},
[63420] = {name = "Oscuridad profunda", description = "[q]Inflige 750 p. de daño a todos los enemigos y aumenta el daño de las Sombras recibido un 10% por aplicación."},
[63499] = {name = "Disipar magia", description = "[q]Elimina la magia de un aliado, liberándolo de 1 efecto dañino."},
[63507] = {name = "Aterrar bestia de añublo", description = "[q]Un golpe rápido que inflige 4050 to 4950 p. de daño a las bestias de añublo. Este ataque se puede ejecutar muy rápido."},
[63520] = {name = "Hoja trituradora Grito de Guerra", description = "[q]Selecciona un barril de explosivos de la Horda para destruirlo. La hoja también se puede lanzar a un objetivo de Grito de Guerra de la Horda que le infligirá 158 to 192 p. de daño físico. Desactiva el botín y la experiencia en el objetivo afectado. No se puede usar sobre un objetivo de Grito de Guerra de la Horda que ya esté asignado a otro jugador."},
[63527] = {name = "Invocar esfera cargada", description = "[q]Invoca una esfera de electricidad que sirve de conducto de energía a un aliado."},
[63528] = {name = "Supercargado", description = "[q]Aumenta el daño infligido por el objetivo un 60%, hace que los ataques del objetivo causen potentes oleadas eléctricas y sacrifica al taumaturgo durante el proceso."},
[63539] = {name = "Ansiedad de separación", description = "[q]La separación de un compañero provoca una oleada de energía."},
[63541] = {name = "Relámpago bifurcado", description = "[q]Inflige daño de Naturaleza a los enemigos que se encuentran en un cono frente al taumaturgo."},
[63546] = {name = "Pisotón", description = "[q]Pisotea el suelo repeliendo a los enemigos cercanos."},
[63573] = {name = "Machaque aéreo con un brazo", description = "[q]Este ataque, no tan potente, inflige daño físico y reduce la armadura un 20% durante 20 sec."},
[63575] = {name = "Estela de humo", description = "[q]La reacción de la explosión hace volar por los aires al pasajero durante un periodo corto de tiempo."},
[63601] = {name = "Raíces férreas reforzadas", description = nil},
[63603] = {name = "¡Dale con una llave!", description = "[q]Si golpeas a la trituradora en el punto justo (o durante el tiempo suficiente) la arreglarás con aproximadamente 225 to 275 p. de salud. No se puede usar en combate o en movimiento."},
[63612] = {name = "Enseña de relámpagos", description = "[q]Electrocuta al objetivo, inflige 2500 p. de daño de Naturaleza y aumenta el daño de Naturaleza recibido un 40% durante 20 sec."},
[63615] = {name = "Devastar armadura", description = "[q]Reduce la armadura del objetivo un 5%."},
[63618] = {name = "Sobrecarga", description = "[q]Dispara una explosión orbital que elimina instantáneamente a los enemigos cercano si salen del Campo de Formación."},
[63619] = {name = "Arrastrarse por las Sombras", description = "[q]Se arrastra por las sombras hacia el objetivo enemigo y aumenta el daño que inflige el maligno de las Sombras un 15% durante 5 sec."},
[63630] = {name = "Marea vengativa", description = "[q]La muerte de un compañero despierta una marea de energía que aumenta el daño infligido un 100% y la velocidad de ataque un 50%."},
