[64746] = {name = "Vientos sobrecalentados", description = "[q]Invoca un ciclón que se mueve al azar. Los enemigos que estén cerca del ciclón saldrán volando por el aire."},
[64757] = {name = "Golpe de tormenta", description = "[q]Ataque instantáneo con ambas armas. Además, las 2 fuentes siguientes de daño de Naturaleza infligidas al objetivo aumentan un 20%. Dura 12 sec."},
[64758] = {name = "Cadena de relámpagos", description = "[q]Golpea al enemigo con una Descarga de relámpagos que se extiende a otro enemigo cercano. Afecta a un máximo de 5 objetivos e inflige mayor daño de Naturaleza a cada objetivo sucesivo."},
[64766] = {name = "Tromba de láseres", description = "[q]Dispara una tromba de ataques Arcanos que daña a los enemigos cercanos."},
[64771] = {name = "Fundir armadura", description = "[q]Reduce la velocidad de movimiento, de ataque y la armadura de un enemigo un 20% durante 20 sec."},
[64773] = {name = "Explosión de Fuego", description = "[q]Inflige daño de Fuego a un enemigo."},
[64775] = {name = "Barrera enigmática", description = "[q]Protege al taumaturgo de todos los ataques y hechizos."},
[64776] = {name = "Ametralladora", description = "[q]Un ataque canalizado que inflige 1885 to 2115 p. de daño cada cuarto de segundo a un enemigo seleccionado durante 3 sec. Este efecto ignora la armadura."},
[64779] = {name = "Salto cargado", description = "[q]Salta hacia la ubicación seleccionada."},
[64783] = {name = "Dispositivo de desplazamiento", description = nil},
[64819] = {name = "Salto apabullante", description = "[q]Salta hacia un enemigo, le inflige 6500 p. de daño físico y aturde a los enemigos que en un radio de 10 m durante 4 sec."},
[64825] = {name = "Rugido pasmante", description = "[q]Inflige 8775 to 9225 p. de daño físico a los enemigos en un cono frente al taumaturgo."},
[64847] = {name = "Caños de llamas rúnicos", description = "[q]Caños de llamas inflige 6825 to 7175 p. de daño de Fuego cada 1 s durante 5 sec en un cono frente al taumaturgo de 15 m."},
[64852] = {name = "Runa flamante", description = "[q]Inflige 2438 to 2562 p. de daño de Fuego cada 1 s a los enemigos en un radio de 3 m durante 10 sec."},
[64870] = {name = "Ráfaga de lava", description = "[q]Lanzas al objetivo lava fundida que inflige 8788 to 10212 p. de daño de Fuego. Si el objetivo tiene Choque de llamas, Ráfaga de lava consumirá Choque de llamas, haciendo que Ráfaga de lava aseste un golpe crítico."},
[64874] = {name = "Puñetazo rúnico", description = "[q]Golpe masivo que inflige 11100 to 12900 p. de daño y graba una runa en la piel del objetivo."},
[64877] = {name = "Endurecer puños", description = "[q]Da al taumaturgo un 100% de daño extra por golpe durante 10 sec, pero lo enraíza en el lugar."},
[64889] = {name = "Relámpago cargado", description = "[q]Los ataques tienen una probabilidad de lanzar Bola de relámpagos."},
[64897] = {name = "Fundir metal", description = "[q]Sana a un objetivo amistoso 19500 to 20500 p. cada 1 s durante 5 sec."},
[64903] = {name = "Fundir relámpagos", description = "[q]Infecta a un enemigo durante 10 sec, lo que hace que inflija daño de Naturaleza a sus aliados cercanos cada 2 s."},
[64918] = {name = "Choque eléctrico", description = "[q]Un golpe de electricidad que inflige 8775 to 9225 p. de daño de Naturaleza y derriba al objetivo."},
[64919] = {name = "Nova de hielo", description = "[q]Inflige 1885 to 2115 p. de daño de Escarcha a los enemigos en un radio de 10 m, aumenta el daño de Escarcha recibido un 25% y reduce la velocidad de movimiento un 10% durante 10 sec."},
[64953] = {name = "Ataque con salto", description = "[q]Salta al objetivo y le inflige un 120% de daño con arma."},
[64954] = {name = "Descarga de agua", description = "[q]Lanza una descarga de agua a un enemigo, lo que le inflige 33 to 38 p. de daño de Escarcha."},
[64966] = {name = "Torreta de hielo", description = "[q]Invoca a una Torreta de hielo cercana."},
[64979] = {name = "Cohete antiaéreo", description = "[q]Dispara un misil explosivo que explota en contacto con los objetivos aéreos."},
[65033] = {name = "Desgarro constrictor", description = "[q]Inflige daño físico a un enemigo cada 3 s durante 15 sec y reduce el movimiento un 3%."},
[65035] = {name = "Veneno acídulo", description = "[q]Inflige daño de Naturaleza a un enemigo."},
[65038] = {name = "Machacar", description = "[q]Machaca al objetivo y le inflige 1388 to 1612 p. de daño."},
[65060] = {name = "Descarga Crepuscular", description = "[q]Lanza una descarga de magia oscura a un enemigo y le inflige daño de las Sombras."},
[65062] = {name = "Latigazo", description = "[q]Golpea al objetivo, infligiendo daño de Naturaleza."},
[65063] = {name = "Descarga esmeralda", description = "[q]Inflige daño de Naturaleza a los jinetes Crepusculares y al Portal Crepuscular."},
[65070] = {name = "Matriz de defensa", description = "[q]La matriz protectora protege a todos los aliados en un radio de 7 m. Reduce el daño recibido un 90% durante 15 sec."},
[65072] = {name = "Encadenado a los establos", description = "[q]Una correa de cuerda."},
[65073] = {name = "Compactador de basura", description = nil},
[65075] = {name = "Torre de Llamas", description = "[q]Las defensas orbitales cargadas suministran poder adicional al leviatán. Salud y daño de hechizos de Fuego aumentados."},
[65076] = {name = "Torre de Tormentas", description = "[q]Las defensas orbitales cargadas suministran poder adicional al leviatán. Salud y daño aumentados."},
[65077] = {name = "Torre de Escarcha", description = "[q]Las defensas orbitales cargadas suministran poder adicional al leviatán. Salud aumentada. Enemigos cercanos frenados."},
[65078] = {name = "Compactado", description = "[q]Inflige 6338 to 6662 p. de daño de Naturaleza a los enemigos en un radio de 8 m del taumaturgo."},
[65080] = {name = "Chatarra tallada", description = "[q]Inflige 2438 to 2562 p. de daño de Fuego cada 1 s durante 4 sec."},
[65084] = {name = "Autodestrucción", description = "[q]Se destroza a sí mismo e inflige daño de Fuego a los enemigos cercanos."},
[65089] = {name = "Hojas de sierra", description = "[q]Inflige 488 to 512 p. de daño físico y 147 to 153 p. de daño físico extra cada 1 s durante 6 sec. Se acumula."},
[65099] = {name = "Desplegar sierras de recuperobot", description = "[q]Invoca a sierra de recuperobot."},
[65123] = {name = "Nube tormentosa", description = "[q]Crea una nube tormentosa cargada con relámpagos sobre un jugador amistoso aleatorio. Mientras está envuelto en esta nube, hasta 4 jugadores amistosos que toque el jugador recibirán una descarga de relámpago y se cargarán con Poder de tormenta, lo que aumenta su daño por golpe crítico un 135% durante 30 sec."},
[65127] = {name = "Brillo de fuego fatuo de Costaoscura", description = nil},
[65128] = {name = "Lanzar dinamita", description = "[q]Inflige 1110 to 1290 p. de daño de Fuego a todos los enemigos en un radio de 5 m del objetivo."},
[65129] = {name = "Deflagración", description = "[q]Inflige 4163 to 4837 p. de daño de Fuego a los enemigos cercanos."},
[65130] = {name = "Bomba de relojería", description = "[q]Explota después unos segundos, inflige 7400 to 8600 p. de daño de Fuego.\n Tic-tac."},
[65133] = {name = "Nube tormentosa", description = "[q]Crea una nube tormentosa cargada con relámpagos sobre un jugador amistoso aleatorio. Mientras está envuelto en esta nube, hasta 6 jugadores amistosos que toque el jugador recibirán una descarga de relámpago y se cargarán con Poder de tormenta, lo que aumenta su daño por golpe crítico un 135% durante 30 sec."},
[65147] = {name = "Romper escudo", description = "[q]Un ataque a distancia que inflige 2000 p. de daño y elimina una capa de Defender del oponente."},
[65162] = {name = "Jugador monta sobre Keristrasza", description = nil},
[65172] = {name = "Descarga de Escarcha", description = "[q]Inflige daño de Escarcha a un enemigo y reduce su velocidad de movimiento durante 4 sec."},
[65173] = {name = "Cadenas de hielo", description = "[q]Forma cadenas glaciales alrededor del enemigo que bloquean al objetivo y consume 350 to 450 p. de maná cada 2 s durante 10 sec."},
[65191] = {name = "Consumido", description = nil},
[65192] = {name = "Inhibidor de llamas", description = "[q]Lanza el Inhibidor de llamas al aire, sofocando todas las llamas en un radio de 10 m. Además, cualquier enemigo afectado tendrá su velocidad de lanzamiento de hechizos reducida un 50% durante 8 sec."},
[65204] = {name = "La Bestia liberada", description = nil},
[65210] = {name = "Desestabilización de matriz", description = "[q]Canaliza un rayo de energía titánica que desestabiliza la estructura molecular de algunas criaturas basadas en saronita, reduciendo la velocidad de ataque un 100% y la velocidad de lanzamiento un 300%. Dura 30 sec."},
[65214] = {name = "Inmolar señorial", description = "[q]Quema todos los vórtices giratorios cercanos, les inflige 24 to 29 p. de daño de Fuego y después inflige 4 p. de daño de Fuego adicionales cada 3 s durante 12 sec."},
[65215] = {name = "Conflagración infernal", description = "[q]Inflige 302 to 338 p. de daño de Fuego a un vórtice giratorio aleatorio y a aquellos que se encuentren cerca."},
[65216] = {name = "Venganza del Vórtice", description = "[q]Envuelve a Lord Magmathar con el poder de El Vórtice, inflige 278 to 322 p. de daño de Naturaleza y 225 p. de daño de Naturaleza extra cada 3 s durante 9 sec."},
[65231] = {name = "Rayo celestial", description = "[q]Golpea a Lord Magmathar con un rayo y le inflige 24 to 29 p. de daño de Naturaleza."},
[65232] = {name = "Extinguir llamas", description = "[q]Extingue el Inmolar señorial de Lord Magmathar. Se puede usar una vez cada 15 segundos."},
[65233] = {name = "Volver a El Vórtice", description = "[q]Os devuelve a ti y al vórtice giratorio a El Vórtice en el límite sur de Pico del Trueno. Solo se puede usar si ya estás rodeando a Lord Magmathar."},
[65248] = {name = "Aliento de Escarcha", description = "[q]Inflige 5363 to 5637 p. de daño de Escarcha a los enemigos a 30 m en un cono frente al taumaturgo. Además, la velocidad de ataque de los objetivos se reduce un 100% durante 6 sec."},
[65260] = {name = "Sacudida de alas", description = "[q]Inflige 750 to 1250 p. de daño a todos los enemigos en un radio de 25 m y los derriba."},
[65297] = {name = "Deflagración inestable", description = "[q]2625 to 3375 p. de daño de Fuego infligidos al pobre orco que lleve el lanzallamas."},
[65305] = {name = "El artefacto vuelve a sonar", description = nil},
[65306] = {name = "Invocar pila de tesoros enterrados", description = nil},
[65310] = {name = "Titan's Terminal Quest Complete", description = nil},
[65351] = {name = "Lanzar zepelín de papel", description = "[q]Lanza el zepelín a tu amigo de Rasganorte."},
[65386] = {name = "Honrar a los muertos", description = nil},
[65388] = {name = "Lanzallamas", description = "[q]Quema a todos los enemigos en un cono frente al demoledor que inflige daño de Fuego extra cada 1 s durante 10 sec."},
[65402] = {name = "Flagelo", description = "[q]Flagela a los enemigos cercanos e inflige 241 to 279 p. de daño."},
[65406] = {name = "Arañazo", description = "[q]Araña al objetivo, lo que inflige 240 to 360 p. de daño y 360 p. de daño extra durante 9 sec."},
[65407] = {name = "Abalanzarse", description = "[q]Te abalanzas con un salto de hasta 5 to 45 metros, lo que aturde al objetivo durante 3 sec e inflige 319 to 431 p. de daño. El alcance mínimo es de 5 metros."},
[65417] = {name = "Demoledor Grito de Guerra explota", description = "[q]El demoledor explota, lo que inflige 263 to 337 p. de daño de Fuego y derriba a cualquier miembro de la Horda cercano en un radio de 10 metros."},
[65481] = {name = "¡Vuelve a la base!", description = "[q]Ordena al jinete del viento que vuelva a la Avanzada Grito Infernal."},
[65488] = {name = "Tortura mental", description = "[q]Inflige daño de las Sombras a un enemigo y reduce su velocidad de movimiento durante 3 sec."},
[65489] = {name = "Lanzar explosivo", description = "[q]Acaba con los elfos y con sus recursos en Astranaar."},
[65490] = {name = "Toque vampírico", description = "[q]Inflige 33750 to 41250 p. de daño de las Sombras durante 15 sec a tu objetivo."},
[65492] = {name = "Explosión mental", description = "[q]Golpea al objetivo y le inflige 7088 to 7912 p. de daño de las Sombras."},
[65494] = {name = "Llamar a un kodo de carga", description = nil},
[65516] = {name = "Bola de nieve", description = "[q]¡Lánzame antes de que me funda!"},
[65541] = {name = "Palabra de las Sombras: dolor", description = "[q]Pronuncia una palabra de oscuridad que inflige daño de las Sombras a un enemigo cada 3 s durante 15 sec."},
[65542] = {name = "Silencio", description = "[q]Silencia al objetivo impidiendo que lance hechizos durante 5 sec."},
[65543] = {name = "Alarido psíquico", description = "[q]Emite un alarido psíquico que hace huir a los enemigos cercanos durante 8 sec."},
[65544] = {name = "Dispersión", description = "[q]Te conviertes en energía de las Sombras pura y reduces todo el daño recibido un 90%. No puedes atacar ni lanzar hechizos, pero regeneras un 6% de maná cada segundo durante 6 sec. Dispersión se puede lanzar mientras estás aturdido, asustado o silenciado, quita todos los efectos de frenado y de reducción del movimiento al lanzarlo y te hace inmune a ellos mientras dura."},
[65546] = {name = "Disipar magia", description = "[q]Elimina la magia del objetivo, suprimiendo 2 efectos dañinos de un amigo o 2 hechizos beneficiosos de un enemigo."},
[65547] = {name = "Abalorio JcJ", description = "[q]Disipa todos los efectos de merma de movimiento y todos los efectos que provocan pérdida de control de tu personaje."},
[65571] = {name = "Ofrenda de cristal", description = "[q]Invoca una ofrenda de cristal para los Magnos."},
[65579] = {name = "Aterrizar Thessera", description = "[q]Ordena a Thessera que aterrice."},
[65605] = {name = "Semilla detonadora", description = "[q]Planta una semilla volátil en un enemigo. Pasados 4 s, la semilla explotará e infligirá 3200 p. de daño de Naturaleza al objetivo y a sus aliados cercanos."},
[65611] = {name = "Lanzar muñeca de trapo", description = "[q]Lanza la muñeca de trapo a un matón wolvar."},
[65628] = {name = "Angelito", description = "[q]Eres una buena persona."},
[65629] = {name = "Desagradable", description = "[q]No es que seas muy buena persona."},
[65667] = {name = "Calor", description = "[q]Golpea a todos los ensamblajes férreos cercanos con calor intenso que aumenta su velocidad de movimiento 5 p. y celeridad 5 p. Este efecto se acumula hasta 10 veces. Si el efecto se acumula 10 veces, el ensamblaje férreo afectado se fundirá."},
[65719] = {name = "Nova de las Sombras", description = "[q]Inflige daño de las Sombras indiscriminado a las criaturas que se encuentren en un radio de 15 m."},
[65735] = {name = "Rejuvenecimiento", description = "[q]Sana a un aliado cada 3 s durante 12 sec."},
[65753] = {name = "Ojos de Tonga", description = "[q]Tonga Tótem de Runa te observa desde el cielo."},
[65762] = {name = "Garras extraafiladas", description = "[q]Shadumbra está muy decepcionada con la Vanguardia Grito de Guerra por haber invadido el Refugio de la Algaba y les inflige 110 to 140 p. de daño físico extra."},
[65766] = {name = "Oleada de Luz", description = "[q]Emitiendo una Oleada de Luz que inflige 4500 p. de daño cada 2 s a los enemigos de la oscuridad."},
[65768] = {name = "Oleada de oscuridad", description = "[q]Emitiendo una Oleada de oscuridad que inflige 4500 p. de daño cada 2 s a los enemigos de la Luz."},
[65775] = {name = "Mandíbulas empapadas en ácido", description = "[q]El ácido de las mandíbulas de escarabajo inflige 1800 p. de daño de Naturaleza cada 3 s. Dura 1 min."},
[65790] = {name = "Contrahechizo", description = "[q]Contrarresta el lanzamiento de hechizos del enemigo, evitando que lance hechizos de esa escuela de magia durante 8 sec. Genera una gran cantidad de amenaza."},
[65791] = {name = "Explosión Arcana", description = "[q]Golpea al objetivo con energía, lo que inflige 12488 to 14512 p. de daño Arcano. Cada vez que lances el hechizo Explosión Arcana, el daño de todos tus hechizos Arcanos aumentará un 10% y el coste de maná de Explosión Arcana aumentará un 150%. El efecto se acumula hasta 4 veces y dura 6 sec o hasta que se lance un hechizo que inflija daño Arcano, excepto Explosión Arcana."},
[65792] = {name = "Nova de Escarcha", description = "[q]Golpea a los enemigos situados cerca del taumaturgo, lo que inflige 365 to 415 p. de daño de Escarcha y los congela durante un máximo de 8 sec. Cualquier daño infligido puede interrumpir su efecto."},
[65793] = {name = "Traslación", description = "[q]Teletransporta al taumaturgo 20 m hacia delante salvo si hay algún obstáculo y lo libera de sus ataduras y del aturdimiento."},
[65795] = {name = "Luz desatada", description = "[q]La Bola de la Luz libera su energía e inflige 14625 to 15375 p. de daño de la Luz a los enemigos en un radio de 6 m."},
[65799] = {name = "Tromba Arcana", description = "[q]Lanza contra el objetivo enemigo varios misiles que le infligen 10450 to 11550 p. de daño Arcano."},
[65800] = {name = "Deflagración Arcana", description = "[q]Provoca una explosión de magia Arcana alrededor del taumaturgo, que inflige 5280 to 5720 p. de daño Arcano a todos los objetivos en un radio de 10 m."},
[65801] = {name = "Polimorfia", description = "[q]Transforma al enemigo en oveja obligándole a deambular durante 10 sec. Mientras deambula, la oveja no puede atacar ni lanzar hechizos. Si recibe daño volverá a su forma normal. Solo se puede aplicar polimorfia a 1 objetivo al mismo tiempo. Solo funciona con bestias, humanoides y alimañas."},
[65802] = {name = "Bloque de hielo", description = "[q]Te envuelve en un bloque de hielo que te protege de todos los ataques físicos y hechizos durante 5 sec, pero durante ese tiempo no puedes moverte, atacar ni lanzar hechizos. También provoca Hipotermia, que te impide volver a lanzar Bloque de hielo durante 30 sec."},
[65804] = {name = "Plantar estandarte de la Horda", description = "[q]¡Por la Horda!"},
[65807] = {name = "Descarga de Escarcha", description = "[q]Lanza una descarga de Escarcha al enemigo que inflige 11563 to 13437 p. de daño de Escarcha y ralentiza su velocidad de movimiento un 40% durante 9 sec."},
[65808] = {name = "Oscuridad desatada", description = "[q]La oscuridad concentrada libera su energía e inflige 14625 to 15375 p. de daño de la oscuridad a los enemigos en un radio de 6 m."},
[65809] = {name = "Miedo", description = "[q]Provoca miedo en el enemigo y hace que huya durante un máximo de 10 sec. El daño infligido puede interrumpir el efecto. Solo se puede asustar a 1 objetivo cada vez."},
[65810] = {name = "Corrupción", description = "[q]Corrompe al objetivo, inflige 30000 p. de daño de las Sombras durante 18 sec."},
[65812] = {name = "Aflicción inestable", description = "[q]La energía de las Sombras destruye poco a poco al objetivo e inflige 23125 to 26875 p. de daño durante 15 sec. Además, si se disipa tu hechizo Aflicción inestable, el que lo haya disipado recibirá 13875 to 16125 p. de daño de las Sombras y quedará silenciado durante 4 sec."},
[65814] = {name = "Maldición de agonía", description = "[q]Lanza sobre el objetivo una Maldición de agonía, lo que inflige 42000 p. de daño de las Sombras durante 24 sec. El daño se inflige despacio al principio y aumenta progresivamente a medida que la maldición alcanza su duración completa. Un brujo solo puede tener una maldición activa a la vez sobre un mismo objetivo."},
[65815] = {name = "Maldición de agotamiento", description = "[q]Reduce la velocidad de movimiento del objetivo un 50% durante 12 sec. Cada brujo solo puede tener una maldición activa sobre cada objetivo."},
[65816] = {name = "Llamas infernales", description = "[q]Prende el área que rodea al taumaturgo, infligiéndose 1500 p. de daño de Fuego a sí mismo y 9000 p. de daño de Fuego a todos los demás enemigos cercanos cada 1 s. Dura 15 sec."},
[65819] = {name = "Dolor abrasador", description = "[q]Inflige un dolor abrasador al objetivo enemigo infligiendo 5490 to 6510 p. de daño de Fuego."},
[65821] = {name = "Descarga de las Sombras", description = "[q]Lanza una descarga de las Sombras al enemigo, lo que le inflige 11813 to 13187 p. de daño de las Sombras."},
[65837] = {name = "Latigazo de vid", description = "[q]Azota hasta a 5 objetivos y les inflige 43 to 57 p. de daño de Naturaleza cada segundo."},
[65843] = {name = "Ola de choque", description = "[q]Envía una ola de fuerza frente al ancestro que inflige 324 to 376 p. de daño y repele a todos los objetivos en un radio de 30 metros."},
[65854] = {name = "Fuego estelar", description = "[q]Inflige 12387 to 14613 p. de daño Arcano al objetivo."},
[65855] = {name = "Enjambre de insectos", description = "[q]Rodea al objetivo enemigo de insectos, lo que reduce su probabilidad de golpear con ataques cuerpo a cuerpo y a distancia un 3% e inflige 22200 to 25800 p. de daño de Naturaleza durante 12 sec."},
[65856] = {name = "Fuego lunar", description = "[q]Quema al enemigo, inflige 4140 to 4860 p. de daño Arcano y 7000 p. de daño Arcano extra durante 12 sec."},
[65857] = {name = "Raíces enredadoras", description = "[q]Enraíza al enemigo e inflige 1576.7 to 1756.7 p. de daño de Naturaleza durante 10 sec. El daño infligido puede interrumpir el efecto."},
[65858] = {name = "Escudo de la luz", description = "[q]Un escudo de la luz que protege a la hermana del peligro y absorbe 1200000 p. de daño y evita la interrupción de hechizos durante 16 sec."},
[65859] = {name = "Ciclón", description = "[q]Lanza al objetivo enemigo por los aires, lo que evita que pueda realizar ninguna acción pero lo hace invulnerable durante 6 sec. Ciclón solo puede afectar a un objetivo a la vez."},
[65860] = {name = "Piel de corteza", description = "[q]La piel del druida se endurece como la corteza de un árbol. Todo el daño recibido se reduce un 40%. Se puede usar mientras se está aturdido, congelado, incapacitado, asustado o dormido. Se puede usar bajo todas las formas. Dura 12 sec."},
[65861] = {name = "Fuerza de la Naturaleza", description = "[q]Invoca a 3 antárboles que atacan objetivos enemigos durante 30 sec."},
[65862] = {name = "Cólera", description = "[q]Inflige 8460 to 9540 p. de daño de Naturaleza al objetivo."},
[65863] = {name = "Fuego feérico", description = "[q]Reduce la armadura del objetivo un 15% durante 40 sec. Mientras dura su efecto, el objetivo no puede utilizar el sigilo ni hacerse invisible."},
[65866] = {name = "Disparo explosivo", description = "[q]Lanzas una carga explosiva al objetivo, que inflige 4533 to 5467 p. de daño de Fuego. La carga golpeará al objetivo cada segundo durante 2 sec adicionales."},
[65867] = {name = "Disparo firme", description = "[q]Un disparo firme que provoca daño con arma a un objetivo."},
[65868] = {name = "Disparar", description = "[q]Dispara a un enemigo y le inflige daño físico."},
[65869] = {name = "Separación", description = "[q]Intentas retirarte del combate y saltas hacia atrás."},
[65871] = {name = "Disuasión", description = "[q]Cuando se activa, aumenta la probabilidad de parar un 100% y otorga una probabilidad de un 100% de desviar los hechizos lanzados por objetivos que estén frente a ti. Dura 5 sec."},
[65874] = {name = "Escudo de la oscuridad", description = "[q]Un escudo de la oscuridad que protege a la hermana del peligro y absorbe 1200000 p. de daño y evita la interrupción de hechizos durante 16 sec."},
[65875] = {name = "Pacto de las Gemelas", description = "[q]El Pacto de las Gemelas sana un 50% de su salud total."},
[65876] = {name = "Pacto de las Gemelas", description = "[q]El Pacto de las Gemelas sana un 50% de su salud total."},
[65877] = {name = "Picadura de dracoleón", description = "[q]Un disparo punzante que duerme al objetivo durante 8 sec. Cualquier daño infligido con posterioridad anulará su efecto. Cuando el objetivo se despierta, la picadura inflige 2.6 p. de daño de Naturaleza durante 8 sec. Un cazador solo puede tener una picadura activa a la vez sobre un mismo objetivo."},
[65879] = {name = "Poder de las Gemelas", description = "[q]El poder compartido de las Gemelas aumenta el daño físico infligido un 20% y permite a las Gemelas la doble empuñadura durante 15 sec."},
[65880] = {name = "Trampa de Escarcha", description = "[q]Coloca una trampa de Escarcha que crea una mancha de hielo durante 30 sec cuando el primer enemigo se acerca. Todos los enemigos en un radio de 10 m se ralentizarán un 50% mientras estén en el área de efecto. La trampa dura 1 min. Solo puede haber 1 trampa activa a la vez."},
[65883] = {name = "Disparo de puntería", description = "[q]Un disparo de puntería que aumenta el daño a distancia 6000 p. y reduce la sanación del objetivo un 50%. Dura 10 sec."},
[65916] = {name = "Poder de las Gemelas", description = "[q]El poder compartido de las Gemelas aumenta el daño físico infligido un 20% y permite a las Gemelas la doble empuñadura durante 15 sec."},
[65918] = {name = "Aturdido", description = "[q]Caer golpeado de una montura aturde al objetivo durante 10 sec."},
[65924] = {name = "Abrumar", description = "[q]Abruma al enemigo instantáneamente y le inflige daño con arma más 35 p. Solo se puede utilizar después de que el objetivo esquive. El Abrumar no se puede bloquear, ni esquivar ni parar."},
[65925] = {name = "Asalto inexorable", description = "[q]Cuando Abrumar alcanza a un jugador que está lanzando un hechizo, reduce su daño mágico y su sanación un 50% durante 6 sec."},
[65926] = {name = "Golpe mortal", description = "[q]Un golpe sanguinario que inflige daño con arma más 1000 p. y hiere al objetivo, lo que reduce la eficacia de cualquier sanación un 50% durante 10 sec."},
[65927] = {name = "Cargar", description = "[q]Carga contra un enemigo y lo aturde durante 1.50 sec."},
[65929] = {name = "Carga aturdidora", description = "[q]Carga contra un enemigo y lo aturde durante 1.50 sec."},
[65930] = {name = "Grito intimidador", description = "[q]El guerrero grita haciendo que los enemigos en un radio de 8 m se encojan de pavor durante 8 sec."},
[65932] = {name = "Represalias", description = "[q]Contraataca instantáneamente a cualquier enemigo que te ataque cuerpo a cuerpo durante 12 sec. No se puede contraatacar si el ataque cuerpo a cuerpo llega por la espalda. Un máximo de 20 ataques provocarán Represalias."},
[65935] = {name = "Desarmar", description = "[q]Desarma el arma a distancia y principal del enemigo durante 10 sec."},
[65936] = {name = "Hender armadura", description = "[q]Hiende la armadura del objetivo, lo que la reduce un 4% por cada uso de Hender armadura y genera una gran cantidad de amenaza. Amenaza aumentada con el poder de ataque. Se puede usar un máximo de 5 veces. Dura 20 sec."},
[65940] = {name = "Lanzamiento destrozador", description = "[q]Lanza tu arma a un enemigo e inflige daño con arma. Reduce la armadura del objetivo un 20% durante 10 sec o elimina cualquier invulnerabilidad."},
[65946] = {name = "Filotormenta", description = "[q]En un torbellino de acero atacas a enemigos en un radio de 8 m e infliges un 50% de daño con arma."},
[65949] = {name = "Presura Val'kyr", description = "[q]El Poder de las Gemelas aumenta la velocidad de ataque un 10% durante 15 sec. Se acumula."},
[65950] = {name = "Toque de la Luz", description = "[q]El Toque de la Luz Val'kyr te afecta e inflige 5850 to 6150 p. de daño de la Luz a los jugadores bajo el efecto de Esencia de la oscuridad cada 2 s. El Toque de la luz no infligirá daño a los objetivos con este mismo atributo."},
[65954] = {name = "Hemorragia", description = "[q]Un golpe instantáneo que inflige un 110% de daño con arma y le provoca al objetivo una hemorragia, aumentando cualquier daño físico infligido al objetivo hasta 75 p. Dura 15 sec o hasta que se utilizan 10 cargas. Otorga 1 p. de combo."},
[65955] = {name = "Abanico de cuchillos", description = "[q]Lanza instantáneamente las dos armas a todos los objetivos que se encuentren en un radio de 10 m, lo que inflige un 90% de daño con arma."},
[65956] = {name = "Aluvión de acero", description = "[q]Aumenta tu velocidad de ataque un 20%. Además, los ataques alcanzan a un enemigo cercano extra. Dura 15 sec."},
[65957] = {name = "Eviscerar", description = "[q]Remate que causa 8750 to 11250 p. de daño físico."},
[65960] = {name = "Ceguera", description = "[q]Ciega al objetivo, provocando que deambule sin rumbo durante un máximo de 1 min. Cualquier daño infligido con posterioridad anulará su efecto."},
[65961] = {name = "Capa de las Sombras", description = "[q]Elimina instantáneamente todos los efectos de hechizos dañinos y aumenta tu probabilidad de resistir todos los hechizos un 90% durante 5 sec. No elimina los efectos que te impiden usar la Capa de las Sombras."},
[65962] = {name = "Veneno hiriente", description = "[q]Envenena al enemigo, reduciendo todos los efectos de sanación utilizados sobre él un 50% durante 15 sec."},
[65970] = {name = "Golpe de tormenta", description = "[q]Ataque instantáneo con ambas armas. Además, las 4 fuentes siguientes de daño de Naturaleza infligidas al objetivo aumentan un 20%. Dura 12 sec."},
[65973] = {name = "Choque de tierra", description = "[q]Choque instantáneo con fuerza de conmoción que inflige 3213 to 3387 p. de daño de Naturaleza al objetivo. También interrumpe el lanzamiento de hechizos e impide que se lance cualquier hechizo de esa misma escuela durante 2 sec."},
[65974] = {name = "Latigazo de lava", description = "[q]Cargas con lava el arma de tu mano izquierda e infliges instantáneamente un 100% de daño con arma con tu mano izquierda. El daño aumenta un 25% si tu arma de mano izquierda está encantada con Lengua de Fuego."},
[65976] = {name = "Viento Furioso", description = "[q]Imbuido del poder del viento, golpeas con rapidez 2 veces extra."},
[65980] = {name = "Ansia de sangre", description = "[q]Aumenta la velocidad de ataque cuerpo a cuerpo, a distancia y de lanzamiento de hechizos de todos los miembros del grupo un 30%. Dura 40 sec."},
[65983] = {name = "Heroísmo", description = "[q]Aumenta la velocidad de ataque cuerpo a cuerpo, a distancia y de lanzamiento de hechizos de todos los miembros del grupo y banda un 30%. Dura 40 sec."},
[65986] = {name = "Arma vorágine", description = "[q]Reduce el tiempo de lanzamiento de tu siguiente Descarga de relámpagos, Cadena de relámpagos, Ola de sanación inferior, Ola de sanación o Sanación en cadena un 20%."},
[65998] = {name = "Abrasamiento", description = "[q]Abrasa al objetivo con 4950 to 6050 p. de daño de Fuego."},
[66001] = {name = "Toque de la oscuridad", description = "[q]El Toque de la oscuridad Val'kyr te afecta e inflige 5850 to 6150 p. de daño de la oscuridad a los jugadores bajo el efecto de Esencia de la luz cada 2 s. El Toque de la oscuridad no infligirá daño a los objetivos con este mismo atributo."},
[66003] = {name = "Golpe de cruzado", description = "[q]Un golpe instantáneo que inflige un 110% de daño con arma."},
[66004] = {name = "Sello de orden", description = "[q]Todos los ataques cuerpo a cuerpo tienen una probabilidad de infligir daño Sagrado extra."},
[66005] = {name = "Sentencia de orden", description = "[q]Juzga al enemigo y le inflige 8550 to 9450 p. de daño Sagrado."},
[66006] = {name = "Tormenta divina", description = "[q]Un ataque instantáneo con arma que inflige un 110% del daño con arma hasta un total de 4 enemigos que se encuentren en un radio de 8 m."},
[66007] = {name = "Martillo de Justicia", description = "[q]Aturde al objetivo durante 6 sec."},
[66008] = {name = "Arrepentimiento", description = "[q]Pone al objetivo enemigo en estado de meditación, lo que lo incapacita durante un máximo de 10 sec. Cualquier daño despertará al objetivo. Solo funciona contra demonios, dragonantes, gigantes, humanoides y no-muertos."},
[66009] = {name = "Mano de protección", description = "[q]Protege a un miembro aliado seleccionado de todos los ataques físicos durante 10 sec."},
[66010] = {name = "Escudo divino", description = "[q]Protege al paladín de todo el daño y hechizos durante 12 sec pero reduce el daño que inflijas un 50%."},
[66011] = {name = "Cólera vengativa", description = "[q]Aumenta todo el daño infligido y toda la sanación realizada un 20% durante 20 sec."},
[66012] = {name = "Tajo congelante", description = "[q]Inflige un 25% de daño de Fríofísico a un enemigo y lo congela en un bloque de hielo durante 3 sec."},
[66013] = {name = "Frío penetrante", description = "[q]Frío penetrante se adentra en varios objetivos enemigos e inflige 6000 p. de daño de Escarcha cada 3 s. Dura 18 sec."},
[66017] = {name = "Atracción letal", description = "[q]Reúne la energía profana que rodea y vincula toda materia, lo que acerca al objetivo hacia el caballero de la Muerte y obliga al enemigo a atacar al caballero de la Muerte durante 3 sec."},
[66018] = {name = "Estrangular", description = "[q]Estrangula al enemigo, le silencia durante 5 sec e inflige 180 p. de daño de las Sombras al final del efecto."},
[66019] = {name = "Espiral de la muerte", description = "[q]Dispara una oleada de energía profana, que inflige 6475 to 7525 p. de daño de las Sombras a un enemigo o sana 9713 p. de daño a un objetivo no-muerto amistoso."},
[66020] = {name = "Cadenas de hielo", description = "[q]Encadena al objetivo con cadenas heladas, lo que reduce su velocidad de movimiento a un 5% de lo normal. El objetivo recupera un 10% de su velocidad de movimiento cada segundo durante 10 sec."},
[66021] = {name = "Toque helado", description = "[q]Enfría al objetivo e inflige 5272 - 5728 p. de daño de Escarcha y lo contagia con Fiebre de Escarcha, una enfermedad que inflige daño periódico y reduce la velocidad de ataque a distancia y cuerpo a cuerpo un 20% durante 21 sec."},
[66023] = {name = "Entereza ligada al hielo", description = "[q]El caballero de la Muerte congela su sangre para hacerse inmune a los efectos de aturdir y reduce todo el daño recibido un 45% durante 12 sec."},
[66032] = {name = "Red", description = "[q]Inmoviliza a un enemigo durante 10 sec."},
[66042] = {name = "Bola de Fuego", description = "[q]Inflige daño de Fuego a un enemigo."},
[66043] = {name = "Polimorfia", description = "[q]Transforma al enemigo en oveja obligándole a deambular durante 4 sec. Mientras deambula, la oveja no puede atacar ni lanzar hechizos. Si recibe daño volverá a su forma normal. Solo se puede aplicar Polimorfia a un objetivo al mismo tiempo. Solo funciona con bestias, dragones, gigantes, humanoides y alimañas."},
[66044] = {name = "Ola explosiva", description = "[q]Desata una ola de llamas que inflige daño de Fuego a los enemigos cercanos y reduce su velocidad de movimiento durante 4 sec."},
[66045] = {name = "Celeridad", description = "[q]Aumenta las velocidades de ataque y de lanzamiento del taumaturgo un 100%."},
[66046] = {name = "Vórtice de la luz", description = "[q]Fjola Penívea comienza a lanzar un poderoso Vórtice de la luz. Se canaliza durante 5 segundos e inflige 15600 to 16400 p. de daño cada 1 s durante 5 sec."},
[66047] = {name = "Golpe de Escarcha", description = "[q]Golpea instantáneamente al enemigo y le inflige un 100% de daño con arma más 250 p. de daño de Escarcha. No se puede esquivar, bloquear o parar."},
[66053] = {name = "Mareas Vivas", description = "[q]Sana a un objetivo amistoso 57600 to 62400 p. y otros 138750 to 161250 p. durante 15 sec."},
[66054] = {name = "Maleficio", description = "[q]Transforma al enemigo en una rana. Mientras esté bajo el maleficio, el objetivo hechizado no puede ni atacar ni lanzar hechizos. Si se le inflige daño, se puede interrumpir el efecto. Dura 8 sec. Solo se puede echar un maleficio a un objetivo a la vez. Solo funciona con humanoides y bestias."},
[66055] = {name = "Ola de sanación inferior", description = "[q]Sana a un objetivo amistoso 84015 to 95985 p."},
[66056] = {name = "Limpiar espíritu", description = "[q]Limpia el espíritu de un objetivo amistoso, disipando 1 efecto de veneno, 1 efecto de enfermedad y 1 efecto de maldición."},
[66058] = {name = "Vórtice de la oscuridad", description = "[q]Eydis Penaumbra comienza a lanzar un poderoso Vórtice de la oscuridad. Se canaliza durante 5 sec e inflige 15600 to 16400 p. de daño cada 1 s durante 5 sec."},
[66060] = {name = "Sprint", description = "[q]Aumenta la velocidad de movimiento un 120% durante 3 sec."},
[66063] = {name = "Escudo de tierra", description = "[q]Protege al objetivo con un escudo de tierra que hace que los ataques sanen al objetivo protegido. Este efecto solo ocurre una vez cada pocos segundos. 6 cargas. Dura 2 min."},
[66065] = {name = "Rejuvenecimiento", description = "[q]Sana al objetivo 416250 to 483750 p. durante 15 sec."},
[66066] = {name = "Nutrir", description = "[q]Sana a un objetivo amistoso 138750 to 161250 p."},
[66067] = {name = "Recrecimiento", description = "[q]Sana a un objetivo amistoso 85050 to 94950 p. y otros 194250 to 225750 p. durante 21 sec."},
[66068] = {name = "Espinas", description = "[q]Hace que salgan espinas del objetivo amistoso que infligen 463 to 537 p. de daño de Naturaleza cuando los atacantes golpean. Dura 10 min."},
[66069] = {name = "Punta de las Gemelas", description = "[q]La Punta de las Gemelas inflige un 125% de daño con arma y aumenta el daño recibido un 20% durante 15 sec o durante 5 cargas."},
[66070] = {name = "Raíces enredadoras", description = "[q]Enraíza al enemigo e inflige 1500 p. de daño de Naturaleza durante 10 sec. El daño infligido puede interrumpir el efecto. Solo se puede usar en el exterior."},
[66071] = {name = "Trabazón con la Naturaleza", description = "[q]Mientras esté activo, cada vez que un enemigo ataque al taumaturgo tendrá un 100% de probabilidad de ser afligido por Raíces enredadoras. 1 cargas. Dura 45 sec."},
[66075] = {name = "Punta de las Gemelas", description = "[q]La Punta de las Gemelas inflige un 125% de daño con arma y aumenta el daño recibido un 20% durante 15 sec o durante 5 cargas."},
[66076] = {name = "Lanzar", description = "[q]Lanza tu arma e inflige un 0% de su daño al objetivo."},
[66081] = {name = "Multidisparo", description = "[q]Dispara una descarga de misiles al enemigo y aliados cercanos que alcanza a un máximo de 3 objetivos y aumenta el daño normal 100 p."},
[66083] = {name = "Flechas de relámpagos", description = "[q]Invoca relámpagos que golpean las flechas del taumaturgo y las encantan. Los ataques a distancia básicos infligen 4713 to 5287 p. de daño de la Naturaleza extra."},
[66086] = {name = "Tranquilidad", description = "[q]Regenera a todos los miembros del grupo cercanos 231250 to 268750 p. cada 2 s durante 10 sec. Canalizado."},
[66092] = {name = "Determinación", description = "[q]El escarabajo recupera su salud y se vuelve inmune a los efectos de reducción de movimiento y de aturdimiento. También aumenta la velocidad de movimiento un 100%."},
[66093] = {name = "Flor de vida", description = "[q]Sana 11310 to 12690 p. cada segundo y 55500 to 64500 p. cuando el efecto se acaba o se disipa."},
[66098] = {name = "Penitencia", description = "[q]Lanza una salva de Luz Sagrada al objetivo que sana 42255 to 47745 p. a un aliado instantáneamente y cada 1 s durante 2 sec."},
[66099] = {name = "Palabra de poder: escudo", description = "[q]Refuerza con un escudo el alma de un miembro del grupo, amortiguando 67063 to 77937 p. de daño. Dura 30 sec. Mientras dura el escudo, el lanzamiento de hechizos no se verá interrumpido por el daño. Una vez protegido, el objetivo no puede recibir protección de nuevo hasta pasados 15 sec."},
[66100] = {name = "Quemadura de maná", description = "[q]Destruye 4374 to 4626 p. de maná de un objetivo. Por cada punto de maná destruido, el objetivo recibe 3 p. de daño de las Sombras."},
[66104] = {name = "Sanación relámpago", description = "[q]Sana a un objetivo amistoso 83250 to 96750 p."},
[66112] = {name = "Luz Sagrada", description = "[q]Sana a un objetivo amistoso 127710 to 142290 p."},
[66113] = {name = "Destello de Luz", description = "[q]Sana a un objetivo amistoso 84825 to 95175 p."},
[66114] = {name = "Choque Sagrado", description = "[q]Golpea al objetivo con energía Sagrada, inflige 72000 to 78000 p. de daño Sagrado a un enemigo o sana 72000 to 78000 p. a un aliado."},
[66115] = {name = "Mano de libertad", description = "[q]Da a un objetivo amistoso una Mano que le concede inmunidad a los efectos que impiden el movimiento durante 10 sec. Los jugadores solo pueden tener una Mano activa por paladín a la vez."},
[66116] = {name = "Limpiar", description = "[q]Purga a un objetivo amistoso, lo que elimina 1 efecto de envenenamiento, 1 efecto de enfermedad y 1 efecto de magia."},
[66117] = {name = "Disparar", description = "[q]Dispara a un enemigo y le inflige daño físico."},
[66118] = {name = "Enjambre parasitante", description = "[q]Libera un enjambre de insectos que ataca a los enemigos del taumaturgo y parasita un 30% de la salud actual de cada objetivo cada 1 s. Parasitará un mínimo de 250 p. de salud."},
[66129] = {name = "Frenesí de araña", description = "[q]Aumenta la velocidad de movimiento, de lanzamiento y de ataque un 100% por cada perforador nerubiano en un radio de 12 m."},
[66134] = {name = "Golpe de las Sombras", description = "[q]Camina entre las sombras para aparecer detrás de un enemigo seleccionado e infligir 40000 p. de daño de las Sombras."},
[66177] = {name = "Renovar", description = "[q]Sana al objetivo 346875 to 403125 p. durante 15 sec."},
[66178] = {name = "Paso de las Sombras", description = "[q]Intenta dar un paso en las sombras y reaparecer detrás de tu enemigo."},
[66180] = {name = "Bola de cuero pesado", description = "[q]Lanza la bola a un jugador amistoso. Si tiene sitio en la mochila, la cogerá."},
[66187] = {name = "Runa constante", description = nil},
[66193] = {name = "Escarcha permanente", description = "[q]Congela una pequeña zona del suelo, evita que muchas criaturas excaven y reduce la velocidad de movimiento del área un 80%."},
[66195] = {name = "Napalm", description = "[q]Dispara una bola de napalm e inflige daño de Fuego a todos los enemigos en un radio de 5 m del impacto. Además, los objetivos quedarán cubiertos de napalm, lo que inflige daño de Fuego extra durante 5 sec."},
[66197] = {name = "Llama de la Legión", description = "[q]Quema al objetivo y hace que aparezca una llama de la Legión en donde se encuentren los objetivos cada 1 s durante 6 sec."},
[66203] = {name = "Golpe de vapor", description = "[q]Inflige daño de Fuego a todos los enemigos en un radio de 15 m y los derriba. Cuando se activa esta facultad, la torreta no podrá generar vapor durante 4 sec."},
[66207] = {name = "Cortar alas", description = "[q]Mutila al enemigo y reduce la velocidad de movimiento del objetivo un 50% durante 10 sec."},
[66218] = {name = "Lanzar", description = "[q]Lanza al conductor a la ubicación designada."},
[66228] = {name = "Poder abisal", description = "[q]El Poder de lo abisal aumenta el daño mágico infligido un 20% durante 30 sec."},
[66237] = {name = "Incinerar carne", description = "[q]¡Quema la carne de tus huesos! Absorbe los próximos 85000 p. de la sanación recibida y reduce el daño infligido un 50% durante 15 sec. Si Incinerar carne no se elimina antes de que expire causará un Inferno ardiente."},
[66251] = {name = "Lanzar", description = "[q]Lanza al conductor a la ubicación designada."},
[66254] = {name = "Invocar ciclón de fuego", description = "[q]Invoca a 1 elemental de fuego para acompañar al taumaturgo hasta que lo retire."},
[66258] = {name = "Erupción infernal", description = "[q]Lord Jaraxxus invoca un volcán infernal que engendra infernales."},
[66269] = {name = "Portal abisal", description = "[q]Lord Jaraxxus invoca un Portal abisal."},
[66282] = {name = "Gemido de tdampa pada daptodes", description = "[q]La tdampa emite un sonido que destroza el oído, como si un millón de raptores te gritasen a la cara."},
[66283] = {name = "Punta de dolor giratoria", description = "[q]Salta hacia un enemigo, lo agarra y le inflige un daño físico importante."},
[66290] = {name = "Dormir", description = "[q]Duerme a un enemigo durante un máximo de 6 sec. Cualquier daño despertará al objetivo. Solo se puede dormir a 1 objetivo a la vez."},
[66296] = {name = "Velo de sombras", description = "[q]Cubre el vehículo con sombras y aumenta la velocidad de movimiento y de sigilo durante 20 sec."},
[66300] = {name = "A fondo", description = "[q]¡Pisa a fondo! Aumenta la velocidad un 50%."},
[66313] = {name = "Bomba de Fuego", description = "[q]Inflige 4813 to 6187 p. de daño de Fuego a todos los enemigos en un radio de 8 m del objetivo."},
[66320] = {name = "Bomba de Fuego", description = "[q]Inflige 7800 to 8200 p. de daño de Fuego a todos los enemigos cada 1 s."},
[66330] = {name = "Pisotón pasmante", description = "[q]Inflige un Pisotón pasmante que inflige 11700 to 12300 p. de daño físico a todos los enemigos en un radio de 15 m e interrumpe el lanzamiento de hechizos durante 8 s."},
[66331] = {name = "Empalar", description = "[q]Inflige un 150% de daño con arma a un enemigo y lo hace sangrar 3938 to 5062 p. de daño por cada aplicación cada 2 s durante 40 sec."},
[66334] = {name = "Beso de la Maestra", description = "[q]El próximo hechizo con un tiempo de lanzamiento que lances interrumpe esa escuela durante 8 sec e inflige 13650 to 14350 p. de daño de las Sombras."},
[66378] = {name = "Tajo shivaísta", description = "[q]Un ataque de torbellino que inflige un 75% de daño con arma y perfora la armadura."},
[66407] = {name = "Rotura craneal", description = "[q]Aturde al enemigo durante 2 sec."},
[66408] = {name = "Sacudir", description = "[q]Golpea a un enemigo e inflige un 75% de daño cuerpo a cuerpo normal. Interrumpe el lanzamiento de hechizos durante 5 sec."},
[66456] = {name = "Lanzamiento de guja", description = "[q]Dispara una gran guja hacia el objetivo e inflige daño físico y 4242 to 4758 p. de daño de asedio en un radio de 5 m."},
[66489] = {name = "Lanzamiento de lanza", description = "[q]Inflige daño de Escarcha a un enemigo."},
[66494] = {name = "Veta vil", description = "[q]El infernal llama vil carga contra un objetivo y le inflige 11700 to 12300 p. de daño de Fuego. Activa un Inferno vil en la zona."},
[66495] = {name = "Inferno vil", description = "[q]Inflige periódicamente daño de Fuego a los aliados en un radio de 15 m durante 6 sec."},
