[70391] = {name = "Maldición de agonía", description = "[q]Lanza sobre el objetivo una Maldición de agonía, lo que inflige 7200 p. de daño de las Sombras durante 24 sec. El daño se inflige despacio al principio y aumenta progresivamente a medida que la maldición alcanza su duración completa. Un brujo solo puede tener una maldición activa a la vez sobre un mismo objetivo."},
[70392] = {name = "Estigma negro", description = "[q]Inflige 139 to 161 p. de daño de las Sombras a un enemigo y aumenta el daño de las Sombras que sufre un 50% durante 10 sec."},
