[71274] = {name = "Orbe congelado", description = "[q]El taumaturgo canaliza un orbe congelado enorme que selecciona como objetivo a un enemigo cercano."},
[71289] = {name = "Dominar mente", description = "[q]Somete la voluntad de un objetivo enemigo y provoca que quede embelesado durante 12 sec. Su daño infligido aumenta un 200% y su sanación realizada aumenta un 500% durante ese tiempo."},
[71296] = {name = "Descarga de las Sombras", description = "[q]Inflige 12000 p. de daño de las Sombras al objetivo."},
[71298] = {name = "Desterrar", description = "[q]Destierra al objetivo enemigo, evitando que pueda realizar ninguna acción, pero volviéndolo invulnerable hasta un máximo de 6 sec. Solo se puede desterrar a un objetivo al mismo tiempo."},
[71299] = {name = "Abrazo de la Muerte", description = "[q]Un escudo con poder de las Sombras rodea al taumaturgo y lo vuelve inmune a las interrupciones. La barrera amortigua 250000 p. de daño y dura 10 sec."},
[71303] = {name = "Invocar Ymirjar", description = "[q]Invoca a 71302 zombis para que ayuden al taumaturgo."},
[71306] = {name = "Vientos retorcidos", description = "[q]Cambias de sitio y listas negras con el objetivo."},
[71316] = {name = "Golpe glacial", description = "[q]Un ataque sanguinario y escalofriante que provoca que el objetivo sangre abundantemente hasta que sane por completo. Inflige un 20% de daño de Escarcha cada 3 segundos hasta que sane por completo."},
[71318] = {name = "Descarga de Escarcha", description = "[q]Inflige daño de Escarcha a un enemigo y reduce su velocidad de movimiento durante 6 sec."},
[71320] = {name = "Nova de Escarcha", description = "[q]Inflige 4625 to 5375 p. de daño de Escarcha a los enemigos cercanos y los inmoviliza durante un máximo de 4 sec."},
[71323] = {name = "Filoescarcha", description = "[q]Ataca a los enemigos cercanos con un torbellino de acero que dura 6 sec e inflige el doble de daño."},
[71325] = {name = "Filoescarcha", description = "[q]Imbuye tu arma con el poder de Corona de Hielo y provoca que emita daño de Escarcha durante 14 sec."},
[71326] = {name = "Escarabajos de la cripta", description = "[q]Invoca a varios escarabajos de la cripta para que ataquen al objetivo del taumaturgo."},
[71327] = {name = "Telaraña", description = "[q]Lanza una telaraña sobre un enemigo, inmovilizándolo durante 5 sec."},
[71330] = {name = "Tumba de hielo", description = "[q]Aturde al enemigo incapacitándolo para moverse o atacar durante 5 sec."},
[71335] = {name = "Brea ardiente", description = "[q]Lanza un trozo de brea ardiente que hace arder el área objetivo, inflige 17250 p. de daño de asedio y 7500 p. de daño de Fuego extra durante 3 sec."},
[71336] = {name = "Pacto de los Caído Oscuro", description = "[q]Inflige daño de las Sombras creciente a ti y a los aliados no vinculados cercanos. Este efecto se acaba cuando todos los objetivos vinculados se encuentran en un radio de 5 m entre ellos."},
[71337] = {name = "Choque de conmoción", description = "[q]Salta en el aire y derriba a todos los enemigos cercanos."},
[71339] = {name = "Brea ardiente", description = "[q]Lanza un trozo de brea ardiente que hace arder el área objetivo, inflige 17250 p. de daño de asedio y 7500 p. de daño de Fuego extra durante 3 sec."},
[71350] = {name = "Enfocar Fuego", description = "[q]La cría enfoca sus ataques sobre el objetivo."},
[71357] = {name = "Ordenar cría", description = "[q]Provoca que el cuidador Celador de Escarcha obligue a una cría a atacar a un enemigo específico."},
[71361] = {name = "Explosión de Escarcha", description = "[q]Inflige daño de Escarcha a un enemigo y reduce su velocidad de movimiento durante 4 sec."},
[71369] = {name = "Barrido de cola", description = "[q]Inflige 5250 to 8750 p. de daño a los enemigos que se encuentran en un cono detrás del taumaturgo y los derriba."},
[71376] = {name = "Explosión helada", description = "[q]Inflige 7875 to 10125 p. de daño de Escarcha a los enemigos que se encuentren cerca del punto de impacto."},
[71380] = {name = "Explosión helada", description = "[q]Reduce la velocidad de movimiento un 75% e inflige 7400 to 8600 p. de daño de Escarcha cada 2 s. a quienes se encuentren dentro del escalofrío gélido persistente."},
[71386] = {name = "Aliento de Escarcha", description = "[q]Inflige daño de Escarcha a los enemigos situados en un cono frente al taumaturgo, reduciendo su velocidad de ataque un 75% durante 4 sec."},
[71387] = {name = "Aura de Escarcha", description = "[q]Inflige 1200 p. de daño de Escarcha cada 2 s."},
[71393] = {name = "Llamas", description = "[q]Explota con violencia, infligiendo un mínimo de 12000 p. de daño de Fuego a los enemigos cercanos. Las bolas de llamas más grandes infligen daño adicional basado en el tamaño."},
[71400] = {name = "Esfumarse", description = "[q]Teletransporta al taumaturgo detrás del objetivo y lo libera de sus ataduras."},
[71405] = {name = "Lanza de las Sombras", description = "[q]Lanza una descarga de magia oscura a un enemigo y le inflige 19013 to 19987 p. de daño de las Sombras."},
[71410] = {name = "Puñalada", description = "[q]Inflige daño normal más 40 p. a un enemigo, pero solo si atacas por la espalda."},
[71420] = {name = "Descarga de Escarcha", description = "[q]Inflige 58500 to 61500 p. de daño de Escarcha a un enemigo y reduce su velocidad de movimiento un 50% durante 4 sec."},
[71463] = {name = "Escudo de éter", description = "[q]Protege al taumaturgo de la mayoría de los ataques y hechizos. Además, el taumaturgo regenera salud."},
[71465] = {name = "Oleada divina", description = "[q]Un pulso de energía divina que inflige 1500 p. de daño Sacroarcano cada 2 s a todos los enemigos."},
[71468] = {name = "Ráfaga de éter", description = "[q]Inflige 15000 p. de daño Sacroarcano."},
[71488] = {name = "Golpe de la Plaga", description = "[q]Un golpe profano que inflige un 200% de daño con arma como daño de las Sombras."},
[71489] = {name = "Golpe letal", description = "[q]Un ataque letal que inflige un 75% de daño con arma más 84 p. y sana al caballero de la Muerte un 20% de su salud máxima."},
[71490] = {name = "Espiral de la muerte", description = "[q]Inflige 5000 p. de daño de las Sombras a un objetivo aleatorio cercano."},
[71507] = {name = "Altamente perfumado", description = "[q]¡Uf! Qué olor tan abrumador..."},
[71516] = {name = "Infusión de Sombras", description = "[q]Succiona mocos malignos para que imbuyan a la Filo de las Sombras con Poder profano."},
[71521] = {name = "Mano de Gul'dan", description = "[q]Invoca un meteorito que cae sobre un enemigo, le inflige 1793 p. de daño de Pirosombra y crea un aura de magia en un radio de 4 m que hace que todos los objetivos en su interior tengan un 10% más de probabilidades de que cualquier mascota demoníaca del brujo les aseste un golpe crítico con sus facultades. El aura dura 15 sec."},
[71546] = {name = "Punición", description = "[q]Pune al enemigo y le inflige 7200 to 8800 p. de daño Sagrado."},
[71548] = {name = "Palabra de poder: escudo", description = "[q]Refuerza con un escudo el alma de un miembro del grupo, amortiguando 14550 p. de daño. Dura 30 sec. Mientras dura el escudo, el lanzamiento de hechizos no se verá interrumpido por el daño. Una vez protegido, el objetivo no puede recibir protección de nuevo hasta pasados 15 sec."},
[71549] = {name = "Golpe de cruzado", description = "[q]Un golpe instantáneo que inflige un 110% de daño con arma."},
[71550] = {name = "Escudo divino", description = "[q]Protege al paladín de todo el daño y hechizos durante 12 sec pero reduce el daño que inflijas un 50%. Una vez protegido, Escudo divino, Protección divina y Mano de protección no pueden proteger al objetivo de nuevo durante 1 min. No se puede usar a menos de 12 sec de haber usado Cólera vengativa."},
[71551] = {name = "Sentencia de orden", description = "[q]Juzga al enemigo y le inflige 3325 to 3675 p. de daño Sagrado."},
[71552] = {name = "Golpe mortal", description = "[q]Un golpe sanguinario que inflige daño con arma más 380 p. y hiere al objetivo, lo que reduce la eficacia de cualquier sanación un 50% durante 10 sec."},
[71553] = {name = "Cargar", description = "[q]Carga contra un enemigo y lo aturde durante 1.50 sec."},
[71554] = {name = "Hender armadura", description = "[q]Hiende la armadura del objetivo, lo que la reduce un 4% por cada uso de Hender armadura y genera una gran cantidad de amenaza. Amenaza aumentada con el poder de ataque. Se puede usar un máximo de 5 veces. Dura 30 sec."},
[71590] = {name = "Lanzar cohete", description = "[q]Lanza un potente cohete al enemigo que inflige 960 to 1440 p. de daño y lo aturde durante 3 s. Aunque hay que tener cuidado con el retroceso..."},
[71592] = {name = "Bomba de hierro vil", description = "[q]Inflige 330 to 770 p. de daño de Fuego y aturde a los objetivos en un radio de 5 m durante 2 sec. Cualquier daño anulará el efecto. No es fiable contra enemigos de nivel superior a 76."},
[71594] = {name = "Ametralladora", description = "[q]Centra las ametralladoras sobre el enemigo. La ametralladora rastreará al enemigo durante 6 sec aunque se mueva."},
[71595] = {name = "Sanación relámpago", description = "[q]Sana a un objetivo amistoso 8325 to 9675 p."},
[71603] = {name = "Fuerza mutada", description = "[q]Aumenta el daño infligido un 50% y aumenta la velocidad de ataque un 50%. También hace que los ataques provoquen que el taumaturgo mute aún más e inflija más daño cada 3 s."},
[71612] = {name = "Invocar visión de Charlga", description = nil},
[71614] = {name = "Retén de hielo", description = "[q]Encierra al objetivo en hielo, le impide actuar y provoca que sea inmune."},
[71615] = {name = "Gas lacrimógeno", description = "[q]Quedas atontado por el gas lacrimógeno, ¡que te impide moverte!"},
[71621] = {name = "Crear brebaje", description = "[q]¡El profesor Putricidio está revolviendo su mesa de laboratorio en busca de una poderosa poción!"},
[71623] = {name = "Tajo delirante", description = "[q]Inflige un 75% de daño con arma a un enemigo y lo hace sangrar 7000 to 9000 p. de daño por cada aplicación cada 3 s durante 15 sec."},
[71656] = {name = "Habló con Mankrik", description = nil},
[71657] = {name = "Descarga mortífera de huevos", description = "[q]Lanza contra los enemigos del Mecapollo una descarga mortífera de huevos explosivos que inflige 14 to 21 p. de daño de Fuego. Golpea a todos los enemigos en un rango de 5 metros del objetivo principal del Mecapollo."},
[71658] = {name = "Descarga de huevos explosivos", description = "[q]Lanza contra el Martillo una descarga mortífera de huevos explosivos que infligen 285 to 365 p. de daño de Fuego. Sistema armamentístico ofrecido por el Mecapollo."},
[71659] = {name = "Alucinante rayo láser", description = "[q]Inflige 190 to 210 p. de daño de Fuego al Martillo. ¡Cada tiburón necesita un alucinante rayo láser!"},
