[71785] = {name = "Conflagración", description = "[q]Incendia al enemigo, lo que inflige 3000 p. de daño de Fuego durante 10 sec y le hace entrar en pánico. Mientras el objetivo está en llamas, estas agostan a sus aliados cercanos periódicamente con 150 p. de daño."},
[71792] = {name = "Flema de veneno", description = "[q]Inflige 47 to 53 p. de daño de Naturaleza a un enemigo más 20 p. de daño de Naturaleza cada 3 s durante 15 sec."},
[71801] = {name = "Carga", description = "[q]Aumenta el daño infligido por el siguiente ataque cuerpo a cuerpo del taumaturgo un 50%."},
