[71807] = {name = "Chispas relumbrantes", description = "[q]Las manos del taumaturgo disparan unas chispas relumbrantes en forma de cono, que infligen a los enemigos 16720 to 18480 p. de daño de Fuego y que reducen su velocidad de movimiento un 40% durante 8 sec."},
[71812] = {name = "Descarga de las Sombras", description = "[q]Lanza una descarga de magia oscura a un enemigo y le inflige 13 to 16 p. de daño de las Sombras."},
