[71822] = {name = "Resonancia de las Sombras", description = "[q]El objetivo más cercano resuena con sombras, lo que le inflige 1000 p. de daño y reduce el daño de las Sombras recibido de todas las fuentes un 35% durante until cancelled."},
[71841] = {name = "Punición", description = "[q]Pune al enemigo infligiéndole 1591 to 1785 p. de daño Sagrado y sanando al propio taumaturgo un 25% de su salud máxima."},
[71842] = {name = "Punición", description = "[q]Pune al enemigo infligiéndole 1804 to 2022 p. de daño Sagrado y sanando al propio taumaturgo un 25% de su salud máxima."},
[71861] = {name = "Sombras enjambradoras", description = "[q]Un vacío enjambrador consume al objetivo y provoca que una masa de sombras aparezca bajo el objetivo cada 0.5 segundos."},
[71893] = {name = "Tragar pociones", description = "[q]¡El profesor Putricidio comienza a beberse todas las pociones que hay sobre la mesa!"},
[71906] = {name = "Esencia cortada", description = "[q]Arranca el alma del objetivo del cuerpo. La parte oscura del alma forma un ser sombrío que está sometido a la voluntad del taumaturgo. Dura until cancelled."},
[71921] = {name = "Salto feral", description = nil},
[71923] = {name = "Peste de sangre", description = "[q]Una enfermedad que inflige daño de las Sombras durante 12 sec."},
[71924] = {name = "Golpe de peste", description = "[q]Un golpe feroz que inflige un 60% de daño con arma más 27 p. de daño y contagia al objetivo con Peste de sangre, una enfermedad que inflige daño de las Sombras en el tiempo. El golpe elimina un efecto de sanación en el tiempo del objetivo."},
[71925] = {name = "Destrozar", description = "[q]Destroza al enemigo y le inflige un 100% de daño normal más 25 p. y provoca que el objetivo reciba un 30% de daño extra de Magullar, Triturar y los efectos de sangrado durante 12 sec. Otorga 1 p. de combo."},
[71926] = {name = "Destripar", description = "[q]Remate que inflige daño en el tiempo."},
[71927] = {name = "Disparar", description = "[q]Dispara a un enemigo y le inflige daño físico."},
[71928] = {name = "Bola de Fuego", description = "[q]Inflige daño de Fuego a un enemigo."},
[71930] = {name = "Destello de Luz", description = "[q]Invoca la magia Sagrada para sanar a un aliado."},
[71931] = {name = "Sanación superior", description = "[q]Invoca la magia Sagrada para sanar a un aliado."},
[71932] = {name = "Renovar", description = "[q]Sana al objetivo 23565 to 26435 p. de daño durante 15 sec."},
[71933] = {name = "Eviscerar", description = "[q]Remate que inflige daño."},
[71934] = {name = "Descarga de relámpagos", description = "[q]Lanza una descarga de relámpagos sobre el objetivo que le inflige 2334 to 2666 p. de daño de Naturaleza."},
[71936] = {name = "Descarga de las Sombras", description = "[q]Lanza una descarga de magia oscura a un enemigo y le inflige daño de las Sombras."},
