[71940] = {name = "Pesadillas retorcidas", description = "[q]Aflige al objetivo con pesadillas, que inflige 200 p. de daño de Naturaleza por s. Además, el objetivo regenerará 200 p. de maná cada 3 s. El daño y la sanación del objetivo también se aumentarán un 10%. Se acumula hasta 100 veces. Dura 40 sec."},
[71943] = {name = "Resonancia de las Sombras", description = "[q]Invoca un núcleo oscuro. Los núcleos oscuros se alimentan de su propia energía lo que causa que se destruyan con el tiempo."},
[71951] = {name = "Golpe necrótico", description = "[q]Golpea a un enemigo con una hoja maldita que inflige 4625 to 5375 p. de daño físico al objetivo. También le inflige un mal duradero que anula los siguientes 15000 p. de sanación recibidos."},
[71953] = {name = "Aura de radiancia", description = "[q]El paladín titila con una Luz resplandeciente y sana un 3% de su salud máxima a los aliados cercanos cada 3 s."},
[71955] = {name = "Ataques centrados", description = "[q]Salta sobre la espalda del objetivo durante 30 sec y aumenta la velocidad de ataque, pero imposibilita que ataque a otro objetivo."},
[71956] = {name = "Lluvias reponedoras", description = "[q]Regenera a todos los miembros del grupo cercanos 13875 to 16125 p. cada 2 s durante 10 sec. Canalizado."},
[71960] = {name = "Salto heroico", description = "[q]Salta en el aire y golpea a todos los enemigos en un radio de 8 m del área objetivo, lo que inflige un daño y los aturde durante 2 sec."},
[71965] = {name = "Lluvia de caos", description = "[q]Invoca una lluvia de magma que quema a todos los enemigos de un área seleccionada e inflige 142 to 157 p. de daño de Caos cada 3 s durante 9 sec."},
[71973] = {name = "Flema de serpiente", description = "[q]Inflige daño de Fuego al objetivo."},
[71974] = {name = "Guardián serpiente", description = "[q]Invoca un Tótem abrasador con 5 p. de salud a tus pies durante 1 min que ataca repetidamente a un enemigo que esté en un radio de 0 m e inflige 5 p. de daño de Fuego."},
[71976] = {name = "Corriente de sanación", description = "[q]Sana 50 p. cada 2 s."},
[71984] = {name = "Tótem Corriente de sanación", description = "[q]Invoca un Tótem Corriente de sanación con 5 p. de salud a los pies del taumaturgo durante 10 min. El tótem sana a los miembros del grupo que se encuentran en un radio de 60 metros durante 50 cada 2 segundos."},
[71993] = {name = "Marra congelada", description = "[q]¡Los ataques tienen una probabilidad de aplicar Mordedura de Escarcha! Mordedura de Escarcha inflige 2438 to 2562 p. de daño cada 2 s durante 20 sec."},
[72004] = {name = "Mordisquielo", description = "[q]Mordisquielo inflige 2438 to 2562 p. de daño de Escarcha cada 2 s y reduce la velocidad de movimiento un 5% durante 20 sec."},
[72013] = {name = "Cañón de Piedra Ígnea", description = "[q]Dispara una potente bola de cañón al enemigo."},
[72014] = {name = "Sanación en cadena", description = "[q]Imbuye a un aliado herido con energía de sanación que se extiende a otro aliado cercano. El hechizo afecta hasta a 3 objetivos, pero la energía de sanación disminuye un 50% en cada objetivo sucesivo."},
[72034] = {name = "Tormenta de nieve", description = "[q]Inflige 13163 to 13837 p. de daño de Escarcha a todos los enemigos cercanos y aumenta el daño de Escarcha recibido un 15%."},
[72037] = {name = "Vórtice de choque", description = "[q]Crea un vórtice con fuerza de torbellino cerca de un objetivo enemigo que inflige daño y derriba a los enemigos que se encuentran cerca."},
[72039] = {name = "Vórtice de choque potenciado", description = "[q]Crea vórtices de fuerza sobre todos los enemigos cercanos, inflige 5000 p. de daño físico y derriba a los enemigos que se encuentran cerca del objetivo, lanzándolos lejos."},
[72040] = {name = "Crear llama potenciada", description = "[q]Crea una bola de llamas potenciada que vuela hacia el objetivo y explota al impactar."},
