[72050] = {name = "Roca ígnea", description = "[q]Inflige daño a los enemigos en un radio de 5 m del área objetivo."},
[72052] = {name = "Bomba cinética", description = "[q]Explota al impactar con el suelo, e inflige 18000 to 22000 p. de daño físico y derriba a los enemigos en un área amplia."},
[72055] = {name = "Escudo de absorción", description = "[q]Utiliza los poderes de las regiones abisales para proteger al taumaturgo, absorbiendo 50 p. de daño. Dura 5 min. Mientras el escudo esté activo, el lanzamiento de hechizos no se verá interrumpido por el daño."},
[72057] = {name = "Tunda exánime", description = "[q]Abofetea al objetivo con una mano fría, inflige 10500 to 13500 p. de daño de las Sombras y lo derriba."},
