[72066] = {name = "Sudario de amparo de hechizos", description = "[q]Protege al miembro del grupo elegido de los ataques mágicos durante 6 sec."},
[72069] = {name = "Soldado de la Batalla por la Ciudad de Gilneas", description = "[q]Más rápido, mejor y más fuerte."},
[72090] = {name = "Tierra helada", description = "[q]Inflige daño de Escarcha a los enemigos cercanos y los inmoviliza durante un máximo de 5 sec."},
[72091] = {name = "Orbe congelado", description = "[q]Crea un orbe congelado en una ubicación cercana."},
[72114] = {name = "Disparo de puntería", description = "[q]Un disparo de puntería que aumenta el daño a distancia 0 p."},
[72120] = {name = "Mordisquielo", description = "[q]Mordisquielo inflige 1950 to 2050 p. de daño de Escarcha cada 2 s y reduce la velocidad de movimiento un 5% durante 10 sec."},
[72122] = {name = "Marra congelada", description = "[q]¡Los ataques tienen una probabilidad de aplicar Mordedura de Escarcha! Mordedura de Escarcha inflige 1950 to 2050 p. de daño cada 2 s durante 10 sec."},
