[72133] = {name = "Dolor y sufrimiento", description = "[q]Inflige 7540 to 8460 p. de daño de las Sombras en un efecto de cono frente al taumaturgo. Además, los objetivos recibirán 1200 p. de daño de las Sombras por segundo durante 3 sec."},
[72143] = {name = "Enfurecer", description = "[q]Aumenta el daño físico un 200%."},
