[77509] = {name = "Añadir huevo", description = "[q]Añadiendo huevo."},
[77510] = {name = "Añadir anca de carnero", description = "[q]Añadiendo anca de carnero."},
[77511] = {name = "Añadir queso", description = "[q]Añadiendo queso del Alud."},
[77517] = {name = "Toxina de temeridad", description = "[q]Envenena al enemigo, lo que reduce la probabilidad de esquivar y parar un 10%. Aumenta la velocidad de movimiento un 15%."},
[77522] = {name = "Descenso en picado", description = "[q]Inflige un 50% de daño con arma a un enemigo y lo derriba una distancia corta."},
