[77553] = {name = "Invocar trueno encadenado", description = "[q]Invoca un trueno encadenado que acompañe al taumaturgo durante 10 sec."},
[77556] = {name = "Tormenta de truenos", description = "[q]Invoca una descarga de relámpagos que inflige daño a los enemigos cercanos en un radio de 10 m. Inflige 94 to 106 p. de daño de Naturaleza a todos los enemigos cercanos, los derriba y destruye el Trueno encadenado."},
[77558] = {name = "Golpe de sangre", description = "[q]Un ataque instantáneo que inflige un 110% de daño con arma. Aumenta el daño con ataques un 5%, pero disminuye la probabilidad de golpear un 5%. Dura 15 sec."},
[77567] = {name = "Marca de los Magram", description = "[q]Deja la Marca de los Magram en el objetivo, lo que aumenta el poder de ataque a distancia de todos los que ataquen al objetivo 20 p. Además, el objetivo de esta facultad seguirá siendo visible para el rastreador aunque esté en modo Sigilo o se vuelva invisible. Dura 15 sec."},
[77568] = {name = "Rotura craneal", description = "[q]Reduce el aguante de un enemigo durante 18 sec."},
[77569] = {name = "Liberar aberraciones", description = "[q]Libera a 3 aberraciones de sus cámaras de crecimiento, lo que provoca que ataquen a todos los enemigos en la zona."},
[77570] = {name = "Descarga de las Sombras", description = "[q]Lanza una descarga de magia oscura a un enemigo y le inflige daño de las Sombras."},
[77576] = {name = "Aliento de piedra", description = "[q]Inflige daño de Naturaleza a los enemigos que se encuentran en un cono frente al taumaturgo y los aturde durante 2 sec."},
[77581] = {name = "Ritual del amuleto", description = "[q]Lleva a cabo un ritual de sangre, lo que lleva al objetivo a la guarida de Razelikh."},
[77593] = {name = "¡Revitalizado!", description = "[q]El enemigo regenera salud y maná a un ritmo acelerado."},
[77603] = {name = "Desarraigar retoños", description = "[q]Arranca tres retoños para que ayuden al taumaturgo en combate."},
[77611] = {name = "Fragor resonador", description = "[q]El sonido resonador del escudo enano antiguo interrumpe el lanzamiento del dragón ciego."},
[77612] = {name = "Modulación", description = "[q]El dragón ciego empieza a lanzar Modulación, lo que inflige 48750 to 51250 p. de daño de las Sombras, lo que inflige un daño extra que se basa en el sonido del jugador."},
[77626] = {name = "Descarga de represión", description = "[q]Debilita la resolución de Nespirah."},
[77627] = {name = "Invocar esbirro ígneo", description = "[q]Invoca un esbirro ígneo para que ayude a arrasar Hyjal."},
[77628] = {name = "Rescatar a druida joven", description = "[q]Agarra a un druida joven indefenso de la Arboleda de Thal'darah."},
[77629] = {name = "Dejar a druida joven", description = "[q]Deja a un druida joven en la estación de grifos en el Mirador Thal'darah."},
[77645] = {name = "Sangre vil", description = "[q]El enemigo está cubierto de sangre vil, lo que aumenta el daño recibido de Fuego y Sombras y le permite oír los susurros de Sargeras."},
