[77721] = {name = "Tejer sombras", description = "[q]Lanza una descarga de magia oscura a un enemigo y le inflige daño de las Sombras."},
[77722] = {name = "Látigo de vacío", description = "[q]Lanza un zarcillo de las Sombras al enemigo, lo que inflige daño de las Sombras."},
