[77760] = {name = "Escalofrío cortante", description = "[q]Rodea a un objetivo jugador aleatorio con un círculo de escarcha que inflige 10000 p. de daño de Escarcha al objetivo y a cualquier amigo del objetivo a 3 m. Dura 10 sec."},
