[77777] = {name = "Furia de arrabio", description = "[q]Némesis explota con la furia volcánica de cien soles e inflige daño a todos los enemigos en un radio de 40 m."},
[77780] = {name = "Sigilo de asaltante Peloescarcha", description = "[q]Permite al pícaro merodear sin ser visto pero reduce su velocidad un 40%. Dura hasta que se cancela."},
[77786] = {name = "Llamas acuciantes", description = "[q]Prende en llamas a un objetivo aleatorio, lo que inflige 13500 p. de daño de Fuego por s. Cualquier daño mágico adicional recibido por el objetivo aumenta el daño infligido por este efecto un 50% del daño recibido. Dura 10 sec."},
[77787] = {name = "Martillo de Justicia", description = "[q]Aturde al enemigo incapacitándolo para moverse o atacar durante 4 sec."},
[77789] = {name = "Fuego de pantano", description = nil},
