[77808] = {name = "Grito de batalla", description = "[q]Aumenta el poder de ataque cuerpo a cuerpo de los objetivos amistosos cercanos durante 2 min."},
[77826] = {name = "Aliento de Pirosombra", description = "[q]Inflige 54000 p. de daño de Pirosombra a los enemigos en un cono frente al taumaturgo. También resucita a todos los guerreros osarios animados que se encuentran el área de efecto."},
[77827] = {name = "Latigazo de cola", description = "[q]Un barrido de cola golpea a todos los enemigos que se encuentren detrás del taumaturgo, lo que inflige 61250 to 78750 p. de daño y los aturde durante 2 sec."},
[77840] = {name = "Llamas abrasadoras", description = "[q]Las llamas infligen 39506 to 40493 p. de daño de Fuego y aumentan el daño de Fuego recibido un 50% durante 6 sec. Se acumula."},
