[78223] = {name = "Cero absoluto", description = "[q]Invoca una esfera de energía ultracongelante cerca de un objetivo aleatorio. Si la esfera entra en un radio de 3 m de un enemigo explota, inflige 94250 to 105750 p. de daño de Escarcha a todos los enemigos en un radio de 5 m y los derriba."},
[78225] = {name = "Nova de ácido", description = "[q]Inflige 15000 p. de daño de Naturaleza a todos los enemigos durante 10 sec."},
[78295] = {name = "Explosión de cohetes", description = "[q]Una bala, una baja."},
[78296] = {name = "Comer naga", description = "[q]Provoca que el gran tiburón dominado devore salvajemente a un guardián Zin'jatar cercano en los Rediles de Encierro."},
