[78371] = {name = "Lanzar roca", description = "[q]Lanza una roca enorme lejos que inflige 1350 to 1650 p. de daño a los enemigos en un radio de 8 m. de la explosión."},
[78372] = {name = "Embestir", description = "[q]Embiste a los enemigos frente al demoledor, inflige 1900 to 2100 p. de daño y los derriba."},
[78397] = {name = "Red a prueba de fuego", description = "[q]Inmoviliza a un enemigo durante 2 sec."},
[78403] = {name = "Rabieta de arrabio", description = "[q]Al no tener a nadie cerca a quien atacar, Faucemagma entra en frenesí. Aumenta el daño de Fuego infligido un 100%. Se acumula 10 veces."},
