[78508] = {name = "Marca de las llamas", description = "[q]Aumenta 10 p. el daño de Fuego infligido a un enemigo durante 30 sec."},
[78509] = {name = "Lanzamiento de antorcha", description = "[q]Lanza una antorcha que inflige daño de Fuego y quema al objetivo, lo que inflige daño de Fuego extra durante 10 sec."},
