[78521] = {name = "Aliento de dragón", description = "[q]Los objetivos situados en un cono frente al taumaturgo reciben daño de Fuego y quedan desorientados durante 1.50 sec. Cualquier ataque directo que inflija daño reanimará a los objetivos."},
