[78535] = {name = "Llamada de la tumba", description = "[q]Inflige 20 p. de daño de las Sombras a los enemigos cercanos después de 15 s."},
[78542] = {name = "Salpicadura", description = "[q]Una salpicadura de agua empapa a todos los enemigos cercanos, lo que les inflige daño de Escarcha y los repele a una corta distancia."},
[78578] = {name = "Trampa de inmolación", description = "[q]Coloca una trampa de Fuego que quemará al primer enemigo que se acerque, lo que le infligirá daño de Fuego durante 15 sec. La trampa dura 1 min."},
[78619] = {name = "Hijos de Alamuerte", description = "[q]Nefarian y Onyxia atacan un 100% más rápido cuando están juntos."},
[78620] = {name = "Hijos de Alamuerte", description = "[q]Nefarian y Onyxia atacan un 100% más rápido cuando están juntos."},
[78621] = {name = "Tromba de Pirosombra", description = "[q]Inflige 36000 to 44000 p. de daño de Pirosombra a un enemigo."},
[78622] = {name = "Salto heroico", description = "[q]Salta en el aire y golpea a todos los enemigos en un radio de 8 m del área objetivo, lo que inflige daño físico."},
