[78754] = {name = "Disparo Arcano", description = "[q]Dispara a un enemigo y le inflige daño Arcano."},
[78770] = {name = "Tótem de Magma", description = "[q]Invoca un Tótem de Magma con 5 p. de salud a los pies del taumaturgo durante 20 s que inflige daño de Fuego a las criaturas que están en un radio de 8 m cada 2 s."},
[78799] = {name = "Colisión de ola", description = "[q]Inflige daño de Fuego y derriba al objetivo."},
[78801] = {name = "Descarga de agua", description = "[q]Lanza una descarga de agua a un enemigo, lo que inflige daño de Escarcha moderado."},
[78802] = {name = "Ola aplastante", description = "[q]Invocas un violento Tifón que inflige 508 to 591 p. de daño de Naturaleza cuando entra en contacto con objetivos hostiles y los repele 5 m."},
[78807] = {name = "Trizar", description = "[q]Lanza las púas que sobresalen de Ozruk a los enemigos circundantes, lo que inflige 188500 to 211500 p. de daño a todos los enemigos en un radio de 15 m."},
[78808] = {name = "Rayo de sol", description = "[q]Inflige daño de Fuego a los enemigos en un radio de 8 m del objetivo."},
[78810] = {name = "Incandescente", description = "[q]Carbones incandescentes queman a los atacantes. Dura 10 min."},
[78823] = {name = "Grito de orden", description = "[q]Aumenta 50 p. la salud máxima de todos los miembros del grupo situados en un radio de 20 m. Dura 2 min."},
[78826] = {name = "Evasión", description = "[q]Aumenta la probabilidad de esquivar del taumaturgo un 25%. Dura 6 sec."},
[78827] = {name = "Filotormenta", description = "[q]En un torbellino de acero atacas a todos los enemigos en un radio de 8 m e infliges daño con ambas armas cuerpo a cuerpo a cada enemigo."},
[78835] = {name = "Escudo de púas de elementium", description = "[q]Provoca que salgan púas del cuerpo de Ozruk. Todo ataque físico contra Ozruk mientras esté bajo este efecto provocará que el atacante sangre por valor de 0 p. de daño por segundo durante 5 sec. Este efecto se acumula hasta 5 veces y dura 10 sec."},
[78841] = {name = "Disparo de puntería", description = "[q]Un disparo de puntería que inflige un 100% de daño con arma y reduce la sanación realizada a ese objetivo un 50%. Dura 10 sec."},
[78842] = {name = "Mordedura carnívora", description = "[q]Inflige daño físico a un enemigo cada 3 s durante 15 sec."},
[78855] = {name = "Portador de la bandera de Ventormenta", description = nil},
[78867] = {name = "Fetidez sobrecogedora", description = "[q]Reduce 100 p. el intelecto de un enemigo durante 10 sec."},
[78894] = {name = "Mella con salto", description = "[q]Salta hacia el objetivo, lo que inflige un 100% de daño con arma a un enemigo y a sus dos aliados más cercanos."},
[78895] = {name = "Imbuido en Escarcha", description = nil},
[78896] = {name = "Imbuido en fuego", description = nil},
[78903] = {name = "Embate en el suelo", description = "[q]Se prepara para un fuerte golpe contra el suelo que inflige 94250 to 105750 p. de daño a todos los enemigos en un radio de 4 m del punto de impacto."},
[78904] = {name = "Tierra agostada", description = "[q]Hace explotar la tierra."},
[78907] = {name = "Fuego estelar", description = "[q]Inflige daño Arcano al objetivo."},
[78921] = {name = "Látigo de sueño", description = "[q]Inflige 33 to 38 p. de daño de Naturaleza."},
[78939] = {name = "Baluarte de elementium", description = "[q]Carga las placas de elementium injertadas en Ozruk, lo que provoca que tengan un 100% de probabilidad de reflejar hechizos durante 10 sec."},
[78941] = {name = "Infección parasitaria", description = "[q]Inflige 23125 to 26875 p. de daño cada 2 s. Parásitos de lava infecciosos se introducen en ti y se multiplican entre las sublimes capas de tus entrañas."},
[78943] = {name = "Enfurecer", description = "[q]Reduce el tiempo entre tus ataques un 50% durante 1 min."},
[78965] = {name = "Toque de ternura", description = "[q]Invoca la magia Sagrada para sanar a un aliado."},
[78968] = {name = "Fatalidad de agua", description = "[q]Condena a una muerte acuosa a los devotos Crepusculares y a los sobrestantes ignotos en un radio de 40 m e inflige 18000 to 22000 p. de daño."},
[78972] = {name = "Vórtice", description = "[q]Crea un vórtice con fuerza de torbellino que traga a los devotos Crepusculares y sobrestantes ignotos en un radio de 40 m del taumaturgo."},
[78986] = {name = "Escudo de Marl", description = "[q]Protege a Marl Gusaespina."},
[79002] = {name = "Fragmento de seísmo", description = "[q]Arranca un poco de tierra del suelo y lo suelta hacia la ubicación de un jugador aleatorio. Cuando la roca impacta en la ubicación, cualquier unidad amiga o enemiga en un radio de 3.5 m recibirá 942500 to 1057500 p. de daño."},
[79010] = {name = "Punto vulnerable", description = "[q]¡El lado tierno de la parte trasera de la cabeza de Faucemagma está expuesto!\n El daño infligido a esa zona aumenta un 100%.\n La cabeza y el cuerpo comparten salud."},
[79023] = {name = "Medida de seguridad de incineración", description = "[q]La medida defensiva inflige 14625 to 15375 p. de daño de Fuego cada 1 s durante 4 sec."},
[79050] = {name = "Escudo de energía", description = "[q]Inflige 47500 to 52500 p. de daño Arcano a todos los enemigos en un radio de 5 m y los derriba. Además, un escudo de energía rodea al taumaturgo. Este escudo reduce todo el daño recibido un 75% y permite volar al taumaturgo."},
[79058] = {name = "Luminosidad Arcana", description = "[q]Infunde luminosidad a todos los miembros del grupo o banda, lo que aumenta 2126 p. su maná máximo y un 6 % su poder con hechizos durante 1 hour. Si el objetivo pertenece a tu grupo o banda, todos los miembros del grupo o banda se verán afectados."},
[79060] = {name = "Marca de lo Salvaje", description = "[q]Aumenta la fuerza, la agilidad, el aguante y el intelecto del objetivo amistoso un 5%, y todas sus resistencias mágicas [( 85 <= 70 ? 85 / 2 : ( 85 <= 80 ? 85 / 2 + ( 85 - 70) / 2 * 5 : 85 / 2 + ( 85 - 70) / 2 * 5 + ( 85 - 80) / 2 * 7)) - 0.5] p. durante 1 hour. Si el objetivo está en tu grupo o banda, todos los miembros del grupo o banda se verán afectados."},
[79085] = {name = "Descarga de relámpagos", description = "[q]Golpea a un objetivo con relámpagos, lo que le inflige daño de Naturaleza."},
[79086] = {name = "Cadena de relámpagos", description = "[q]Golpea al enemigo con una Descarga de relámpagos que se extiende a otro enemigo cercano. Afecta a un máximo de 3 objetivos causando daño de Naturaleza a cada uno."},
[79094] = {name = "Drenar alma", description = "[q]Drena el alma del objetivo, lo que inflige daño de las Sombras cada 1 s durante 5 sec."},
[79109] = {name = "Vórtice de arena", description = nil},
[79115] = {name = "Oscuridad ancestral", description = "[q]Lanza contra un enemigo un misil de oscuridad, lo que inflige daño de las Sombras."},
[79116] = {name = "Cachava de pordiosero", description = "[q]¿Qué habrá en la bolsa?"},
[79129] = {name = "Encogimiento", description = "[q]Provoca que un horror de tentáculos sufra un encogimiento masivo. El radio es de 55 m."},
[79130] = {name = "Congelación profunda", description = "[q]Inflige daño de Escarcha a los enemigos cercanos y los inmoviliza durante un máximo de 1 min."},
[79174] = {name = "Pulverizador de arena", description = "[q]Inflige daño a los enemigos que estén en un cono frente al taumaturgo, reduce su movimiento y aumenta el tiempo entre sus ataques durante 3 sec."},
[79175] = {name = "Cortar y rebanar", description = "[q]Corta, rebana y cercena a tu enemigo durante 5 sec."},
[79187] = {name = "Disparo multisombras", description = "[q]Dispara contra un enemigo y contra sus aliados cercanos una salva de misiles que inflige daño de las Sombras."},
[79203] = {name = "Ahuyentada", description = "[q]Marion Alagusano se huele algo."},
[79213] = {name = "Consumir esencia", description = "[q]Consume al sobrestante ignoto más cercano, lo que lo mata y transfiere el 200% de su salud al taumaturgo."},
[79222] = {name = "Explosión acuática", description = "[q]Condena a una muerte acuosa a Hallazeal y a sus zelotes ascendidos e inflige 18000 to 22000 p. de daño."},
[79223] = {name = "Salpicadura de conmoción", description = "[q]Interrumpe a todos los enemigos en un radio de 30 m de Hallazeal."},
[79232] = {name = "Himno de Sira'kess", description = "[q]Aumenta la velocidad de lanzamiento y de ataque un 25% y el daño físico que inflige un 25%."},
[79318] = {name = "Dominio", description = "[q]Domina las mentes de varios humanoides cercanos y se hace con el control de sus cuerpos durante 20 sec."},
[79330] = {name = "Potenciar", description = "[q]El taumaturgo se vuelve más poderoso con el tiempo, lo que aumenta el daño infligido y la velocidad de movimiento. Se acumula."},
[79339] = {name = "Cenizas explosivas", description = "[q]Quema a varios enemigos al azar y los cubre con un residuo explosivo que inflige daño de Fuego periódico. El residuo detona tras 8 sec e inflige 42750 to 47250 p. de daño de Fuego en un área pequeña."},
[79340] = {name = "Invocar pozo de gravedad", description = "[q]Invoca un área de gravedad aplastante. Todas las unidades amistosas o enemigas en un radio de 10 se verán atraídas hacia el centro. Este hechizo inflige daño persistente a todos los enemigos al alcance; inflige más daño cuanto más cerca esté el objetivo del centro del efecto. Dura 20 s."},
[79345] = {name = "Maldición de sangre", description = "[q]Aumenta el daño físico recibido del objetivo 5000 p. durante 15 sec."},
[79351] = {name = "Agarre de fuerza", description = "[q]Captura al objetivo en un campo de energía, lo alza en el aire y lo lanza contra el suelo. Cada vez que el objetivo golpea el suelo recibe 70687 to 79312 p. de daño. Este daño no se reduce con la armadura. Dura 5 sec."},
[79353] = {name = "Sombra de cobardía", description = "[q]Inflige 30000 p. de daño de Pirosombra a un enemigo y aumenta un 100% el daño de las Sombras recibido."},
[79356] = {name = "Babosa serpenteante", description = "[q]Cubre al objetivo de moco, lo que provoca que se mueva más deprisa en el agua pero inflige daño de Naturaleza cada segundo."},
[79379] = {name = "Azote de bala de cañón", description = "[q]Inflige un 300% de daño con arma."},
[79384] = {name = "Embate de mar", description = "[q]Embiste hacia delante, lo que inflige un 300% del daño con arma y carga a través de los enemigos cercanos."},
[79400] = {name = "Veneno de Escamasalada", description = "[q]Reduce la fuerza y el intelecto 700 p."},
[79403] = {name = "Escabullirse", description = "[q]Pone al taumaturgo en modo sigilo pero ralentiza su movimiento un 50%. Dura hasta que se cancela."},
[79411] = {name = "Aguas hediondas", description = "[q]Corrompe el agua alrededor del objetivo, lo que inflige daño de las Sombras cada segundo durante 15 sec."},
[79413] = {name = "Secreto brillante", description = "[q]Inflige daño físico a un enemigo."},
[79414] = {name = "Toque frágil", description = "[q]Sin armadura."},
[79417] = {name = "Mordedura tóxica", description = "[q]Inflige daño de Naturaleza cada segundo durante 10 sec."},
[79420] = {name = "Frenesí", description = "[q]Se pone a nadar frenéticamente alrededor del objetivo."},
[79425] = {name = "Arremetida segadora", description = "[q]Corta, rebana y cercena a tu enemigo durante 5 sec."},
[79429] = {name = "Tótem de resistencia elemental", description = "[q]Invoca un tótem de resistencia elemental."},
[79430] = {name = "Propulsión con cohete", description = "[q]Activa los cohetes propulsores del gólem de la cosecha sobrecargado, lo que aumenta su velocidad un 100% durante 5 sec y pasa por encima de todo lo que se ponga en su camino."},
[79431] = {name = "Tótem Lengua de Fuego", description = "[q]Invoca un tótem de resistencia elemental."},
[79441] = {name = "Vórtice de arena", description = nil},
[79442] = {name = "Quijaforte", description = "[q]Un fuerte ataque que inflige un 300% de daño."},
[79443] = {name = "Paso de arena", description = "[q]Sacude la arena cerca del taumaturgo, lo que lo hace desaparecer durante 1 sec."},
[79444] = {name = "Empalar", description = "[q]Lanza una lanza al objetivo, lo que inflige un 188 to 211% de daño y daño extra cada 3 s durante 9 sec."},
[79449] = {name = "Embate corporal", description = "[q]Golpea al objetivo con el cuerpo. Le inflige daño igual a un 15% de la salud máxima y desplaza al objetivo y al taumaturgo a una pequeña distancia."},
[79467] = {name = "Alivio umbrío", description = "[q]Sana al objetivo un 40% de su salud total."},
[79479] = {name = "Golpe del Punzacarne", description = "[q]Inflige un 1000% de daño con arma a todos los enemigos al alcance."},
[79482] = {name = "Sobrecargado", description = "[q]El increíble peso de esta arma ha reducido su velocidad de movimiento un 50%."},
[79487] = {name = "Rueda rotunda", description = "[q]Lanzas tu escudo al enemigo, lo que le inflige daño con arma y lo aturde durante 1 sec."},
[79490] = {name = "Mordedura enfermiza", description = "[q]Aumenta el tiempo entre los ataques de un enemigo un 54% y su velocidad de movimiento un 25% durante 10 sec."},
[79501] = {name = "Fijando objetivo", description = "[q]Fijando un objetivo. Después de 4 sec Magmatron comenzará a canalizar su Lanzallamas contra el objetivo, lo que inflige 63375 to 66625 p. de daño de Fuego cada 1 s a todos los enemigos que se encuentren frente a él durante 4 sec."},
[79503] = {name = "Aluvión", description = "[q]Reduce el daño físico infligido por el taumaturgo 50 p. Aumenta la velocidad de ataque cuerpo a cuerpo un 200%."},
[79505] = {name = "Lanzallamas", description = "[q]¡El lanzallamas se ha hecho con un nuevo objetivo!"},
[79507] = {name = "Poderío de los Kvaldir", description = "[q]Aumenta el daño físico infligido por el taumaturgo durante 15 sec."},
[79508] = {name = "Tormenta de arena", description = "[q]Inflige daño de Naturaleza a los enemigos cercanos y los repele."},
[79510] = {name = "Tormenta de arena", description = "[q]Crea una Tormenta de arena bajo un objetivo enemigo."},
[79515] = {name = "Excavar", description = "[q]Desaparece bajo el fondo arenoso del océano."},
[79516] = {name = "Erupción", description = "[q]Explota desde la arena, daña a los enemigos y los derriba."},
[79537] = {name = "Cuerda estranguladora", description = "[q]Estrangula al objetivo con una cuerda, lo que provoca que reciba daño igual a un 10% de su salud máxima cada 2 s durante 6 sec."},
[79539] = {name = "Ola aplastante", description = "[q]Invocas una violenta ola que inflige daño de Escarcha cuando entra en contacto con objetivos hostiles, lo que los derriba 5 m."},
[79542] = {name = "Cautivar", description = "[q]Cautiva al objetivo, lo aturde durante 4 sec y le obliga a bailar."},
[79544] = {name = "Sobrecarga psiónica", description = "[q]Golpea al enemigo con un haz de energía mental que inflige daño de las Sombras."},
[79546] = {name = "¡Ataque de ancla!", description = "[q]Lanza una cuerda y ancla al objetivo, lo que lo clava al fondo del mar durante un breve período de tiempo."},
[79554] = {name = "Bofetada con pez", description = "[q]Abofetea al objetivo con tu pez e inflige un 110% de daño con arma."},
[79556] = {name = "Lanzamiento de pez", description = "[q]Lanza tu pez al objetivo, lo que inflige un 200% de daño con arma."},
[79558] = {name = "Bioluminiscencia", description = "[q]Lanza una ola explosiva de magia e inflige daño Arcano a los enemigos cercanos."},
[79559] = {name = "Luz Escama Luminosa", description = "[q]Reduce un 20% la probabilidad de esquivar y un 10% la agilidad de un enemigo durante 1 min. Mientras esté afectado, el objetivo no puede utilizar Sigilo ni Invisibilidad."},
[79560] = {name = "Mordedura aplastante", description = "[q]Muerde la armadura de un enemigo y la reduce con Hender armadura. Se puede aplicar hasta 5 veces. Dura 20 sec."},
[79561] = {name = "Manía sangrienta", description = "[q]El sabor de la sangre enfurece al taumaturgo, lo que aumenta el daño un 5% durante 30 sec."},
[79563] = {name = "Armadura de hielo", description = "[q]Aumenta la armadura 2000 p. y la resistencia a la Escarcha 50 p. Si un enemigo golpea al taumaturgo, su movimiento puede verse ralentizado un 30% y los ataques ralentizados un 20% durante 5 sec. Un mago solo puede tener un tipo de hechizo de armadura activo a la vez. Dura 30 min."},
[79564] = {name = "Bola de Fuego Crepuscular", description = "[q]Inflige daño de Fuego a un enemigo."},
[79565] = {name = "Ola de choque Crepuscular", description = "[q]Desata una ola de llamas que inflige daño de Fuego a los enemigos cercanos y reduce su velocidad de movimiento durante 6 sec."},
[79566] = {name = "Aspersión", description = "[q]Lanza agua en todas direcciones, lo que inflige daño de Escarcha a los enemigos cercanos y los repele."},
[79571] = {name = "Cono de frío", description = "[q]Inflige daño de Escarcha a los enemigos situados en un cono frente al taumaturgo y reduce su velocidad de movimiento durante 4 sec."},
[79580] = {name = "Machaque aéreo", description = "[q]Inflige 8750 to 11250 p. de daño a todos los enemigos en un radio de un cono de 20 m frente al taumaturgo y los derriba."},
[79581] = {name = "Bufido de enfado", description = "[q]Desata un bufido de enfado, lo que inflige daño de Naturaleza y derriba al objetivo a una distancia corta."},
[79582] = {name = "Barrera", description = "[q]Amortigua 450000 p. de daño. Si la barrera se rompe, desata un vendaval de llamas que inflige 151125 to 158875 p. de daño de Fuego a todos los enemigos."},
[79583] = {name = "Atracción empaladora", description = "[q]Lanza tu tridente a un objetivo y lo atrae hacia ti, lo que inflige 24 to 29 p. de daño físico y provoca al enemigo."},
[79584] = {name = "Abanico de cuchillos", description = "[q]Lanza cuchillos en todas direcciones que infligen daño físico a los enemigos cercanos."},
[79589] = {name = "Cadenas constrictoras", description = "[q]Reviste a los objetivos de cadenas, lo que inflige 18500 to 21500 p. de daño físico cada 1 s y aturde al objetivo durante 5 sec."},
[79604] = {name = "Atronar", description = "[q]Inflige a todos los enemigos en un radio de 15 m 63750 to 86250 p. de daño de Naturaleza y ralentiza la velocidad de ataque un 20%."},
[79607] = {name = "Salpicadura de tósigo", description = "[q]Inflige daño de Naturaleza a todos los enemigos en el veneno. Dura 10 sec."},
[79610] = {name = "Disparar", description = "[q]Dispara a un enemigo y le inflige daño físico."},
[79615] = {name = "Esfumarse", description = nil},
[79616] = {name = "Drenar vida", description = "[q]Drena salud de un enemigo durante 5 sec y se la transfiere al taumaturgo."},
[79618] = {name = "Aliados de VanCleef", description = nil},
[79624] = {name = "Generador de poder", description = "[q]Invoca un generador de poder debajo de un objetivo amistoso aleatorio. Esto aumenta un 50% el daño de todos los objetivos amistosos y enemigos en un radio de 5 m. Dura 1 min."},
[79628] = {name = "Generador de poder", description = "[q]Invoca un generador de poder debajo de un objetivo amistoso aleatorio. Esto aumenta un 50% el daño de todos los objetivos amistosos y enemigos en un radio de 5 m. Dura 1 min."},
[79630] = {name = "Carga dracónida", description = "[q]Carga contra el enemigo más lejano en un radio de 80 m, lo que lo aturde durante 5 sec."},
[79673] = {name = "Zona de vacío", description = "[q]Invoca una zona de vacío que inflige daño de las Sombras a los enemigos que se encuentran en su radio."},
[79675] = {name = "Batalla mental", description = "[q]Obliga al objetivo a luchar consigo mismo."},
[79687] = {name = "Mella hendida", description = "[q]Inflige el daño normal más 5 p. a un enemigo y a sus aliados cercanos. Afecta hasta a 3 objetivos. Los golpes también reducen la armadura 50 p."},
[79710] = {name = "Aniquilador Arcano", description = "[q]Inflige 78000 to 82000 p. de daño Arcano a un objetivo enemigo aleatorio."},
[79719] = {name = "Localizar", description = "[q]Localiza el emplazamiento del objetivo, lo que aumenta el poder de ataque a distancia de todos los atacantes contra ese objetivo 20 p. Además, el objetivo de esta facultad seguirá siendo visible para el tirador aunque esté en modo Sigilo o se vuelva invisible. Dura 30 sec."},
[79721] = {name = "Disparo explosivo", description = "[q]Inflige daño con arma y daño de Fuego extra a un enemigo y a cualquiera de sus aliados cercanos."},
[79726] = {name = "Bomba de relojería que hace tic tac", description = "[q]Explota tras unos segundos e inflige daño de Fuego. Tic-tac."},
[79728] = {name = "Deflagración", description = "[q]Inflige daño de Fuego a los enemigos cercanos."},
[79729] = {name = "Conversión de poder", description = "[q]Conversión de poder convierte el daño contra el objetivo en daño mágico extra y velocidad de lanzamiento."},
[79732] = {name = "Azote de escudo", description = "[q]Azota al enemigo con el escudo del taumaturgo, lo que inflige un 10% de daño con arma y derriba al enemigo."},
[79733] = {name = "Grito de orden", description = "[q]Aumenta 20 p. la salud máxima de todos los miembros del grupo situados en un radio de 20 m. Dura 2 min."},
[79735] = {name = "Poder transformado", description = "[q]El Poder transformado aumenta un 10% el daño mágico infligido y aumenta un 10% la velocidad de lanzamiento durante 30 sec. Se acumula."},
[79765] = {name = "Explosión de burbuja", description = "[q]Dispara una explosión de burbujas que inflige daño de Escarcha a un enemigo."},
[79782] = {name = "Orbe de corrupción", description = "[q]Inflige daño de las Sombras a un enemigo y reduce su velocidad de movimiento durante 5 sec."},
[79797] = {name = "Tormenta de truenos", description = "[q]Invoca una descarga de relámpagos que inflige daño a los enemigos cercanos en un radio de 15 m. Inflige 3237 to 3762 p. de daño de Naturaleza a todos los enemigos cercanos y los derriba."},
[79810] = {name = "Cuchilla de Escarcha", description = "[q]Inflige un 110% de daño con arma y reduce la velocidad de ataque y de lanzamiento un 10% a un enemigo y a sus aliados cercanos. Afecta hasta a 3 objetivos."},
[79811] = {name = "Dispersión", description = "[q]Te conviertes en energía de las Sombras pura y reduces todo el daño recibido un 90%. No puedes atacar ni lanzar hechizos, pero regeneras un 6% de maná cada segundo durante 6 sec. Dispersión se puede lanzar mientras estás aturdido, asustado o silenciado, quita todos los efectos de frenado y de reducción del movimiento al lanzarlo y te hace inmune a ellos mientras dura."},
[79821] = {name = "Visión mental de Zul'Gurub", description = "[q]Te permite ver a través de los ojos de una prole de colazote capturada."},
[79822] = {name = "Nova Sagrada", description = "[q]Provoca una explosión de Luz Sagrada alrededor del taumaturgo que inflige 24 to 29 p. de daño Sagrado a todos los objetivos enemigos en un radio de 10 m y sana 360 to 440 p. a todos los miembros del grupo en un radio de 10 m. Estos efectos no generan amenaza."},
[79823] = {name = "Lluvia de estrellas", description = "[q]Inflige daño Arcano a los enemigos de un área seleccionada cada 2 s durante 10 sec."},
[79824] = {name = "Invocar aliado Antárbol", description = "[q]Invoca a 1 antárbol aliado para acompañar al taumaturgo."},
[79825] = {name = "Invocar champiñón inestable", description = "[q]Invoca a un champiñón putrefacto."},
[79826] = {name = "Nube inestable", description = "[q]Inflige daño de Naturaleza durante 15 sec.\n Velocidad de movimiento reducida un 25%."},
[79828] = {name = "Destrozar", description = "[q]Araña al objetivo, lo que inflige 24 to 29 p. de daño y 20 p. de daño extra durante 15 sec."},
[79831] = {name = "Colisión de ola", description = "[q]Inflige daño de Escarcha."},
[79833] = {name = "Marca de lo Salvaje", description = "[q]Aumenta la fuerza, la agilidad, el aguante y el intelecto del objetivo amistoso un 5% durante 1 hour. Si el objetivo pertenece a tu grupo o banda, todos los miembros del grupo o banda se verán afectados."},
[79835] = {name = "Bala empapada de veneno", description = "[q]Aplica Empapado de veneno a los ataques cuerpo a cuerpo y con hechizos, lo que inflige 20000 p. de daño de Naturaleza cada 2 s. Se acumula."},
[79840] = {name = "Endurecer", description = "[q]Inyecta quitina en el caparazón del taumaturgo, lo que reduce todo el daño recibido un 5% durante 20 sec."},
[79846] = {name = "Fogonazo", description = "[q]Invoca una columna de llamas que quema a todos los enemigos del área seleccionada e inflige daño extra cada 2 s durante 8 sec."},
[79847] = {name = "Ola explosiva", description = "[q]Desata una ola de llamas que inflige daño de Fuego a los enemigos cercanos y reduce su velocidad de movimiento durante 6 sec."},
[79848] = {name = "Armadura de arrabio", description = "[q]Inflige daño de Fuego."},
[79849] = {name = "Armadura de arrabio", description = "[q]Inflige daño de Fuego al recibir un golpe, aumenta la probabilidad de asestar un golpe crítico con hechizos un 3% y reduce la probabilidad de recibir un golpe crítico un 5%. Solo puede haber un hechizo de armadura activo a la vez. Dura 10 min."},
[79850] = {name = "Nova de Escarcha", description = "[q]Inflige daño de Escarcha a los enemigos cercanos y los inmoviliza durante un máximo de 4 sec."},
[79851] = {name = "Abanico de cuchillos", description = "[q]Lanza instantáneamente cuchillos a todos los enemigos cercanos, lo que inflige un 100% de daño con arma."},
[79852] = {name = "Eviscerar", description = "[q]Remate que inflige daño."},
[79853] = {name = "Evasión", description = "[q]Aumenta la probabilidad de esquivar del taumaturgo un 35%."},
[79854] = {name = "Bola de Fuego", description = "[q]Inflige daño de Fuego a un enemigo."},
[79855] = {name = "Explosión de Fuego", description = "[q]Inflige daño de Fuego a un enemigo."},
[79856] = {name = "Fogonazo", description = "[q]Invoca una columna de llamas que quema a todos los enemigos del área seleccionada e inflige daño extra cada 2 s durante 8 sec."},
[79857] = {name = "Ola explosiva", description = "[q]Desata una ola de llamas que inflige daño de Fuego a los enemigos cercanos y reduce su velocidad de movimiento durante 6 sec."},
[79858] = {name = "Descarga de Escarcha", description = "[q]Inflige daño de Escarcha a un enemigo y reduce su velocidad de movimiento durante 4 sec."},
[79859] = {name = "Lanza de hielo", description = "[q]Inflige daño de Escarcha a un objetivo enemigo. Inflige el doble de daño contra objetivos congelados."},
[79860] = {name = "Ventisca", description = "[q]Invoca una ventisca que dura 8 sec, lo que inflige daño de Escarcha cada 2 s a todos los enemigos de un área seleccionada."},
[79862] = {name = "Puñalada", description = "[q]Inflige un 150% de daño a un enemigo, pero solo si atacas por la espalda."},
[79863] = {name = "Hemorragia", description = "[q]Inflige daño con arma y aumenta el daño físico recibido por un enemigo un 20% durante 10 sec."},
[79864] = {name = "Paso de las Sombras", description = "[q]Intenta dar un paso en las sombras y reaparecer detrás de tu enemigo."},
[79865] = {name = "Armadura de Escarcha", description = "[q]Reviste al taumaturgo con una capa de Escarcha que dura 30 min. El hielo espeso aumenta la armadura, ralentiza el movimiento de los atacantes cuerpo a cuerpo y aumenta el tiempo entre los ataques."},
[79866] = {name = "Lanzamiento mortal", description = "[q]Lanza una daga al enemigo y le inflige un 24 to 29% de daño con arma. También interrumpe el lanzamiento de hechizos y evita que se pueda lanzar cualquier hechizo de esa misma escuela durante 3 sec."},
[79868] = {name = "Explosión Arcana", description = "[q]Golpea al objetivo con energía, lo que inflige daño Arcano. Cada vez que lances Explosión Arcana, el daño de todos tus hechizos Arcanos aumentará un 10% y el coste de maná de Explosión Arcana aumentará un 150%. El efecto se acumula hasta 4 veces y dura 6 sec o hasta que se lance un hechizo que inflija daño Arcano, excepto Explosión Arcana."},
[79870] = {name = "Carga feral", description = "[q]Provoca que cargues contra un enemigo, lo que lo inmoviliza e interrumpe el lanzamiento de todos los hechizos durante until cancelled."},
[79871] = {name = "Temeridad", description = "[q]Inmune a todos los efectos de miedo.\n Todos los ataques asestan golpes críticos.\n Armadura reducida un 35%."},
[79872] = {name = "Ola de choque", description = "[q]Envía una ola de fuerza frente al guerrero, lo que inflige daño y aturde a los objetivos enemigos en un radio de 10 m en un cono frontal durante 4 sec."},
[79875] = {name = "¡Machaque Ongolongo!", description = "[q]Golpea al objetivo hacia atrás."},
[79877] = {name = "Sed de sangre", description = "[q]Ataca instantáneamente al objetivo, lo que le inflige un 120% de daño con arma normal. Además, los ataques cuerpo a cuerpo con éxito restaurarán un 1% de la salud máxima. Este efecto dura 10 sec."},
[79878] = {name = "Sed de sangre", description = "[q]Ataca instantáneamente al objetivo infligiéndole un 120% de daño con arma normal. Además, los ataques cuerpo a cuerpo que alcancen al objetivo restaurarán un 1% de salud máxima. Este efecto dura 10 sec."},
[79879] = {name = "Descarga eléctrica", description = "[q]Inflige 43875 to 46125 p. de daño de Naturaleza a un máximo de 3 enemigos en un radio de 8 m."},
[79880] = {name = "Ralentizar", description = "[q]Aumenta el tiempo entre los ataques de un enemigo un 25%, aumenta el tiempo de lanzamiento un 25% y ralentiza su velocidad de movimiento un 25% durante 6 sec."},
[79881] = {name = "Embate", description = "[q]Embiste al adversario, lo que inflige daño con arma más una cantidad extra."},
[79883] = {name = "Lanzamiento destrozador", description = "[q]Lanza tu arma a un enemigo e inflige daño con arma. Reduce la armadura del objetivo un 20% durante 10 sec."},
[79884] = {name = "Descarga de relámpagos", description = "[q]Golpea a un objetivo con relámpagos, lo que le inflige daño de Naturaleza."},
[79885] = {name = "Golpe en el corazón", description = "[q]Golpea instantáneamente al objetivo y a su aliado más cercano, lo que inflige un 110% de daño con arma al objetivo principal y un 55% de daño con arma al objetivo secundario."},
[79886] = {name = "Ráfaga de lava", description = "[q]Lanzas lava fundida al objetivo y le infliges daño de Fuego."},
[79887] = {name = "Muerte y descomposición", description = "[q]Daño de las Sombras infligidos cada 1 s a todos los objetivos en el área afectada durante 10 sec."},
[79888] = {name = "Conductor de relámpagos", description = "[q]Infecta a un enemigo durante 15 sec, lo que hace que inflija daño de Naturaleza a sus aliados cercanos cada 2 s."},
[79890] = {name = "Choque de llamas", description = "[q]Quema instantáneamente a un enemigo y luego inflige daño de Fuego extra cada 3 s durante 12 sec."},
[79891] = {name = "Cuerno de invierno", description = "[q]El caballero de la Muerte sopla el cuerno de invierno, que genera 10 p. de poder rúnico y aumenta la agilidad y la fuerza total 5 p. Dura 2 min."},
[79892] = {name = "Escudo de agua", description = "[q]El taumaturgo queda rodeado por 3 esferas de agua. Cada hechizo o ataque cuerpo a cuerpo o a distancia que golpee al taumaturgo inflige daño de Escarcha y ralentiza los lanzamientos de hechizos del atacante. 3 cargas, dura 10 min."},
[79893] = {name = "Gusano de sangre", description = "[q]Crea un gusano de sangre que ataca a los objetivos cercanos. Dura 8 sec."},
[79894] = {name = "Atracción letal", description = "[q]Reúne la energía profana que rodea y vincula toda materia, lo que acerca al objetivo hacia el caballero de la Muerte y obliga al enemigo a atacar al caballero de la Muerte durante 3 sec."},
[79895] = {name = "Golpe de Escarcha", description = "[q]Golpea instantáneamente al enemigo, lo que inflige un 110% de daño con arma como daño de Escarcha."},
[79896] = {name = "Explosión aullante", description = "[q]Golpea al objetivo con un viento helado que inflige daño de Escarcha a todos los enemigos en un radio de 10 m. Inflige el triple de daño a los enemigos congelados."},
[79897] = {name = "Toque helado", description = "[q]Inflige daño de Escarcha y reduce la velocidad de ataque a distancia, cuerpo a cuerpo y de lanzamiento del objetivo un 15% durante 8 sec."},
[79899] = {name = "Cadenas de hielo", description = "[q]Encadena al objetivo con cadenas heladas, lo que reduce su velocidad de movimiento a un 5% de lo normal. El objetivo recupera un 10% de su velocidad de movimiento cada segundo durante 10 sec."},
[79900] = {name = "Escudo inestable", description = "[q]Los ataques lanzados contra el escudo inestable provocan un Choque estático en la ubicación de un enemigo cercano. Choque estático inflige 58500 to 61500 p. de daño de Naturaleza a todos los enemigos en un radio de 6 m del objetivo."},
[79901] = {name = "Peste de sangre", description = "[q]Un golpe feroz que inflige daño con arma y apesta al objetivo, lo que inflige daño de las Sombras durante 12 sec."},
[79902] = {name = "Golpe de peste", description = "[q]Un golpe feroz que inflige daño con arma y apesta al objetivo, lo que inflige 8000 p. de daño de las Sombras durante 12 sec."},
[79903] = {name = "Espiral de la muerte", description = "[q]Inflige 24 to 29 p. de daño de las Sombras a un objetivo aleatorio cercano."},
[79904] = {name = "Cargar", description = "[q]Carga contra un enemigo, lo que inflige 13423 to 15600 p. de daño al objetivo y a cualquiera de sus aliados cercanos y los repele."},
[79905] = {name = "Levantar a muerto", description = "[q]Resucita a un necrófago para que luche a tu lado. Si no hay ningún cadáver de humanoide disponible que reporte experiencia u honor, deberá administrarse polvo de cadáver para completar el hechizo. Solo puedes tener un máximo de un necrófago a la vez. Dura 1 min."},
[79907] = {name = "Zona antimagia", description = "[q]Coloca una gran zona antimagia estática que reduce un 75% el daño con hechizos infligido a cualquier miembro de tu grupo o banda que se encuentre dentro. La zona antimagia dura 10 sec o hasta que absorbe (10000 + 2 * Attack power) p. de daño con hechizos."},
[79909] = {name = "Golpe de la Plaga", description = "[q]Un golpe profano que inflige un 150% de daño con arma como daño de las Sombras."},
[79913] = {name = "Cadena de relámpagos", description = "[q]Golpea al enemigo con una Descarga de relámpagos que se extiende a otro enemigo cercano. Afecta a un máximo de 3 objetivos e inflige menos daño de Naturaleza a cada objetivo sucesivo."},
[79914] = {name = "Espíritu feral", description = "[q]Invoca 2 espíritus de lobo a las órdenes del chamán. Duran 45 sec."},
[79921] = {name = "Llama de las Sombras", description = "[q]Inflige 23125 to 26875 p. de daño de Fuego a los enemigos en un cono frente al taumaturgo."},
[79922] = {name = "Despellejar", description = "[q]Inflige un 60% de daño de arma a todos los enemigos en un radio de 4 m cada 0,25 s durante 5 sec."},
[79924] = {name = "Ola de sanación", description = "[q]Invoca la magia de Naturaleza para sanar a un aliado."},
[79925] = {name = "Choque de Escarcha", description = "[q]Inflige daño de Escarcha a un enemigo y reduce su velocidad de movimiento durante 6 sec."},
[79927] = {name = "Escudo de tierra", description = "[q]Protege al objetivo con un escudo de tierra que hace que los ataques cuerpo a cuerpo sanen al objetivo protegido. Este efecto solo ocurre una vez cada pocos segundos. 10 cargas. Dura 10 min."},
[79928] = {name = "Escudo de tierra", description = "[q]Protege al objetivo con un escudo de tierra que hace que los ataques cuerpo a cuerpo sanen al objetivo protegido. Este efecto solo ocurre una vez cada pocos segundos."},
[79930] = {name = "Corrupción", description = "[q]Corrompe a los enemigos cercanos, lo que inflige daño de las Sombras cada 3 s durante 15 sec."},
[79932] = {name = "Descarga de las Sombras", description = "[q]Lanza una descarga de magia oscura a un enemigo y le inflige daño de las Sombras."},
[79933] = {name = "Drenar vida", description = "[q]Drena salud de un enemigo durante 5 sec y se la transfiere al taumaturgo."},
[79934] = {name = "Armadura demoníaca", description = "[q]Protege al taumaturgo aumentando su armadura y la cantidad de salud que se genera mediante hechizos y efectos un 20%. Solo puede haber un tipo de hechizo de armadura activo a la vez. Dura 30 min."},
[79937] = {name = "Inmolar", description = "[q]Quema a un enemigo e inflige daño de Fuego extra cada 3 s durante 15 sec."},
[79938] = {name = "Incinerar", description = "[q]Inflige daño de Fuego a tu objetivo."},
[79939] = {name = "Descarga de Caos", description = "[q]Lanza una descarga de Fuego caótico al enemigo, que inflige daño de Fuego. Descarga de Caos no se puede resistir y atraviesa la inmunidad defensiva."},
[79949] = {name = "Escudo de agua", description = "[q]El taumaturgo queda rodeado por 3 esferas de agua que le dan 10 p. de maná cada 5 s. Cada hechizo o ataque cuerpo a cuerpo o a distancia que golpee al taumaturgo restaura 758 p. de maná y consume una de las esferas. Solo se activará una esfera cada varios segundos. Dura 10 min. Un chamán solo puede tener un Escudo elemental activo a la vez."},
[79954] = {name = "Armadura vil", description = "[q]Rodea al taumaturgo con energía vil, lo que aumenta el poder con hechizos un 10%. Además, recuperas un 2% de tu salud máxima cada 5 s. Solo se puede tener un hechizo de armadura activo a la vez. Dura 30 min."},
[79955] = {name = "Lluvia de Fuego", description = "[q]Invoca una lluvia de arrabio que quema a todos los enemigos del área seleccionada e inflige daño de Fuego cada 3 s durante 9 sec."},
[79956] = {name = "Maldición de los Elementos", description = "[q]Maldice al objetivo durante 30 sec, lo que reduce su resistencia a lo Arcano, al Fuego, a la Escarcha, a la Naturaleza y a las Sombras 45 p. y aumenta el daño mágico que recibe un 8%. Un taumaturgo solo puede tener una maldición activa a la vez sobre un mismo objetivo."},
[79960] = {name = "Luz Sagrada", description = "[q]Invoca la magia Sagrada para sanar a un aliado."},
[79961] = {name = "Choque Sagrado", description = "[q]Golpea al objetivo con energía Sagrada que inflige daño Sagrado a un enemigo."},
[79962] = {name = "Sello de Rectitud", description = "[q]Colma al taumaturgo con espíritu sagrado durante 30 min, otorga a cada ataque cuerpo a cuerpo daño Sagrado extra."},
[79963] = {name = "Aura de concentración", description = "[q]Los aliados en un radio de 40 m pierden un 35% menos de tiempo de lanzamiento o de canalización cuando reciben daño."},
[79964] = {name = "Exorcismo", description = "[q]Inflige daño Sagrado a un enemigo."},
[79965] = {name = "Escudo de vengador", description = "[q]Lanza un escudo Sagrado al enemigo, lo que inflige daño Sagrado y lo deja atontado. A continuación salta a los enemigos cercanos. Afecta a 2 objetivos en total."},
[79966] = {name = "Consagración", description = "[q]Consagra el suelo que el taumaturgo tiene bajo sus pies, lo que inflige daño Sagrado durante 8 sec a los enemigos que lo pisen."},
[79967] = {name = "Escudo Sagrado", description = "[q]Aumenta la probabilidad de bloquear un 30% durante 10 sec e inflige daño Sagrado por cada ataque bloqueado mientras está activo. Cada bloqueo gasta una carga. 3 cargas."},
[79968] = {name = "Bendición de reyes", description = "[q]Da a un objetivo amistoso una Bendición que aumenta la fuerza, la agilidad, el aguante y el intelecto un 5% durante 5 min."},
[79970] = {name = "Tormenta divina", description = "[q]Un ataque con arma instantáneo que inflige daño Sagrado a un máximo de 4 enemigos en un radio de 8 m."},
[79971] = {name = "Martillo de cólera", description = "[q]Lanza un martillo que alcanza al enemigo y le inflige daño Sagrado."},
[79972] = {name = "Sello de Verdad", description = "[q]Colma al paladín de Poder Sagrado, lo que provoca que los ataques contra un solo objetivo apliquen Censura. Esto inflige [(0.01 * Spell power + 0.0193 * Attack power) * 5 */ 100] p. de daño Sagrado extra durante 15 sec. Censura se puede acumular hasta 5 veces. Cuando se haya acumulado 5 veces, cada uno de los ataques del paladín también infligirá un 15% de daño con arma como daño Sagrado extra. Un paladín solo puede tener un sello activo a la vez. Dura 5 min.\n\n Desatar la energía de este sello infligirá (1 + 0.223 * Spell power + 0.142 * Attack power) p. de daño Sagrado a un enemigo, aumentado un 10% por cada aplicación de Censura sobre el objetivo."},
[79975] = {name = "Control mental de Zul'Gurub", description = "[q]Te permite controlar a la prole de colazote capturada."},
[79976] = {name = "Aura de reprensión", description = "[q]Inflige daño Sagrado a cualquier criatura que golpee al taumaturgo."},
[79977] = {name = "Bendición de poderío", description = "[q]Da a un objetivo amistoso una Bendición que aumenta un 20% su poder de ataque y un 10% su poder de ataque a distancia y restaura 1 p. de maná cada 5 s durante 5 min."},
[80003] = {name = "Flecha negra", description = "[q]Dispara una flecha negra al objetivo, lo que aumenta un 6% todo el daño que inflijas al objetivo e inflige daño de las Sombras durante 15 sec."},
[80004] = {name = "Trampa con culebras", description = "[q]Coloca una trampa que liberará varias culebras venenosas que atacan al primer enemigo que se acerque. Las culebras morirán tras 5 sec. La trampa dura 1 min."},
[80009] = {name = "Picadura de serpiente", description = "[q]Pica al objetivo, lo que inflige daño de Naturaleza durante 15 sec."},
[80011] = {name = "Empapado de veneno", description = "[q]Estar empapado de veneno inflige 20000 p. de daño de Naturaleza cada 2 s y provoca que los hechizos y facultades inflijan 19500 to 20500 p. de daño de Naturaleza al objetivo."},
[80015] = {name = "Disparo firme", description = "[q]Un disparo firme que inflige daño con arma más (Ranged attack power * 0.1 + 0) p."},
[80016] = {name = "Marca del cazador", description = "[q]Deja la Marca del cazador en el objetivo, lo que aumenta el daño a distancia de todos los que ataquen al objetivo un 10%. Además, el cazador siempre podrá ver al objetivo, aunque este utilice Sigilo o Invisibilidad. Dura 30 sec."},
[80018] = {name = "Trampa de Escarcha", description = "[q]Coloca una trampa de Escarcha que crea una mancha de hielo durante 30 sec cuando el primer enemigo se acerca. Todos los enemigos en un radio de 10 m se ralentizarán un 50% mientras estén en el área de efecto. La trampa dura 30 sec. Solo puede haber 1 trampa activa a la vez."},
[80019] = {name = "Aura de Trampa de Escarcha", description = "[q]Coloca una trampa de Escarcha que crea una mancha de hielo durante 30 sec cuando el primer enemigo se acerca. Todos los enemigos en un radio de 10 m se ralentizarán un 50% mientras estén en el área de efecto."},
[80021] = {name = "Red", description = "[q]Inmoviliza a un enemigo durante 4 sec."},
[80022] = {name = "Morder red", description = "[q]Rompe a mordiscos las redes de los trols Gurubashi. No funciona con redes de tejido de hierro."},
[80028] = {name = "Perforar roca", description = "[q]Inflige 4000 p. de daño cada 3 s. Se acumula hasta 10 veces. Dura 15 sec."},
[80031] = {name = "Tajo llameante", description = "[q]Inflige un 110% de daño cuerpo a cuerpo normal a un enemigo y a su aliado más cercano."},
[80035] = {name = "Ira vengativa", description = "[q]La muerte de un compañero despierta una marea de energía que aumenta el daño infligido un 100% y la velocidad de ataque un 50%."},
[80038] = {name = "Erupción de magma", description = "[q]Lanza roca fundida en todas direcciones, lo que inflige 56550 to 63450 p. de daño de Fuego a todos los enemigos en un radio de 2 de las ubicaciones objetivo."},
[80051] = {name = "Lesión grave", description = "[q]Un ataque sanguinario que provoca que el objetivo sangre abundantemente hasta que sea sanado por encima de un 90% de tu salud máxima."},
[80053] = {name = "Protocolo de veneno", description = "[q]El protocolo de veneno crea una bomba de veneno cada 3 s que explota tras entrar en contacto con su objetivo o al morir."},
[80058] = {name = "Ráfaga Crepuscular", description = "[q]Ráfaga Crepuscular inflige daño de las Sombras."},
[80065] = {name = "Tornado", description = "[q]Inflige daño de Naturaleza a los enemigos cercanos y los repele."},
[80066] = {name = "Tornado", description = "[q]Crea un Tornado bajo un objetivo enemigo."},
[80068] = {name = "Tormenta de truenos", description = "[q]Invoca una descarga de relámpagos que inflige daño a los enemigos cercanos en un radio de 15 m. Inflige daño de Naturaleza a todos los enemigos cercanos y los derriba."},
[80074] = {name = "Red de tejido de hierro", description = "[q]Inmoviliza a un enemigo durante 4 sec."},
[80084] = {name = "Enfurecer", description = "[q]Reduce el tiempo entre tus ataques un 50% durante 30 sec. Daño infligido aumentado un 50%."},
[80092] = {name = "Bomba de veneno", description = "[q]Inflige 195000 to 205000 p. de daño de Naturaleza a los enemigos en un radio de 6 m."},
[80094] = {name = "Fijar", description = "[q]¡La bomba de veneno está fijada a ti!"},
[80098] = {name = "Cañón de Mekkatorque", description = "[q]Mekkatorque solicita un ataque orbital que inflige daño a un enemigo."},
[80110] = {name = "Misiles de Mekkatorque", description = nil},
[80117] = {name = "Púa terrestre", description = "[q]Crea una púa terrestre que inflige daño de Naturaleza."},
[80127] = {name = "Sacudida de llamas", description = "[q]Inflige 23125 to 26875 p. de daño de Fuego a un enemigo y aumenta el daño de Fuego que recibe un 5% durante 8 sec."},
[80130] = {name = "Latigazo de cola", description = "[q]Inflige 48750 to 81250 p. de daño a los enemigos en un radio de 12 m detrás del punto de impacto y los derriba."},
[80132] = {name = "Oscuridad desligada", description = "[q]Inflige daño de las Sombras a un enemigo."},
[80145] = {name = "Agarre perforador", description = "[q]Agarra a un enemigo cercano y le exprime la vida. Inflige 69375 to 80625 p. de daño físico cada segundo."},
[80146] = {name = "Capón", description = "[q]Le da un capón al enemigo que inflige un 150% de daño con arma y reduce la probabilidad de esquivar o parar un ataque un 20% durante 6 sec."},
[80148] = {name = "Rayo de Mekkatorque", description = "[q]Mekkatorque dispara su Rayo de Mekkatorque, que inflige daño al objetivo enemigo y a todos los enemigos cercanos."},
[80149] = {name = "Lanzar hacha", description = "[q]Lanza un arma al enemigo, lo que inflige un 120% de daño físico."},
[80151] = {name = "Ignición", description = "[q]Inflige 14250 to 15750 p. de daño de Fuego al objetivo actual del taumaturgo y, a continuación, 5000 p. de daño de Fuego extra por segundo durante 10 sec."},
[80157] = {name = "Bomba química", description = "[q]Dispara contra un objetivo aleatorio una bomba química que provoca una nube química en el emplazamiento."},
