[91303] = {name = "Conversión", description = "[q]Cho'gall llama a los débiles de mente, lo que provoca que el objetivo canalice el hechizo Alabar sobre él. Alabar a Cho'gall hace que obtenga Devoción retorcida cada 3 s. Alabar puede interrumpirse."},
[91304] = {name = "Azote de las Sombras", description = "[q]Azota a Chimaeron con energía de las Sombras, lo que provoca un dolor insoportable e interrumpe Enemistad."},
[91307] = {name = "Sombras burlescas", description = "[q]Inflige 2000 p. de daño de las Sombras cada segundo a todos los enemigos."},
[91309] = {name = "Cargar", description = "[q]Carga contra el enemigo más distante y le inflige daño normal más 150 p. A continuación asusta a todos los objetivos cercanos."},
[91313] = {name = "Explosión de llamas", description = "[q]Envía una ola de fuerza frente al gólem que inflige daño de Fuego en un radio de 10 m en un cono frontal durante until cancelled."},
[91314] = {name = "Empujón", description = "[q]Inflige daño y lanza al objetivo por encima del hombro del taumaturgo."},
[91324] = {name = "Toque sombrío", description = "[q]Toque sombrío se extiende e inflige al objetivo 6000 p. de daño de las Sombras cada 3 durante 15 sec."},
[91325] = {name = "Vórtice de sombras", description = "[q]Crea una poza oscura que inflige 60000 p. de daño de las Sombras durante 10 sec a los enemigos que la pisen."},
[91335] = {name = "Embate", description = "[q]Embiste al adversario, lo que inflige daño con arma más una cantidad extra."},
[91348] = {name = "Ablandar", description = "[q]Ablanda al objetivo enemigo, lo que reduce su armadura un 3% por ataque e inflige 150 p. de daño físico cada 1 s. Se puede aplicar un máximo de 15 veces. Dura 10 sec."},
[91408] = {name = "Torbellino", description = "[q]Inflige 18850 to 21150 p. de daño cada segundo a los enemigos alrededor pero reduce la velocidad de movimiento un 35%."},
[91415] = {name = "Frenesí", description = "[q]Velocidad de ataque aumentada un 60%.\n Daño aumentado un 60%."},
[91419] = {name = "Aluvión de huesos", description = "[q]Lanza un hueso, lo que inflige 9000 p. de daño físico a un objetivo aleatorio cada 1 s durante 3 sec."},
[91463] = {name = "Muro de escudo", description = "[q]Reduce el daño físico y mágico recibido por el taumaturgo un 75% durante 10 sec."},
[91522] = {name = "Toque oscuro", description = "[q]Inflige un 40% del daño con arma normal al objetivo enemigo como daño de las Sombras."},
[91554] = {name = "Nube fétida", description = "[q]Una nube de enfermedad repugnante rodea al cadáver e inflige 8000 p. de daño cada segundo durante 10 sec."},
[91561] = {name = "Nube enfermiza", description = "[q]Una nube enfermiza rodea al portador, lo que inflige 1950 to 2050 p. de daño de Naturaleza a los enemigos en un radio de 5 cada 3 s."},
[91563] = {name = "Bomba viva", description = "[q]Pone una bomba ígnea a un enemigo. Tras 6 s la bomba explota, lo que inflige 20000 p. de daño de Fuego al objetivo y sus aliados cercanos."},
[91565] = {name = "Fuego feérico", description = "[q]Reduce la armadura del objetivo un 4% durante 5 min. Mientras dura su efecto, el objetivo no puede utilizar el sigilo ni hacerse invisible. Se acumula hasta 3 veces."},
[91571] = {name = "Ronda de trituradora", description = "[q]Inflige 23797 to 33202 p. de daño a los objetivos que se encuentran en un pequeño radio alrededor del área de impacto. Se puede disparar muy rápidamente."},
[91574] = {name = "Ronda de destructor", description = "[q]Inflige 200400 to 279600 p. de daño de Fuego a las unidades que se encuentran en un gran radio alrededor de la ubicación del impacto. Cargar una nueva ronda de destructor lleva algo de tiempo."},
[91594] = {name = "Wedding Phase Aura", description = nil},
[91601] = {name = "Instant Boulder Poof Spawn", description = nil},
[91611] = {name = "Golpe de gárgola", description = "[q]Inflige 7400 to 8600 p. de daño de Naturaleza a un enemigo."},
[91614] = {name = "Serpientes de Zakahn", description = nil},
[91622] = {name = "Tromba Arcana", description = "[q]Lanza contra el objetivo enemigo varios misiles que le infligen 17100 to 18900 p. de daño Arcano."},
[91623] = {name = "Deflagración Arcana", description = "[q]Lanza una ola explosiva de magia e inflige 13500 to 16500 p. de daño Arcano a los enemigos cercanos."},
[91624] = {name = "Mejorar magia", description = "[q]Aumenta un 50% el daño de Fuego, Escarcha y Arcano infligido por el taumaturgo durante 8 sec."},
[91625] = {name = "Explotar", description = "[q]Inflige daño de Fuego y derriba a los enemigos cercanos."},
[91629] = {name = "Escudo de huesos", description = "[q]Amortigua 100000 p. de daño. Si los huesos amortiguan la cantidad total se partirán e infligirán 30000 p. de daño a los objetivos enemigos en un radio de 15 m."},
[91644] = {name = "Tirón", description = "[q]El ave rapaz agarra el arma del enemigo con sus garras, lo que lo desarma durante 10 sec."},
[91646] = {name = "Plantar girasol", description = "[q]Crea un girasol, que concentra energía solar del cielo. La energía solar se usa para crear otras plantas."},
[91649] = {name = "Plantar escupidora", description = "[q]Crea un escupidor, que dispara ácido a los zombis que se acercan."},
[91668] = {name = "Fuerza inestable", description = "[q]Cambia de forma aleatoria el daño físico infligido por el taumaturgo durante 8 sec."},
[91677] = {name = "Flema pustulosa", description = "[q]Dispara veneno al enemigo, lo que inflige daño de Naturaleza y luego daño extra cada 2 s durante 10 sec."},
[91704] = {name = "Plantar rocanuez", description = "[q]Crea una rocanuez, que absorbe ataques de los necrófagos que se acercan."},
[91710] = {name = "Plantar parra asfixiadora", description = "[q]Crea una parra asfixiadora, que agarra y mata gradualmente a 1 necrófago atacante."},
[91723] = {name = "Golpe de segador", description = "[q]Inflige 13875 to 16125 p. de daño físico en un cono frente al prototipo de segador. Genera 20 p. de presión hidráulica."},
[91726] = {name = "Carga de segador", description = "[q]Carga contra un enemigo, inflige 27750 to 32250 p. de daño físico y derriba al enemigo durante 4 sec."},
[91727] = {name = "Golpe presurizado", description = "[q]La presión de vapor se libera para formar un ataque poderoso. Inflige 18500 to 21500 p. de daño físico y derriba al objetivo."},
[91731] = {name = "Aleación de obsidiana", description = "[q]Una aleación de obsidiana avanzada vuelve al prototipo de segador inmune al daño de Fuego."},
[91737] = {name = "¡En llamas!", description = "[q]Inflige daño de Fuego cada 3 s. La unidad se autodestruirá tras 30 sec."},
[91801] = {name = "Golpe mortal", description = "[q]Inflige un 120% de daño con arma y deja al objetivo herido, lo que reduce un 25% la eficacia de cualquier sanación durante 5 sec."},
[91815] = {name = "Escudo de arrabio", description = "[q]Acero fundido protege al taumaturgo de los ataques mágicos e inflige daño de Fuego a todos los enemigos cercanos. Daño mágico recibido reducido un 50%."},
[91830] = {name = "Fijar", description = "[q]Se fija en el objetivo actual del taumaturgo, lo que provoca que el taumaturgo solo ataque a ese objetivo."},
[91849] = {name = "Atracción mortal", description = "[q]Arrastra a todos los enemigos cercanos a la ubicación de Lord Victor Nefarius."},
[91857] = {name = "Generador de poder sobrecargado", description = "[q]Invoca un generador de poder debajo de un objetivo amistoso aleatorio. Esto aumenta un 50% el daño de todos los objetivos amistosos y enemigos en un radio de 5 m. Dura 1 min."},
[91871] = {name = "Cargar relámpago", description = "[q]Daño recibido aumentado un 33%.\n Daño infligido aumentado un 10%.\n Celeridad aumentada un 10%. La celeridad no se acumula con otros efectos similares."},
[91933] = {name = "Rugido intimidador", description = "[q]Ruge al enemigo, lo que lo paraliza con terror durante 6 sec y hace que huyan todos los demás enemigos cercanos."},
[91977] = {name = "Golpe de boda", description = "[q]Lanza un misil hacia un objetivo aleatorio. Cuando el misil toca tierra, inflige daño de las Sombras a todos los enemigos en un radio de 5 m de ese emplazamiento."},
[91978] = {name = "Pies fríos", description = "[q]¡Inflige al enemigo nervios nupciales, lo que lo congela en el sitio!"},
[91979] = {name = "Alterio de boda", description = "[q]Campanas de boda oscuras anuncian el final de los días. Hace que los enemigos cercanos queden paralizados por el miedo."},
[91980] = {name = "Explosión con tic tac", description = nil},
[91981] = {name = "Ticker Bomb Invis Removal", description = nil},
[91997] = {name = "Descarga de las Sombras", description = "[q]Lanza una descarga de magia oscura a un enemigo y le inflige daño de las Sombras."},
[91998] = {name = "Lanzar roca", description = "[q]Lanza una roca a un enemigo, infligiéndole daño físico."},
[92001] = {name = "Escudo del Convocador", description = "[q]El escudo del Convocador protege contra muchas formas de control de masas."},
[92007] = {name = "Vapor espiral", description = "[q]Inflige daño de Escarcha a los enemigos situados en un cono frente al taumaturgo y reduce su velocidad de movimiento durante 2 sec."},
[92008] = {name = "Vapor condensador", description = "[q]Inflige daño de Escarcha a los enemigos situados en un cono frente al taumaturgo y reduce su velocidad de movimiento durante 2 sec."},
[92011] = {name = "Vapor congelante", description = "[q]Inflige daño de Escarcha a los enemigos situados en un cono frente al taumaturgo y reduce su velocidad de movimiento durante 2 sec."},
[92013] = {name = "Condensación", description = "[q]Drenando energía de los vapores espirales. Con-den-sa-ción."},
[92023] = {name = "Sombras atrapantes", description = "[q]Nefarian sostiene al objetivo adquirido e impide que se mueva."},
[92025] = {name = "Hervor de sangre", description = "[q]Hace hervir la sangre de todos los enemigos en un radio de 10 m, lo que inflige daño de las Sombras."},
[92038] = {name = "Animus de vapor", description = "[q]Los vapores se fusionan y pueden dispersarse en cualquier momento, lo que inflige mucho daño y derriba a los enemigos."},
[92042] = {name = "Unirse", description = "[q]Inflige daño de Escarcha y derriba a los enemigos cercanos."},
[92048] = {name = "Infusión de Sombras", description = "[q]Nefarius imbuye el Conductor de relámpagos se imbuye con Sombras, lo que provoca una Infusión de Sombras. Infusión de las Sombras inflige daño de las Sombras a todos los amigos cercanos, lo que inflige daño en función de la distancia a la Infusión de Sombras."},
[92067] = {name = "Sobrecarga estática", description = "[q]Carga a un enemigo con energía que electrifica a los aliados cercanos, lo que inflige 9250 to 10750 p. de daño de Naturaleza cada 2 s durante 10 sec. Los poderosos campos gravitacionales pueden hacer que esta energía se disipe sin hacer daño."},
[92072] = {name = "Atracción empaladora", description = "[q]Lanza tu tridente a un objetivo y lo atrae hacia ti, lo que inflige 24 to 29 p. de daño físico y provoca al enemigo."},
[92075] = {name = "Núcleo de gravedad", description = "[q]Aplasta a un enemigo con una fuerza gravitatoria intensa, lo que inflige 10000 p. de daño físico cada 2 s. Mientras el objetivo está afectado, el campo gravitatorio dificulta el movimiento y las acciones de los aliados cercanos."},
[92100] = {name = "Brebaje nocivo", description = "[q]Un brebaje potente que hace que los enemigos no puedan moverse o reaccionar."},
[92118] = {name = "Ignición", description = "[q]Incendia el suelo en varias direcciones."},
[92144] = {name = "Tajo ígneo", description = "[q]Inflige un 75% de daño cuerpo a cuerpo normal como daño de Fuego a un objetivo enemigo."},
[92156] = {name = "Cancel Phase - Zone-Specific 02", description = nil},
[92157] = {name = "Plantar calabaza bomba", description = "[q]Crea una calabaza bomba, que explota, lo que destruye todos los zombis cercanos."},
[92177] = {name = "Armagedón", description = "[q]La unidad se va a autodestruir."},
[92212] = {name = "Golpe de llamas", description = "[q]Crea una explosión ígnea cerca de un objetivo enemigo, lo que inflige 195000 to 205000 p. de daño de Fuego y prende el suelo en la zona del impacto."},
[92249] = {name = "Llamada de la vida", description = "[q]Llama a las energías sagradas de todas las tierras altas. Alamuerte nota el trastorno... y quién es responsable."},
[92265] = {name = "Tormenta de cristales", description = "[q]Inflige 9425 to 10575 p. de daño de Naturaleza a todos los enemigos cada 0.5 s. Dura 6 sec. Este hechizo requiere ver al objetivo."},
[92266] = {name = "Descarga de radiación", description = "[q]Inflige daño de Naturaleza a un enemigo y tiene probabilidad de irradiarlo."},
[92307] = {name = "Señal de Escarcha", description = "[q]Hace que un Orbe congelado persiga al objetivo."},
[92380] = {name = "Aliento de Tormenta de Escarcha", description = "[q]La quimera canaliza un ataque de aliento helado, lo que inflige [ 206 + ((Ranged attack power * 0.40) * 0.288)] p. de daño de Tormenta de Escarcha cada 2 s durante 8 sec a todos los enemigos en un radio de 12 m."},
[92426] = {name = "Paralizar", description = "[q]Aturde a todos los enemigos durante 8 sec. Si este efecto alcanza todo su recorrido, infligirá 47500 to 52500 p. de daño Arcano. Este efecto se elimina si el objetivo recibe daño."},
[92440] = {name = "Plantar frisia", description = "[q]Crea una frisia que dispara explosiones escalofriantes que ralentizan a los necrófagos que llegan, lo que reduce la velocidad de movimiento y de ataque."},
[92548] = {name = "Helar", description = "[q]Inflige mucho daño de Escarcha a todos los enemigos cercanos."},
[92552] = {name = "Detonación", description = "[q]Inflige 175 to 225 p. de daño de Fuego a los enemigos cercanos."},
[92573] = {name = "Invocar mordisco de la Naturaleza", description = "[q]Invoca a 3 Fuerzas de la Naturaleza para ayudar en combate durante 20 sec."},
[92596] = {name = "Maná recuperado", description = "[q]Recuperando maná."},
[92603] = {name = "Fin de la pesadilla", description = "[q]Un evento traumático provoca que la víctima de la pesadilla pierda la cabeza."},
[92604] = {name = "Adrenalina", description = "[q]El corazón te late fuerte en el pecho. Velocidad de movimiento aumentada un 50%."},
[92608] = {name = "Asfixiándose", description = "[q]Asfixiándote. Pierdes un 3% de la salud máxima cada segundo."},
[92614] = {name = "Desvío", description = "[q]Cuando se activa, aumenta la probabilidad de parar un 100%, reduce un 100% la probabilidad de que los ataques a distancia te alcancen y otorga una probabilidad de un 100% de desviar hechizos. Dura 10 sec."},
[92619] = {name = "Puñalada trapera", description = "[q]Camina entre las sombras y raja la espalda del enemigo, lo que inflige daño físico durante 10 sec."},
[92622] = {name = "Hojas mortales", description = "[q]Vanessa salta rápidamente de objetivo a objetivo, lo que inflige heridas poderosas."},
[92677] = {name = "Execrable", description = "[q]¡El maligno execrable está haciendo mucho ruido! Aumenta tu sonido 10 p."},
[92681] = {name = "Cambio de fase", description = "[q]Saca de la existencia al maligno execrable, lo que lo hace inmune a los hechizos."},
[92685] = {name = "¡Apestado!", description = "[q]¡El maligno execrable te está apestando!"},
[92713] = {name = "Explosión mental", description = "[q]Inflige daño de las Sombras a un enemigo."},
[92716] = {name = "Imbuido de Sombras", description = "[q]Otorga facultades de las Sombras.\n Inmune a Provocar."},
[92733] = {name = "Disparar cosas", description = "[q]Dispara cosas explosivas aleatorias de los alrededores de La Krazzería. El alcance es de 150 m."},
[92754] = {name = "Oscuridad engullidora", description = nil},
[92789] = {name = "Explosión de formación de cristales", description = nil},
[92796] = {name = "Aluvión de garras", description = "[q]Aumenta tu velocidad de ataque un 40%. Además, los ataques alcanzan a un enemigo cercano extra. Dura 12 sec."},
[92831] = {name = "Lanzar botella negra", description = nil},
[92857] = {name = "Espiando", description = "[q]Pone al taumaturgo en modo sigilo. Dura hasta que se cancela."},
[92888] = {name = "Canción de sirena", description = "[q]¡Hace que todos los enemigos cercanos bailen!"},
[92895] = {name = "Romance salobre", description = "[q]Atrapa al enemigo en un romance salobre. Inflige daño de Escarcha cada 3 s durante 15 sec."},
[92915] = {name = "Explosión de Fuego", description = "[q]Golpea a un draco de las Sombras Crepuscular y le inflige daño de Fuego."},
[92916] = {name = "Favor de la Protectora", description = "[q]Te sana a ti mismo y a un vanguardia Bermellón seleccionado durante 6 s. También restaura maná."},
[92917] = {name = "Babosa imbuida", description = nil},
[92918] = {name = "Golpe final", description = "[q]Se tira contra un draco de las Sombras Crepuscular, le destroza la garganta y acaba con él."},
[92930] = {name = "Fango oscuro", description = "[q]Inflige 15000 p. de daño de las Sombras cada 0,5 s a todos los enemigos situados en un radio de 3 m. Dura 30 sec."},
[92945] = {name = "Perforación de estoque", description = "[q]Inflige un 100% de daño con arma a un enemigo."},
[93017] = {name = "Falla Crepuscular", description = "[q]Canalizando una falla Crepuscular, lo que inflige daño a los enemigos en otros reinos."},
[93059] = {name = "Escudo de tormenta", description = nil},
[93091] = {name = "Rezo de sanación", description = "[q]Convoca la magia Sagrada para sanar a los miembros del grupo cercanos."},
[93094] = {name = "Renovar", description = "[q]Sana a un aliado cada 3 s durante 15 sec."},
[93245] = {name = "Poder de Fuego", description = "[q]La fuerza del elemental de Fuego mengua al reducirse su salud."},
[93276] = {name = "Caparazón de Fuego", description = "[q]¡Inmune al daño!"},
[93277] = {name = "Vendaval desgarrador", description = "[q]Rodea a un enemigo con vientos desgarradores que infligen 6000 p. de daño cada 2 s durante 30 sec. Las aplicaciones extra intensificarán el efecto, pero el poder de la tierra puede contrarrestar los vientos."},
[93278] = {name = "Choque de relámpagos", description = "[q]Inflige 28350 to 31650 p. de daño de Naturaleza."},
[93301] = {name = "Poder de las Sombras", description = "[q]La fuerza del señor de las Sombras mengua al reducirse su salud."},
[93306] = {name = "Vaporizar", description = "[q]Inflige 37000 to 43000 p. de daño de Naturaleza a un enemigo, lo que provoca que su armadura pierda corporalidad y se vuelva incapaz de proteger a su portador durante 10 sec."},
[93311] = {name = "Caparazón de las Sombras", description = "[q]¡Inmune al daño!"},
[93325] = {name = "Ola de choque", description = "[q]Envía una ola de fuerza frente al taumaturgo que inflige 58500 to 71500 p. de daño y aturde a todos los objetivos enemigos en un radio de 15 m en un cono frontal durante 3 sec."},
[93327] = {name = "Sepultar", description = "[q]Machacando a un objetivo con un abrazo terráneo, lo que inflige 6000 p. de daño cada 2 s durante 30 sec. Las aplicaciones extra intensificarán el dolor, pero la exposición a un viento intenso puede contrarrestar el efecto."},
[93331] = {name = "Remolino de Escarcha", description = "[q]Se pone a dar vueltas, lo que libera descargas de hielo sobre los objetivos enemigos, lo que inflige 11875 to 13125 p. de Escarcha y ralentiza el movimiento un 30%."},
[93335] = {name = "Embozo helado", description = "[q]Aire muy frío se arremolina alrededor del taumaturgo, lo que reduce todo el daño reducido un 50% y evita que se interrumpa el hechizo. Absorbe calor de los alrededores, pero una llama intensa podría deshacer el embozo."},
[93336] = {name = "Resguardo de combustión", description = "[q]Aire muy caliente se arremolina alrededor del taumaturgo, lo que inflige daño a todos los atacantes. Se requiere algún tipo de protección contra la llama intensa para atacar con seguridad a través de la barrera."},
[93362] = {name = "Fogonazo", description = "[q]Invoca una columna de llamas que quema a todos los enemigos del área seleccionada e inflige daño extra cada 5 s durante 8 sec."},
[93373] = {name = "Tormenta de hojas", description = "[q]Gira las hojas con rapidez infligiendo un 70% de daño con arma a los enemigos que se encuentran frente al atacante cada 0.5 s durante 4 sec."},
[93377] = {name = "Ruptura Crepuscular", description = "[q]Invoca erupciones Crepusculares para que dañen a los enemigos cercanos que se encuentran frente al taumaturgo."},
[93381] = {name = "Tacto frío", description = "[q]Enfría a los atacantes."},
[93423] = {name = "Asfixiar", description = "[q]El Barón Ashbury comienza a sofocar a todos los enemigos cercanos con las Sombras, lo que inflige daño de las Sombras cada 1 s."},
[93433] = {name = "Ataque de enterramiento", description = "[q]El gusano se entierra en el suelo, lo que sacude el terreno de encima e inflige [( 206 + ((Ranged attack power * 0.40) * 0.288)) * 8] p. de daño de Naturaleza durante 8 sec."},
[93435] = {name = "Rugido de valor", description = "[q]La bestia emite un rugido de valor, lo que aumenta 549 p. la fuerza y la agilidad de todos los miembros del grupo o banda en un radio de 100 m. Dura 1 min."},
[93449] = {name = "Bomba", description = "[q]Bombardea a todos los enemigos de un área seleccionada, inflige daño de Fuego."},
[93456] = {name = "Bomba de humo", description = "[q]Lanza una bomba de humo al suelo, lo que inflige daño y reduce la probabilidad de golpe de los jugadores que permanecen en ella."},
[93468] = {name = "Ejecución pospuesta", description = "[q]¡El Barón Ashbury siente pena por ti, pero solo para poder seguir infligiéndote daño! Sana a todos los enemigos y aliados cercanos un 0% de salud."},
[93473] = {name = "Tormenta de arena", description = "[q]¡Un remolino de arena te rodea!"},
[93479] = {name = "Impacto de ametralladora", description = "[q]Lanza un gran asalto contra una unidad enemiga."},
[93490] = {name = "Algas", description = "[q]Crea un charco de algas en la ubicación objetivo que inflige daño de Naturaleza y ralentiza a los enemigos."},
[93492] = {name = "Embestir", description = "[q]Embiste al enemigo, lo que inflige daño físico y derriba a los enemigos en un cono frente al taumaturgo."},
[93494] = {name = "Espabilar", description = "[q]La cola poderosa de Mobus inflige daño físico y derriba a los enemigos que tiene detrás."},
[93497] = {name = "Carga de burbujas", description = "[q]Carga contra el objetivo en una lluvia de burbujas, lo que inflige daño de Escarcha."},
[93500] = {name = "Haz de burbujas", description = "[q]Lanza burbujas en un cono frente al taumaturgo, lo que inflige daño de Escarcha."},
